insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (38,5,NULL,'Quadrilaterals ','quadrilaterals-and-polygons','https://cdn.kastatic.org/googleusercontent/sIlcOfIBTQSUUWqw7nMj8NhFRlShUvsmhuBmy01Ic1DC8QsLf9JeTVX5mF4dT3OlNxgOlu_z-FnXjqnzLmCWNyk=s128-c','Quadrilaterals only have one side more than triangles, but this opens up an entire new world with a huge variety of quadrilateral types. Learn about it here.','/math/geometry-home/quadrilaterals-and-polygons','2016-09-12 22:06:23','2016-09-12 22:06:23',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (39,5,NULL,'Coordinate plane','geometry-coordinate-plane','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','We use coordinates to describe where something is. In geometry, coordinates say where points are on a grid we call the \"coordinate plane\".','/math/geometry-home/geometry-coordinate-plane','2016-09-12 22:06:23','2016-09-12 22:06:23',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (40,5,NULL,'Area and perimeter','geometry-area-perimeter','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Area and perimeter help us measure the size of 2D shapes. We’ll start with the area and perimeter of rectangles. From there, we’ll tackle trickier shapes, such as triangles and circles.','/math/geometry-home/geometry-area-perimeter','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (41,5,NULL,'Volume and surface area','geometry-volume-surface-area','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Volume and surface area help us measure the size of 3D objects. We’ll start with the volume and surface area of rectangular prisms. From there, we’ll tackle trickier objects, such as cones and spheres.','/math/geometry-home/geometry-volume-surface-area','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (42,5,NULL,'Pythagorean theorem','geometry-pythagorean-theorem','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','The Pythagorean theorem describes a special relationship between the sides of a right triangle. Even the ancients knew of this relationship. In this topic, we’ll figure out how to use the Pythagorean theorem and prove why it works.','/math/geometry-home/geometry-pythagorean-theorem','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (43,5,NULL,'Transformations','transformations','https://cdn.kastatic.org/googleusercontent/GZjzG43hvCOjcFNcojqOLifj5s9_07ifxbfMRe_9psK7duXYaFeBxeLKFi4Q6RXiATMyTWlc6rqKXSaQihawfe-T=s128-c','In this topic you will learn about the most useful math concept for creating video game graphics: geometric transformations, specifically translations, rotations, reflections, and dilations.\n\nYou will learn how to perform the transformations, and how to map one figure into another using these transformations.','/math/geometry-home/transformations','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (44,5,NULL,'Congruence','congruence','https://cdn.kastatic.org/googleusercontent/NyEsvwms0FQtt2L0XrrvSl8Qnxfjh4F1laTiC5Wv8kyLYtQ3eCqbw2GXCoIV0NPbXsatZec-5zZzFAji3b8k0-wB=s128-c','Learn what it means for two figures to be congruent, and how to determine whether two figures are congruent or not. Use this immensely important concept to prove various geometric theorems about triangles and parallelograms.','/math/geometry-home/congruence','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (45,5,NULL,'Similarity','similarity','https://cdn.kastatic.org/googleusercontent/I1Gxl8gue2nG4SM1UEEsW9sIw2aHawhowrbRe6Zijlr2YHUpB9-frQqNYZYHXFNXGwQkfpmVm0rSjc852EJno78b=s128-c','Learn what it means for two figures to be similar, and how to determine whether two figures are similar or not. Use this concept to prove geometric theorems and solve some problems with polygons.','/math/geometry-home/similarity','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (46,5,NULL,'Trigonometry','right-triangles-topic','https://cdn.kastatic.org/googleusercontent/5DxofW1hgz-YnlHJfnx_wSOqcNDy836pUwCOrKSz4TYfHB-ttOjokMCZUkQpWRzOhox1jwOlkaFs3OoyiDdW5xs=s128-c','Triangles are not always right (although they are never wrong), but when they are it opens up an exciting world of possibilities.  Not only are right triangles cool in their own right (pun intended), they are the basis of very important ideas in analytic geometry (the distance between two points in space) and trigonometry.','/math/geometry-home/right-triangles-topic','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (47,5,NULL,'Circles','cc-geometry-circles','https://cdn.kastatic.org/googleusercontent/b22jlkYfbbKXoY1RgqxPCg4Wht2ZD-CYBDJ3I3sf1LaHwHp56F1RwhuC0oGcYCZxo9LWNlMTF_VnF33_6hqiuwF_=s128-c','Explore, prove, and apply important properties of circles that have to do with things like arc length, radians, inscribed angles, and tangents.','/math/geometry-home/cc-geometry-circles','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (48,5,NULL,'Analytic geometry','analytic-geometry-topic','https://cdn.kastatic.org/googleusercontent/icGI9QVLlkE1xu1WH9B7qLZPbIUPhH59XKHPkFA8c1tqnH8SE9DPzKvxKGJOxzvcHA2NwxcyxOAQPX9gUWaIzbhL=s128-c','In analytic geometry, also known as coordinate geometry, we think about geometric objects on the coordinate plane. For example, we can see that opposite sides of a parallelogram are parallel by by writing a linear equation for each side and seeing that the slopes are the same.','/math/geometry-home/analytic-geometry-topic','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (49,5,NULL,'Geometric constructions','geometric-constructions','https://cdn.kastatic.org/googleusercontent/fn0k3bOyKHWNOFpTfBy4ZEgANlSdnBsPqqMS6Lvk7oaGs6pnpylLZLoF2oDfJV0PVBantKSRntKAW742CLAObG37Tg=s128-c','We now have fancy computers to help us perfectly draw things, but have you ever wondered how people drew perfect circles or angle bisectors or perpendicular bisectors back in the day.  Well this tutorial will have you doing just as your grandparents did (actually, a little different since you\'ll still be using a computer to draw circles and lines with a virtual compass and straightedge).','/math/geometry-home/geometric-constructions','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (50,5,NULL,'Miscellaneous','geometry-miscellaneous','https://cdn.kastatic.org/googleusercontent/jemcrK3I9b7lWcJLDVzG8yDorNcVniFlzVVeZ_F3cJ-mDDD66OUJC06mOK0cgiv4aOF89OYSx9XiTyR5aKKZ7vblhg=s128-c','Sal works through problems from the released questions from the California Standards Test for Geometry.  ','/math/geometry-home/geometry-miscellaneous','2016-09-12 22:06:24','2016-09-12 22:06:24',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (51,6,NULL,'Trigonometry with right triangles','trigonometry-right-triangles','https://cdn.kastatic.org/googleusercontent/6-bxPMSoPHXfCyzpKSIsjWFkDvJF6qRFgFRX0Y2gmILODjTD6820tAmFTUPWB8Dr-1DSeQMqBLNrYY6w0BaTvOXvEQ=s128-c','Learn the basics of trigonometry: What are sine, cosine, and tangent? How can we use them to solve for unknown sides and angles in right triangles?','/math/trigonometry/trigonometry-right-triangles','2016-09-12 22:06:30','2016-09-12 22:06:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (52,6,NULL,'Trigonometry with general triangles','trig-with-general-triangles','https://cdn.kastatic.org/googleusercontent/6-bxPMSoPHXfCyzpKSIsjWFkDvJF6qRFgFRX0Y2gmILODjTD6820tAmFTUPWB8Dr-1DSeQMqBLNrYY6w0BaTvOXvEQ=s128-c','Learn how to use trigonometry in order to find missing sides and angles in any triangle.','/math/trigonometry/trig-with-general-triangles','2016-09-12 22:06:30','2016-09-12 22:06:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (53,6,NULL,'The unit circle definition of sine, cosine, and tangent','unit-circle-trig-func','https://cdn.kastatic.org/googleusercontent/T_F3ufhhpgCdZeQvIlZjptgNWFpkEDWzClRuW38bK5JnpTOCTg-_tMwomsd1gbeHMoVPnJOWfpkPVJ2jCsfTr4VxEw=s128-c','Learn how the trigonometric ratios are extended to all real numbers using algebra. Start solving simple problems that involve this new definition of the trigonometric functions.','/math/trigonometry/unit-circle-trig-func','2016-09-12 22:06:30','2016-09-12 22:06:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (54,6,NULL,'Graphs of trigonometric functions','trig-function-graphs','https://cdn.kastatic.org/googleusercontent/FH3hTAStz8a36lY6SZtpbeZuVQ16pstn-dPfbP0u-pCEFo08PZRw0sZdEuN8Pb-I5rBjdCLyRyVrCYMpeYhK-oLj=s128-c','Learn how to graph trigonometric functions and how to interpret those graphs. Learn how to construct trigonometric functions from their graphs or other features.','/math/trigonometry/trig-function-graphs','2016-09-12 22:06:30','2016-09-12 22:06:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (55,6,NULL,'Trigonometric equations and identities','trig-equations-and-identities','https://cdn.kastatic.org/googleusercontent/6-bxPMSoPHXfCyzpKSIsjWFkDvJF6qRFgFRX0Y2gmILODjTD6820tAmFTUPWB8Dr-1DSeQMqBLNrYY6w0BaTvOXvEQ=s128-c','Learn how to solve trigonometric equations and how to use trigonometric identities to solve various problems.','/math/trigonometry/trig-equations-and-identities','2016-09-12 22:06:30','2016-09-12 22:06:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (56,7,NULL,'Displaying and describing data','displaying-describing-data','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','One of the first things we do in statistics is try to understand sets of data. This involves plotting the data in different ways and summarizing what we see with measures of center (like mean and median) and measures of spread (like range and standard deviation). This topic focuses on concepts that are often referred to as \"descriptive statistics\".','/math/statistics-probability/displaying-describing-data','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (57,7,NULL,'Modeling distributions of data','modeling-distributions-of-data','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','The normal distribution is the most commonly used model in all of statistics. Learn how to measure position using z-scores and find what percent of data falls where in a normal distribution.','/math/statistics-probability/modeling-distributions-of-data','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (58,7,NULL,'Describing relationships in quantitative data','describing-relationships-quantitative-data','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','If we want to explore a relationship between two quantitative variables, we make a scatterplot of the data. The fun doesn\'t stop there. We describe pattern, talk about the type of correlation we see, and sometimes fit a line to the data so we can use the pattern to make predictions.','/math/statistics-probability/describing-relationships-quantitative-data','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (59,7,NULL,'Designing studies','designing-studies','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Study design focuses on collecting data properly and making the most valid conclusions we can based on how the data was collected. This topic covers explore samples, surveys, and experiments.','/math/statistics-probability/designing-studies','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (60,7,NULL,'Probability','probability-library','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Probability tells us how often some event will happen after many repeated trials. This topic covers theoretical, experimental, compound probability, permutations, combinations, and more!','/math/statistics-probability/probability-library','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (61,7,NULL,'Random variables','random-variables-stats-library','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Random variables can be any outcomes from some chance process, like how many heads will occur in a series of 20 flips. We calculate probabilities of random variables and calculate expected value for different types of random variables.','/math/statistics-probability/random-variables-stats-library','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (62,7,NULL,'Sampling distributions','sampling-distributions-library','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A sampling distribution shows every possible result a statistic can take in every possible sample from a population and how often each result happens. This topic covers how sample proportions and sample means behave in repeated samples.','/math/statistics-probability/sampling-distributions-library','2016-09-12 22:06:37','2016-09-12 22:06:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (63,7,NULL,'Confidence intervals (one sample)','confidence-intervals-one-sample','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Confidence intervals give us a range of plausible values for some unknown value based on results from a sample. This topic covers confidence intervals for means and proportions.','/math/statistics-probability/confidence-intervals-one-sample','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (64,7,NULL,'Significance tests (one sample)','significance-tests-one-sample','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Significance tests give us a formal process for using sample data to evaluate the likelihood of some claim about a population value. We calculate p-values to see how likely a sample result is to occur by random chance, and we use p-values to make conclusions about hypotheses.','/math/statistics-probability/significance-tests-one-sample','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (65,7,NULL,'Significance tests and confidence intervals (two samples)','significance-tests-confidence-intervals-two-samples','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Learn how to apply what you know about confidence intervals and significance tests to situations that involve comparing two samples to see if there is a significant difference between the two populations.','/math/statistics-probability/significance-tests-confidence-intervals-two-samples','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (66,7,NULL,'Inference for categorical data (chi-square tests)','inference-categorical-data-chi-square-tests','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Chi-square tests are a family of significance tests that give us ways to test hypotheses about distributions of categorical data. This topic covers goodness-of-fit tests to see if sample data fits a hypothesized distribution, and tests for independence between two categorical variables.','/math/statistics-probability/inference-categorical-data-chi-square-tests','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (67,7,NULL,'Advanced regression (inference and transforming)','advanced-regression-inference-transforming','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Advanced regression will introduce you to regression methods when data has  a nonlinear pattern.','/math/statistics-probability/advanced-regression-inference-transforming','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (68,7,NULL,'Analysis of variance (ANOVA)','analysis-of-variance-anova-library','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Analysis of variance, also called ANOVA, is a collection of methods for comparing multiple means across different groups.','/math/statistics-probability/analysis-of-variance-anova-library','2016-09-12 22:06:38','2016-09-12 22:06:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (69,8,NULL,'Limits and continuity','limits-and-continuity-calc','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Reach infinity within a few seconds! Limits are the most fundamental ingredient of calculus. Learn how they are defined, how they are found (even under extreme conditions!), and how they relate to continuous functions.','/math/calculus-home/limits-and-continuity-calc','2016-09-12 22:06:47','2016-09-12 22:06:47',1);
