insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (103,14,NULL,'Chapter 2: Whole numbers','whole-numbers','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Natural numbers are the set of numbers ranging from 1 to infinity. If we add 0 to this set we get a new set of numbers called \'Whole numbers\'.','/math/in-sixth-grade-math/whole-numbers','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (104,14,NULL,'Chapter 3: Playing with numbers','playing-numbers','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','In this chapter we will learn about factors and multiples and about the divisibility rules for 2, 3, 4, 5, 6, 8, 9 and 10','/math/in-sixth-grade-math/playing-numbers','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (105,14,NULL,'Chapter 4: Basic geometrical ideas','basic-geometrical-ideas','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Geometry comes from the greek words for \"earth\" and \"measurement.\" It is the study of shapes and sizes. In this chapter, we will develop a strong foundation for geometry.','/math/in-sixth-grade-math/basic-geometrical-ideas','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (106,14,NULL,'Chapter 5: Understanding elementary shapes','understanding-elementary-shapes','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Corners, edges, planes, open curves and closed curves are different shapes that we see around us. Understanding these shapes can help us solve a wise variety of real world problems.','/math/in-sixth-grade-math/understanding-elementary-shapes','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (107,14,NULL,'Chapter 6: Integers','integers-india','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','The set of numbers from 1 to infinity is called Natural numbers. When we add 0 to this, we get whole numbers. A set of 0, natural numbers and their opposites, make up \'integers\'.','/math/in-sixth-grade-math/integers-india','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (108,14,NULL,'Chapter 7: Fractions','fractions-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A fraction is a number representing part of a whole. We use fractions in everyday life, from dividing a pie to even checking the time. 3/4, 56/3, 25/4 etc. are all fractions.','/math/in-sixth-grade-math/fractions-1','2016-09-12 22:07:20','2016-09-12 22:07:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (109,14,NULL,'Chapter 8: Decimals','decimals-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A dot represents a decimal point. In real life problems like measuring of length, weight, or time, the values are not exact whole numbers but rather portions of whole number. In situations where we need to be exact, we use decimal numbers.','/math/in-sixth-grade-math/decimals-1','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (110,14,NULL,'Chapter 9: Data handling','data-handling','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Data is a collection of numbers gathered to represent some information.','/math/in-sixth-grade-math/data-handling','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (111,14,NULL,'Chapter 10: Mensuration','mensuration','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Mensuration is the branch of mathematics which deals with the study of Geometric shapes, their area, volume and related concepts.','/math/in-sixth-grade-math/mensuration','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (112,14,NULL,'Chapter 11: Algebra','algebra-in','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Algebra is the language through which we describe patterns in mathematics. It involves usage of letters with numbers to depict problems.','/math/in-sixth-grade-math/algebra-in','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (113,14,NULL,'Chapter 12: Ratio and proportion','ratio-and-proportion','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','In certain situations, comparison by division makes better sense than comparison by taking the difference. The comparison by division is called the ratio of the two numbers.','/math/in-sixth-grade-math/ratio-and-proportion','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (114,14,NULL,'Chapter 13: Symmetry','symmetry-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Symmetry is when one shape becomes exactly like another if you flip, slide or turn it.','/math/in-sixth-grade-math/symmetry-1','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (115,14,NULL,'Chapter 14: Practical Geometry','practical-geometry','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Geometry is to draw various shapes with tools like rulers, compasses, dividers, set squares and protractors. It is the study of shapes.','/math/in-sixth-grade-math/practical-geometry','2016-09-12 22:07:21','2016-09-12 22:07:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (116,15,NULL,'Chapter 1: Integers','india-integers','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Integers are a collection of whole numbers (0,1,2,3..) and their opposites (-1,-2,-3,-4..).','/math/in-seventh-grade-math/india-integers','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (117,15,NULL,'Chapter 2: Fractions and decimals','fractions-decimals','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Fractions tell us how many parts of the whole we have. Decimals on the other hand help us with accurate measurement of length, weight etc., because in the real world, these quantities aren\'t exact whole numbers but in fact parts of a large number.','/math/in-seventh-grade-math/fractions-decimals','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (118,15,NULL,'Chapter 3: Data handling','seventh-data-handling','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Data handling helps us in collection, recording and presentation of data -- organizing data makes it easy to understand and interpret.','/math/in-seventh-grade-math/seventh-data-handling','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (119,15,NULL,'Chapter 4: Simple equations','simple-equations','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','An equation is a statement that helps us understand the equality between two or more variables, and constants. A variable can take various values. Its value is not fixed. We use letters x, y, l, m, ... etc. to denote variables. On the other hand, a constant has a fixed value. x+3=7 is an equation where x is a variable and 3 and 7 are constants.','/math/in-seventh-grade-math/simple-equations','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (120,15,NULL,'Chapter 5: Lines and angles','lines-angles','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A \'line\' has no end points, a \'line segment\' has two end points, and an \'angle\' is formed when lines or line segments meet. This is where we dive into the world of geometry!','/math/in-seventh-grade-math/lines-angles','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (121,15,NULL,'Chapter 6: The triangle and its properties','triangle-pror','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A triangle is a simple closed curve made of three line segments. It has three vertices, three sides and three angles.','/math/in-seventh-grade-math/triangle-pror','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (122,15,NULL,'Chapter 7: Congruence of triangles','congruence-triangles','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','In this chapter, we will learn what congruence is and learn different criteria for proving the congruence of two triangles','/math/in-seventh-grade-math/congruence-triangles','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (123,15,NULL,'Chapter 8: Comparing quantities','comparing-quantities','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','We compare quantities to arrive at conclusions and take decisions all the time. In such situations, to have any meaningful result, the units of the quantities being compared must be equal!','/math/in-seventh-grade-math/comparing-quantities','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (124,15,NULL,'Chapter 9: Rational numbers','rational-numbers','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A number which can be expressed in the form p/q where p and q are integers and q is not equal to zero is called a Rational Number.','/math/in-seventh-grade-math/rational-numbers','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (125,15,NULL,'Chapter 10: Practical geometry','seventh-practical-geometry','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','In this chapter we will get a better hold of simple shapes like circles, triangles, and parallelograms and also learn how to construct these shapes of given dimensions.','/math/in-seventh-grade-math/seventh-practical-geometry','2016-09-12 22:07:28','2016-09-12 22:07:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (126,15,NULL,'Chapter 11: Perimeter and area','seventh-perimeter-area','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','The Perimeter of a shape is the distance around a closed figure while its area is the part of plane or region occupied by the closed figure.','/math/in-seventh-grade-math/seventh-perimeter-area','2016-09-12 22:07:29','2016-09-12 22:07:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (127,15,NULL,'Chapter 12: Algebraic expressions','algebraic-expressions','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','An algebraic expression is an expression built up from constants, variables, and the arithmetic operations of addition, subtraction, multiplication, and division.','/math/in-seventh-grade-math/algebraic-expressions','2016-09-12 22:07:29','2016-09-12 22:07:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (128,15,NULL,'Chapter 13: Exponents and powers','exponents-powers','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Very large numbers, for example 1685742300000 are difficult to read, understand and compare. To make these numbers easier to read, understand and compare, we use exponents.','/math/in-seventh-grade-math/exponents-powers','2016-09-12 22:07:29','2016-09-12 22:07:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (129,15,NULL,'Chapter 14: Symmetry','in-s','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Symmetry is an important geometrical concept, commonly exhibited in nature and is used almost in every field of activity. Artists, professionals, designers of clothing or jewelry, car manufacturers, architects and many others make use of the idea of symmetry. The beehives, the flowers, the tree-leaves, religious symbols, rugs, and handkerchiefs — everywhere you find symmetrical designs.','/math/in-seventh-grade-math/in-s','2016-09-12 22:07:29','2016-09-12 22:07:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (130,15,NULL,'Chapter 15: Visualizing solid shapes','visualizing-solid-shapes','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Can you see that, the two dimensional figures can be identified as the faces of the three dimensional shapes? For example a cylinder has two faces which are circles, and a pyramid, shaped like this has triangles as its faces.We will now try to see how some of these 3-D shapes can be visualised on a 2D- surface, that is, on paper.','/math/in-seventh-grade-math/visualizing-solid-shapes','2016-09-12 22:07:29','2016-09-12 22:07:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (131,16,NULL,'Chapter 1: Rational numbers','rational-numbers-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Rational numbers can be expressed in the form p/q where p and q are integers and q is not equal to zero. In this chapter we will look at some properties of rational numbers.','/math/in-eighth-grade-math/rational-numbers-1','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (132,16,NULL,'Chapter 2: Linear equations in one variable','linear-equations-one-variable','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Equations having variables with the highest power as \'1\' are called linear equations, for example 2x+7=3. Here the power of x is 1. In this chapter we will introduce the concept of equations with variables on both sides, and we will look into some applications for linear equations.','/math/in-eighth-grade-math/linear-equations-one-variable','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (133,16,NULL,'Chapter 3: Understanding quadrilaterals','understanding-quadrilaterals-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A quadrilateral is a four-sided polygon with four angles. There are many kinds of quadrilaterals. The five most common types are the parallelogram, the rectangle, the square, the trapezoid, and the rhombus. In this chapter we will learn about polygons, different types of quadrilaterals, and the special case of parallelograms!','/math/in-eighth-grade-math/understanding-quadrilaterals-1','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (134,16,NULL,'Chapter 5: Data handling','data-handling-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','In this chapter we will learn about interpreting data in interesting ways. We will learn how to read and depict data on bars, graphs and charts, and we will also introduce the topic of probability. Probability is used in our daily life, so we will look into it in detail by practicing with many questions.','/math/in-eighth-grade-math/data-handling-1','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (135,16,NULL,'Chapter 6: Squares and square roots','squares-square-roots','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','A square of an integer is the product of some integer with itself. Similarly, the square root of a number is an integer which when multiplied with itself give this number. In this topic we will increase our understanding of squares, square roots, and will will learn about ways of finding them.','/math/in-eighth-grade-math/squares-square-roots','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (136,16,NULL,'Chapter 7: Cubes and cubes roots','cubes-cube-roots','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','We get the cube of a number by finding the result of its multiplication with itself three times. Similarly, we can find the original number from this result by finding the cube root. In this topic we will learn more about cubes and cube roots.','/math/in-eighth-grade-math/cubes-cube-roots','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (137,16,NULL,'Chapter 9: Algebraic expressions and identities','algebraic-expressions-identities','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','An algebraic expression is an expression built up from integer constants, variables, and the algebraic operations. In this chapter we will learn about expressions, factors, coefficients, monomials, binomials, and polynomials.','/math/in-eighth-grade-math/algebraic-expressions-identities','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (138,16,NULL,'Chapter 11: Mensuration','eighth-mensuration','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','The part of geometry concerned with ascertaining lengths, areas, and volumes, is called Mensuration. In this chapter we will add to what we have already studied in previous classes by learning about new shapes like Cube, Cuboid, Cylinder and their volumes.','/math/in-eighth-grade-math/eighth-mensuration','2016-09-12 22:07:35','2016-09-12 22:07:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (139,16,NULL,'Chapter 12: Exponents and powers','exponents-powers-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Exponents are shorthand for repeated multiplication of the same thing by itself, and it\'s power refers to the number of times it is multiplied. In this chapter we will learn about the basics of exponents and introduce the concept of powers with negative exponents.','/math/in-eighth-grade-math/exponents-powers-1','2016-09-12 22:07:36','2016-09-12 22:07:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (140,16,NULL,'Chapter 13: Direct and inverse proportions','direct-inverse-proportions','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Two values are said to be in direct proportion when increase in one results in an increase in the other. Similarly, they are said to be in indirect proportion when the increase in one results in a decrease in the other. I this chapter we will learn more about these properties.','/math/in-eighth-grade-math/direct-inverse-proportions','2016-09-12 22:07:36','2016-09-12 22:07:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (141,16,NULL,'Chapter 14: Factorization','factorization','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Factorization is the decomposition of an object (for example, a number, a polynomial) into a product of other objects, or factors, which when multiplied together give the original. In this chapter we will learn about methods of factorization.','/math/in-eighth-grade-math/factorization','2016-09-12 22:07:36','2016-09-12 22:07:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (142,16,NULL,'Chapter 15: Introduction to graphs','intro-graphs','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','Graphs are a graphical depiction of data, or relation between two or more variables. In this chapter we will learn about linear graphs, coordinates, and some applications. We will see that data becomes very easy to understand and interpret when depicted through graphs!','/math/in-eighth-grade-math/intro-graphs','2016-09-12 22:07:36','2016-09-12 22:07:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (143,16,NULL,'Chapter 16: Playing with numbers','playing-numbers-1','https://cdn.kastatic.org/googleusercontent/S_aapiq_gTnwOtXs_UH23tfv4UAO5svqpiJECDZ9mlN1dg1JfCBBgLBqRb3qQKrwNep1T5kyZyXGq6nM7rQGtAymrw=s128-c','You have studied various types of numbers such as natural numbers, whole numbers, integers and rational numbers.You have also studied a number of interesting properties about them. In grade 6, we explored finding factors and multiples and the relationships among them. In this chapter, we will explore numbers in more detail. These ideas help in justifying tests of divisibility.','/math/in-eighth-grade-math/playing-numbers-1','2016-09-12 22:07:36','2016-09-12 22:07:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (144,18,NULL,'One-dimensional motion','one-dimensional-motion','https://cdn.kastatic.org/googleusercontent/Owbjkcm4N9ulCeq0727nHDjZIwOuUQeVKxLUvwE5m6CwcUK457bipm41h9AovOvrK1IeywSyG76_lFhlpKKZpV8W=s128-c','In this tutorial we begin to explore ideas of velocity and acceleration.  We do exciting things like throw things off cliffs (far safer on paper than in real life) and see how high a ball will fly in the air.','/science/physics/one-dimensional-motion','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (145,18,NULL,'Two-dimensional motion','two-dimensional-motion','https://cdn.kastatic.org/googleusercontent/eL-iG3U8ADj1ju8vhqBZK5B-X2lVoqqelg6fEb81DLfsiWtnohYP2qlLnGm26zMhZYVgufK7aViwmhdOfJlcBqw=s128-c','You understand velocity and acceleration well in one-dimension.  Now we can explore scenarios that are even more fun.  With a little bit of trigonometry (you might want to review your basic trig, especially what sin and cos are), we can think about whether a baseball can clear the \"green monster\" at Fenway Park.','/science/physics/two-dimensional-motion','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (146,18,NULL,'Forces and Newton\'s laws of motion','forces-newtons-laws','https://cdn.kastatic.org/googleusercontent/lRfU-8rzz3IbZxoSWr-yh1F4-lJxgpSgf2bqAayUH344IRcbU9oUwGImtMIL2vmtAZo2f-ieAt-T5tmxZWbSjQwQ=s128-c','This is the meat of much of classical physics.  We think about what a force is and how Newton changed the world\'s (and possibly your) view of how reality works.','/science/physics/forces-newtons-laws','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (147,18,NULL,'Centripetal force and gravitation','centripetal-force-and-gravitation','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Learn about centripetal acceleration and centripetal force. Also, learn about the universal law of gravitation and gravitational orbits.','/science/physics/centripetal-force-and-gravitation','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (148,18,NULL,'Work and energy','work-and-energy','https://cdn.kastatic.org/googleusercontent/otscWyMpj__lS4CnyBgyt5pP6Hqtc_iMiBJUXG3Iz64miCq2i9z_A0J7guhFJyXzQHAdFx4BcIEETdwdojESzhjO=s128-c','\"Energy\" is a word that\'s used a lot. Here, you\'ll learn about how it\'s one of the most useful concepts in physics. Along the way, we\'ll talk about work, kinetic energy, potential energy, conservation of energy, and mechanical advantage.','/science/physics/work-and-energy','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (149,18,NULL,'Impacts and linear momentum','linear-momentum','https://cdn.kastatic.org/googleusercontent/L1kHzD46_z-u63Av8IlKUKnQUDcYBMiGeJIPguInZiNes5grp_6IevjRev_ddL_QpjZah5s8MtQZAQ8AYE8Vit7V=s128-c','Momentum ties velocity and mass into one quantity. It might not be obvious why this is useful, but momentum has this cool property where the total amount of it never changes. This is called the conservation of momentum, and we can use it to analyze collisions and other interactions. Bam!','/science/physics/linear-momentum','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (150,18,NULL,'Torque and angular momentum','torque-angular-momentum','https://cdn.kastatic.org/googleusercontent/yoPfZTwB1vMHZvwG6jJ8lBAVp-YujHs5joYD-5mYA_EUOcNj_m2R3ODndqVO586xSF8GjBGXy3r55uxGFqPzlMvD=s128-c','Everything you\'ve learned about motion, forces, energy, and momentum can be reused to analyze rotating objects. There are some differences, though. Here, you\'ll learn about rotational motion, moments, torque, and angular momentum.','/science/physics/torque-angular-momentum','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (151,18,NULL,'Oscillations and mechanical waves','mechanical-waves-and-sound','https://cdn.kastatic.org/googleusercontent/aX8kGXoISMzKjFPQq-Ek7cLPKNzY6bLs6pRgk2EEqyb8sI8ZN_749Y2s8TScOz_Q0s6WFZHGOFqrtNSCbiq4EHXH=s128-c','Waves are responsible for basically every form of communication we use. Whether you\'re talking out loud, texting on your phone, or waving to someone in a crowd there\'s going to be a wave transmitting information. Learn about the basics of waves in this topic, then learn more about light waves in the topics below.','/science/physics/mechanical-waves-and-sound','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (152,18,NULL,'Fluids','fluids','https://cdn.kastatic.org/googleusercontent/E5IK6npqPL50SF26XvMT1Ks4Kw3pRrQuje9bxAVMANiA8vowZBY4IJCLK0mLviF4BnX2EC8RhYoagVGMz3T14HJv=s128-c','Atmospheric pressure is like an invisible friend who is always squeezing you with a big hug. Learn more about pressure, buoyant force, and flowing fluid so you can appreciate the sometimes invisible, but crucial, effect they have on us and the world around us.','/science/physics/fluids','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (153,18,NULL,'Thermodynamics','thermodynamics','https://cdn.kastatic.org/googleusercontent/1alh0VXihYZTpsdXBtThLZQah-UEAKt0eDcwh24O1cbDovJyhoSbxDugfqy2NNjFUJOmFHpVtfRTxtgG-hIilfcXfg=s128-c','Heat can be useful, but it can also be annoying. Understanding heat and the flow of heat allows us to build heat sinks that prevent our computers from overheating, build better engines, and prevent freeway overpasses from cracking.','/science/physics/thermodynamics','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (154,18,NULL,'Electric charge, field, and potential','electric-charge-electric-force-and-voltage','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Electric forces hold together the atoms and molecules in your eyes which allow you to read this sentence. Take a moment and learn about the force that holds our bodies together.','/science/physics/electric-charge-electric-force-and-voltage','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (155,18,NULL,'Circuits','circuits-topic','https://cdn.kastatic.org/googleusercontent/MEl9lWskJ3CIy77kDn6NndxZRUxNF3m8aanmfCfHu-hmqdqS5UmJbHw5PthAkMDFwhtNCXBmqnSCc-y45537wzYfzg=s128-c','Circuits make computers, digital cameras, and video games possible. Circuits are driving an unprecedented rate of change in how we live. In this topic you\'ll learn about the physics behind the electronic devices we use.','/science/physics/circuits-topic','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (156,18,NULL,'Magnetic forces, magnetic fields, and Faraday\'s law','magnetic-forces-and-magnetic-fields','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Magnetic fields are extremely useful. The magnetic field of the Earth shields us from harmful radiation from the Sun, magnetic fields allow us to diagnose medical problems using an MRI, and magnetic fields are a key component in generating electrical power in most power plants. In this topic you\'ll learn about the forces, fields, and laws that makes these and so many other applications possible. ','/science/physics/magnetic-forces-and-magnetic-fields','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (157,18,NULL,'Electromagnetic waves and interference','light-waves','https://cdn.kastatic.org/googleusercontent/X4Btz_73zj2Jsnz7X5JxJIwSQtwxqjq0JqRlmqQcQUMhq5doo8KuXrThHMKMbsS2LAc4DW5YjHpNF8RqeGpfU5Wa=s128-c','Light can seem mysterious. What is light made out of? What causes color? How do 3D movies work? Learn about some of the mysterious properties of light in these tutorials.','/science/physics/light-waves','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (158,18,NULL,'Geometric optics','geometric-optics','https://cdn.kastatic.org/googleusercontent/h6u4HLTSjoIG3fJiF21_s7wkyTxIG9ZgnmIJhZGfqzy2grH54fo18eHHPH0N0Pj1nULo5Tw6eHRLTHUc7mdeT2s8=s128-c','Light waves can be bent and reflected to form new and sometimes altered images. Understanding how light rays can be manipulated allows us to create better contact lenses, fiber optic cables, and high powered telescopes.','/science/physics/geometric-optics','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (159,18,NULL,'Special relativity ','special-relativity','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Think you know about time and space? Think again. Einstein basically did a pile driver on all our brains when he came up with his theory of special relativity. \n','/science/physics/special-relativity','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (160,18,NULL,'Quantum Physics','quantum-physics','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Do you want to describe the microscopic world of atoms and nuclei? Well, classical physics isn\'t going to cut it. You\'ll need quantum physics to explain things like atomic energy levels, photons, and the wavelength of electrons.','/science/physics/quantum-physics','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (161,18,NULL,'Discoveries and projects','discoveries','https://cdn.kastatic.org/googleusercontent/PwojWqLbtfj4yfAMXpNcu5X1cqM-tE85PFPwm6oAtPDS8JC_PZLce6Mf5OgUwAv9VtYu1cfVnEmEXBkj8GyXmJ2lUA=s128-c','Rediscover the most important Scientific observations in a historical progression','/science/physics/discoveries','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (162,18,NULL,'Review for AP Physics 1 exam','review-for-ap-physics-1-exam','https://cdn.kastatic.org/googleusercontent/VYPvCqzZBZj21JOZGf-rdT_1nHQ7tDl2DZaMHvE7hmsjpqDbpRiS2JT2bh0K6AT5T96qeyqGsg5GSLM2xMWwHfM0Eg=s128-c','Studying for the AP Physics 1 exam? Watch these materials to help you review.','/science/physics/review-for-ap-physics-1-exam','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (163,18,NULL,'Cosmology and astronomy','cosmology-and-astronomy','https://cdn.kastatic.org/googleusercontent/kl-jwgtjNVk_0wSAf-aoVgKwN6I3emOQ9ZuVkNwSdYuWJAGCxTXKwfH6waYTP-_yoCiIDSdSW5EN6n08cV4YF5hPAg=s128-c','It\'s a great, big universe out there. In this section of Khan Academy, you\'ll learn about the brain-bending vastness of time and space. We go from plate tectonics here on Earth to entire galaxies and beyond. ','/science/physics/cosmology-and-astronomy','2016-09-12 22:07:44','2016-09-12 22:07:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (164,19,NULL,'Atoms, compounds, and ions','atomic-structure-and-properties','https://cdn.kastatic.org/googleusercontent/8onJeFJi24Ns28L3OBm8PaioTnGbY4zhDGJ4l2EzAkGqaTgGpjlj8jHI48nRNELiH_L185FyQFAEs-xeLO5hgY4=s128-c','Chemistry is the study of matter, and all matter is made up of atoms. We will learn about elements, atomic number and mass, isotopes, moles (chemistry moles, not the animal), and compounds. ','/science/chemistry/atomic-structure-and-properties','2016-09-12 22:07:52','2016-09-12 22:07:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (165,19,NULL,'Chemical reactions and stoichiometry','chemical-reactions-stoichiome','https://cdn.kastatic.org/googleusercontent/6UI6k-Z6Ll3olp_QF2kP21MJviUv3pb2ecUpzB_UUEaqlbWsC8RENyfw2DRRAieQTRvgGoOSic15XBAE5Jt9f4he=s128-c','We are now going to delve into the heart of chemistry.  We learn ways of representing molecules and how molecules react.  To do this, we\'ll even think about \"how many\" of a molecule we have using a quantity called a \"mole\".','/science/chemistry/chemical-reactions-stoichiome','2016-09-12 22:07:52','2016-09-12 22:07:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (166,19,NULL,'Electronic structure of atoms','electronic-structure-of-atoms','https://cdn.kastatic.org/googleusercontent/8onJeFJi24Ns28L3OBm8PaioTnGbY4zhDGJ4l2EzAkGqaTgGpjlj8jHI48nRNELiH_L185FyQFAEs-xeLO5hgY4=s128-c','So we know that all matter is made up of atoms, but what is an atom made out of? Chemists describe the structure of atoms using models. This section will cover the Bohr model, photoelectric effect, absorption and emission spectra, quantum numbers, and electron configurations. ','/science/chemistry/electronic-structure-of-atoms','2016-09-12 22:07:52','2016-09-12 22:07:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (167,19,NULL,'Periodic table','periodic-table','https://cdn.kastatic.org/googleusercontent/Ja82Ms1ia7ugOCCL_1WSbQQUHK6XQvz7eG_RWKP8wCA3Vgshbg4_4FYUr7VDDsoWXYT54q5aXEj3tcXl5GnMWVmL=s128-c','A little more than a century ago, the chemist Dmitri Mendeleev published an early form of the periodic table, which organizes the known elements of our world. His method of classifying the elements was so useful that we still use it even today. We will learn to apply this elegant table to an understanding of atomic structure and properties, including periodic table trends. ','/science/chemistry/periodic-table','2016-09-12 22:07:52','2016-09-12 22:07:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (168,19,NULL,'Chemical bonds','chemical-bonds','https://cdn.kastatic.org/googleusercontent/yhmXPsmkh9ZEXVirZG1SFi8b9RXxXROUihWQAuSSPEl2O3owO2KG3Et8YXhzh_Ma8QWuCiNpts0vkHaMsM-96dcm=s128-c','Chemical bonds are the glue that hold molecules together. We will learn about the different kinds of bonds, ways chemists draw bonds and molecules, and how the type of chemical bonding affects the bulk properties of a material. We will cover electronegativity, Lewis dot structures, VSEPR, bond hybridization, and ionic, covalent, and metallic bonds. ','/science/chemistry/chemical-bonds','2016-09-12 22:07:52','2016-09-12 22:07:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (169,19,NULL,'Gases and kinetic molecular theory','gases-and-kinetic-molecular-theory','https://cdn.kastatic.org/googleusercontent/y332LP6jjzsq3wDkLqvcQXK8Zkb__gexiQfW1P97H-zX1D4tKNJNrFjIl_RsT1pilV4domqij52pkY7DatJrKkVZ=s128-c','Properties of gases can be modeled using some relatively simple equations, which we can relate to the behavior of individual gas molecules. We will learn about the ideal gas law, vapor pressure, partial pressure, and the Maxwell Boltzmann distribution. ','/science/chemistry/gases-and-kinetic-molecular-theory','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (170,19,NULL,'States of matter and intermolecular forces','states-of-matter-and-intermolecular-forces','https://cdn.kastatic.org/googleusercontent/vNfz9WkqILsh7VsRrouGvQ3aRs2rWEhxaI_1KLp3ng5RBEcObk01K34vkwS1MNhwNVc0z_YbLQd_EKCTk-Gr_BuJ=s128-c','Have you ever wondered how some insects are able to \"skate\" on the surface of water? We will learn about how intermolecular forces make this possible. We will also cover liquids, solids, mixtures, and phase changes.','/science/chemistry/states-of-matter-and-intermolecular-forces','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (171,19,NULL,'Chemical equilibrium','chemical-equilibrium','https://cdn.kastatic.org/googleusercontent/6UI6k-Z6Ll3olp_QF2kP21MJviUv3pb2ecUpzB_UUEaqlbWsC8RENyfw2DRRAieQTRvgGoOSic15XBAE5Jt9f4he=s128-c','Many chemical reactions are reversible, and the forward and backward reactions can occur at the same time. When the rate of the forward reaction is equal to the rate of the backward reaction, we call that a dynamic equilibrium. We will learn how equilibrium can be described by the equilibrium constant K, and how different factors than can affect the chemical equilibrium. ','/science/chemistry/chemical-equilibrium','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (172,19,NULL,'Acids and bases','acids-and-bases-topic','https://cdn.kastatic.org/googleusercontent/TXubWO_6rTgD_g0-hxp5xHiI5_RCqAVPjqp4V1c2bvxaFv5YcB_s8V6tDBL41Mkk0924LqsOrItHWAb_maZgF2V_=s128-c','In this section we will be talking about the basics of acids and bases and how acid-base chemistry is related to chemical equilibrium. We will cover acid and base definitions, pH, acid-base equilibria, acid-base properties of salts, and the pH of salt solutions. ','/science/chemistry/acids-and-bases-topic','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (173,19,NULL,'Buffers, titrations, and solubility equilibria','acid-base-equilibrium','https://cdn.kastatic.org/googleusercontent/TXubWO_6rTgD_g0-hxp5xHiI5_RCqAVPjqp4V1c2bvxaFv5YcB_s8V6tDBL41Mkk0924LqsOrItHWAb_maZgF2V_=s128-c','We can combine our knowledge of acids and bases, equilibrium, and neutralization reactions to understand buffers and titrations. Solubility equilibria will build on concepts from solubility, precipitation, and equilibrium. ','/science/chemistry/acid-base-equilibrium','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (174,19,NULL,'Thermodynamics','thermodynamics-chemistry','https://cdn.kastatic.org/googleusercontent/1alh0VXihYZTpsdXBtThLZQah-UEAKt0eDcwh24O1cbDovJyhoSbxDugfqy2NNjFUJOmFHpVtfRTxtgG-hIilfcXfg=s128-c','Thermodynamics is the study of heat, \"thermo,\" and work, \"dynamics.\" We will be learning about energy transfer during chemical and physical changes, and how we can predict what kind of changes will occur. Concepts covered in this tutorial include the laws of thermodynamics, internal energy, heat, work, PV diagrams, enthalpy, Hess\'s law, entropy, and Gibbs free energy. ','/science/chemistry/thermodynamics-chemistry','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (175,19,NULL,'Redox reactions and electrochemistry','oxidation-reduction','https://cdn.kastatic.org/googleusercontent/OOpcGSJ1Vjqi3B3N7L1lnnePekbH6MrwT7PCxVtaB5elBHQs9lZJG3uvbI7FbDXZkkmW0r5CoeoFY0o9ZeBYpG9pgw=s128-c','Oxidation and reduction reactions power your phone and make it possible for your body to use the oxygen you inhale. We will learn about oxidation states (numbers), oxidation-reduction (redox) reactions, galvanic/voltaic cells, electrolytic cells, cell potentials, and how electrochemistry is related to thermodynamics and equilibrium. ','/science/chemistry/oxidation-reduction','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (176,19,NULL,'Kinetics','chem-kinetics','https://cdn.kastatic.org/googleusercontent/E89_Sd9MYaWFH49kyKG0mVh55TbyCCKkwlcDaTJwQ2x2MP10or0Rx6KyW9bhdX32IgPuKA2A-odeO77sW0abzlM=s128-c','Chemists are often interested in how fast a reaction will occur, and what we can do to control the rate. The study of reaction rates is called kinetics, and we will learn about average reaction rate, rate laws, the Arrhenius equation, reaction mechanisms, catalysts, and spectrophotometry.','/science/chemistry/chem-kinetics','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (177,19,NULL,'Nuclear chemistry','nuclear-chemistry','https://cdn.kastatic.org/googleusercontent/w3Cd_xVlwj4kQg0cXer87Ln04xMca03Qwi33EJXGAVw4cVEbVHKY7pPoM0f6KaAkmIPre4wWNTK4rPB4_6ACFRQN=s128-c','Ancient alchemists attempted but failed to turn different substances into gold. It turns out that the only way to turn one element into another element is using nuclear chemistry! Nuclear reactions change the composition of an atom\'s nucleus, and this process is useful for many applications. ','/science/chemistry/nuclear-chemistry','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (178,19,NULL,'Meet the chemistry professional','meet-a-chemistry-professional','https://cdn.kastatic.org/googleusercontent/z2l0ThTok0ehMZfVRfbuJ92hezMWCCaU9AXZQsTBV0qX5TrA51tKNK07cdVk_LC_kWF4EznkD9TEt1ZHKkjnOr23=s128-c','What can you do with chemistry skills? A lot of things, it turns out! Learn about the diversity of careers available to people interested in chemistry. ','/science/chemistry/meet-a-chemistry-professional','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (179,19,NULL,'Alkanes, cycloalkanes, and functional groups','bond-line-structures-alkanes-cycloalkanes','https://cdn.kastatic.org/googleusercontent/8CSV3mWD9Ju_jpVUF6hXKkPLtabvzoP0F3uWH08V3gbPBFht9J-woXaOoWhx-uBMdMz2a3BKLpehCtgMGtYP5UH44A=s128-c','The properties of organic molecules depend on the structure, and knowing the names of organic compounds allow us to communicate with other chemists. We\'ll be learning about different aspects of molecular structure, including common functional groups and conformations. ','/science/chemistry/bond-line-structures-alkanes-cycloalkanes','2016-09-12 22:07:53','2016-09-12 22:07:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (180,20,NULL,'Intro to biology','intro-to-biology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','New to biology? You\'re in the right place! Start your biological journey here.','/science/biology/intro-to-biology','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (181,20,NULL,'Studying for the AP Biology exam?','studying-for-ap-bio-exam','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Review for the AP Biology exam by working through practice problems with Sal!','/science/biology/studying-for-ap-bio-exam','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (182,20,NULL,'Chemistry of life','chemistry--of-life','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Learn how chemistry makes life possible! From you, to your dog, to your dinner, to the global ecosystem, all living systems are made out of atoms that obey the basic rules of chemistry. Here, you can learn about the key properties of atoms, including what particles they contain, how they are organized, and how they form chemical bonds with one another.','/science/biology/chemistry--of-life','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (183,20,NULL,'Water, acids, and bases','water-acids-and-bases','https://cdn.kastatic.org/googleusercontent/TXubWO_6rTgD_g0-hxp5xHiI5_RCqAVPjqp4V1c2bvxaFv5YcB_s8V6tDBL41Mkk0924LqsOrItHWAb_maZgF2V_=s128-c','Where would we be without water? Well, we probably wouldn\'t exist at all!  Learn more about this fascinating molecule: its hydrogen bonds, its properties, and why it\'s a key ingredient for biological systems of all sizes. Plus, brush up on the acid-base chemistry that takes place in water (and learn how it works in your own bloodstream)!','/science/biology/water-acids-and-bases','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (184,20,NULL,'Properties of carbon','properties-of-carbon','https://cdn.kastatic.org/googleusercontent/Jt2IdXVhI5WABZDSWAZJGdN_r55e8cv38SHDlA5yjCyZYeO7vPeUnmDywmGjAz6U3EXnXKxD7HTfK4naNvnJ29Rk=s128-c','Did you know that your body is approximately 18% carbon? Learn more about carbon and why it makes a great building block for biological molecules. We\'ll look at the bonding properties of carbon, the structures of hydrocarbons, different types of isomerism, and some functional groups important in biology.','/science/biology/properties-of-carbon','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (185,20,NULL,'Macromolecules','macromolecules','https://cdn.kastatic.org/googleusercontent/-cy_mnclsXGMy3PWS_4W_L9lKn4gD_i1ax_HJbGiQIaS1-V-_1k87Yrf7BBQ1NRnM95kpRr582IajbiCb4a0k-6q=s128-c','There\'s an old saying, \"You are what you eat.\" In some senses, this is literally true! When we eat food, we take in the large biological molecules found in the food, including carbohydrates, proteins, lipids (such as fats), and nucleic acids (such as DNA), and use them to power our cells and build our bodies. Dive into the different types of macromolecules, what they are made up of, and how they are built up and broken down.','/science/biology/macromolecules','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (186,20,NULL,'Energy and enzymes','energy-and-enzymes','https://cdn.kastatic.org/googleusercontent/0bVfh-BSxTddsubOqBPxkdqwMTWEzzeTyYw1sUIhSjZHUJVfeW53MNyyrw2uVIEJ31aBCrN1m7bBHZ_zLHBzfSg=s128-c','You, like other living systems, are an amazing energy transformer. As you move your eyes to read these words, your body is busily converting chemical energy from your lunch into kinetic energy and thermal energy (heat). Learn more about how biological energy transfers work, as well as how protein machines called enzymes direct metabolic traffic through your cells.','/science/biology/energy-and-enzymes','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (187,20,NULL,'Structure of a cell','structure-of-a-cell','https://cdn.kastatic.org/googleusercontent/fiJERIcthlfUe4R53_1_U-ABQIcFmoJt8Gdc7NaIyymG1ruQEqALTlKo1zJw3H3x2rMAe15wTnaAZGvaPb1SoQQZMw=s128-c','You, my friend, are made up of cells. Lots and lots of them. Some of them are eukaryotic (human), but many more of them are prokaryotic, thanks to the friendly bacteria of your gut, skin, and other body systems. Jump in to learn more about prokaryotic and eukaryotic cells and the complex and beautiful structures inside of them.','/science/biology/structure-of-a-cell','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (188,20,NULL,'Membranes and transport','membranes-and-transport','https://cdn.kastatic.org/googleusercontent/lBeLxUZKyEXKwE0WrR3-vGatxcIHGrHRH-dgJNPgu3dwPAUbWvPGz_jyFw9je1vOyXJZHepi-EalHDXksXKvsWU=s128-c','How do the cells in your body define their boundaries (and control what comes in or goes out)? As it turns out, cells have a sophisticated and flexible barrier, the plasma membrane, and a wide array of strategies for transporting molecules in and out. Learn more about what the membrane\'s made of and how different types of molecules move across it.','/science/biology/membranes-and-transport','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (189,20,NULL,'Cellular respiration','cellular-respiration-and-fermentation','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','How do your cells extract energy from the food that you eat? As it turns out, cells have a network of elegant metabolic pathways dedicated to just this task. Learn more about cellular respiration, fermentation, and other processes that extract energy from fuel molecules like glucose.','/science/biology/cellular-respiration-and-fermentation','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (190,20,NULL,'Photosynthesis','photosynthesis-in-plants','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','There\'s a lot of carbon in your body - in DNA, proteins, carbohydrates, fats, and other key biological molecules. How did that carbon get there? Drumroll... photosynthesis! Learn how plants and other photosynthetic organisms use light energy to make sugars from carbon dioxide and water, bringing both chemical energy and readily usable carbon into Earth’s global ecosystem.','/science/biology/photosynthesis-in-plants','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (191,20,NULL,'Cell signaling','cell-signaling','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Did you know that your cells are continually \"talking\" to one another? Cells communicate by sending and receiving chemical signals. This molecular conversation allows the cells in your body to coordinate their activities, forming a functional, harmonious whole (a.k.a., you!). Learn more about the remarkable process of cell signaling.','/science/biology/cell-signaling','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (192,20,NULL,'Cell division','cellular-molecular-biology','https://cdn.kastatic.org/googleusercontent/D1-eo0td3MkVW9OyvwxcVS8wvFH3XL_Ue7WBIXrYaxuaUyPaYCPTLFx67IatNFZooqtsITxww98PDOIo1Z1-IKw=s128-c','All living things, including you, are made up of cells. Find out how cells divide through mitosis and meiosis, how the cell cycle is controlled, and how problems in cell cycle control can lead to cancer.','/science/biology/cellular-molecular-biology','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (193,20,NULL,'Classical and molecular genetics','classical-genetics','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Have you ever been told that you have your mother\'s eyes or grandfather\'s nose? Learn why traits run in families and how they\'re passed on. We\'ll look at Mendel\'s model of inheritance, see how it\'s been revised and extended in the past 150 years, and get a sneak preview of the modern concept of a gene.','/science/biology/classical-genetics','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (194,20,NULL,'DNA as the genetic material','dna-as-the-genetic-material','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','What exactly is DNA? This seemingly simple polymer, made up of just four different types of monomers, serves as the genetic material for all living organisms. For example, your DNA provides instructions for building and maintaining your unique body and can be passed on if you have children. Learn more about the discovery, structure, and synthesis of this remarkable molecule.','/science/biology/dna-as-the-genetic-material','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (195,20,NULL,'Central dogma (DNA to RNA to protein)','gene-expression-central-dogma','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','How does a gene in your DNA provide instructions for building a protein? In gene expression, a DNA sequence is first copied to make an RNA molecule, which is then \"decoded\" to build a protein. Learn more about this remarkable process, shared by all living things.','/science/biology/gene-expression-central-dogma','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (196,20,NULL,'Gene regulation','gene-regulation','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','You have tens of thousands of genes in your genome. Does that mean your cells express all of those genes, all the time? Not by a long shot! Even an organism as simple as a bacterium must carefully regulate gene expression, ensuring that the right genes are expressed at the right time. Learn more about the mechanisms cells use to turn genes \"on\" and \"off.\"','/science/biology/gene-regulation','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (197,20,NULL,'Biotechnology','biotech-dna-technology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','What is biotechnology? Well, if you\'ve ever taken the antibiotic penicillin or eaten bread made with yeast, you\'ve experienced it firsthand! Biotechnology is the use of a living organism, or some component of a living system, to make a useful product. Learn more about modern biotechnology and how it is used in research and biomedical applications.','/science/biology/biotech-dna-technology','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (198,20,NULL,'Developmental biology','developmental-biology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Did you know that a frog starts out as a single cell? In fact, so do dogs, elephants, and even human beings! Learn how multicellular organisms end up with many different cell types, organized into the tissues and organs of the body.','/science/biology/developmental-biology','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (199,20,NULL,'Bacteria and archaea','bacteria-archaea','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','They\'re everywhere. They number in the millions of trillions of trillions. They live on every surface, in every environment, and even in your gut. That\'s right...I\'m talking about prokaryotes! Learn more about bacteria and archaea.','/science/biology/bacteria-archaea','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (200,20,NULL,'Viruses','biology-of-viruses','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Viruses: Are they alive? Are they dead? Are they...undead? Learn more about these fascinating (and clinically important) particles that occupy a \"gray area\" between living and non-living things.','/science/biology/biology-of-viruses','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (201,20,NULL,'Evolution and the tree of life','her','https://cdn.kastatic.org/googleusercontent/2imkDnfWflBMG4XPsxjoWezngnBeUNupfOsYfGX41DxwVcoX9h63e4ow0sftMmA5iAQ0Ck4-1XSD6SqeM25kJlE=s128-c','Discover the diversity of life on Earth and the forces that shape it! In this section, you can learn about evolution, natural selection, and the tree of life, as well as basic population genetics. Special bonus: take a peek into the amazing worlds of bacteria and viruses.','/science/biology/her','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (202,20,NULL,'History of life on Earth','history-of-life-on-earth','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Our planet has an amazing history spanning about 4.5 billion years! Learn more about how Earth formed, the early history of life on Earth, and the origins of humans.','/science/biology/history-of-life-on-earth','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (203,20,NULL,'Ecology','ecology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Why are polar bears found only in the Arctic? Why does mildew grow in your shower and not (hopefully) in your sock drawer? Learn how ecologists study the interactions between organisms and their environment, and how these interactions affect where, and in what numbers, different types of organisms are found.','/science/biology/ecology','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (204,20,NULL,'Biodiversity and conservation','biodiversity-and-conservation','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','From penguins to palm trees to bacteria, our planet is host to an awe-inspiring diversity of organisms. Dive deep into Earth\'s breathtaking biodiversity, learn why this biodiversity is in peril today, and discover what you can do to protect the species and ecosystems that support human life on Earth. This content is provided by the California Academy of Sciences.','/science/biology/biodiversity-and-conservation','2016-09-12 22:08:01','2016-09-12 22:08:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (205,20,NULL,'Behavioral biology','behavioral-biology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','What do a dog begging for food, and ant leaving a chemical trail to food, and a human baby learning language all have in common? These are all examples of behaviors. Behavior is a crucial way that animals interact with each other and with their environment. Learn more about types of animal behaviors and how behavior is shaped by genes and environment.','/science/biology/behavioral-biology','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (206,20,NULL,'Principles of physiology','principles-of-physiology','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','How is your body organized? How do your organ systems work together to keep you running? What keeps your blood at the right pH or your body at the right temperature? Learn more about tissues, organs, and organ systems and the control mechanisms that keep internal conditions steady in humans and other animals.','/science/biology/principles-of-physiology','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (207,20,NULL,'Human biology','human-biology','https://cdn.kastatic.org/googleusercontent/WLQu_xd8m9I3sRofX1rdIeSKsKzYf-QvQxSNKPYO_Dd0aZYs_FwA4uZTsyG233X40yRP7uEJeLe4cXyzMlSv49Y=s128-c','Your body is an amazing system! The human body is made up of groups of organs, called organ systems, that work together to keep the body in balance. In this section, we\'ll travel from the circulatory system, to the nervous system, to the immune system and beyond. Learn about the amazing biology that keeps your body ticking!','/science/biology/human-biology','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (208,20,NULL,'Plant biology','new-topic-2016-07-20T16:26:10.463Z','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Plants may seem less interactive than animals. But they actually have many ways of responding to their environment, changing their growth or physiology to make the best use of available resources. Learn more about plants and their response to environmental cues.','/science/biology/new-topic-2016-07-20T16:26:10.463Z','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (209,20,NULL,'Crash Course: Biology and Ecology','crash-course-bio-ecology','https://cdn.kastatic.org/googleusercontent/GxdXAwR1xRWfjcT7loP8qSOQDJh14aZUPS3OerpRsPqFDt1Vb1sIWiDLwkyBeGFICkakPYeVL9kaCi3h79PUd7ps=s128-c','Brush up on a wide range of biology topics, from biochemistry to mitosis to ecology, with fun and informative Crash Course videos!','/science/biology/crash-course-bio-ecology','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (210,20,NULL,'Meet the biology professional','meet-the-biology-professional','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Do you enjoy your biology class, like nature, have an interest in medicine, or simply feel lots of curiosity about living things? If so, you may be thinking about focusing on or majoring in biology. Learn more about what it\'s like to be a biologist by hearing from professionals in a wide range of fields, from primatology to personalized genetics to high school teaching!','/science/biology/meet-the-biology-professional','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (211,20,NULL,'Community explanations','bio-community-explanations','https://cdn.kastatic.org/googleusercontent/GcN8emXH3LUWlUYXyX_0lQchPMQjFTwrbjOCBLGfagRGBre_FvCAWgBqaEwQofiGSQBVVLiukjL6-ps86wQZRPE=s128-c','Calling great explainers of the world!\n\nHelp us answer frequently asked biology questions by writing clear, deep, engaging explanations. Your explanation may even be selected for an article on Khan Academy (with full credit to you)!','/science/biology/bio-community-explanations','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (212,20,NULL,'Ask a biologist (archive)','ask-a-biologist','https://cdn.kastatic.org/googleusercontent/iLEcw-it47-n0IY_OFU2CNXeDKJ7Qux0kXTg9wU_ehHOAvQboIHWHhvNC2sUbEVIjXEHvGjS270kZSylvzSJhiE=s128-c','Archived questions from open question and answer session that ran July 28-August 2, 2015. The session is no longer open for questions, but can still be browsed. Please feel free to ask your own questions in the comments sections below other videos and questions.','/science/biology/ask-a-biologist','2016-09-12 22:08:02','2016-09-12 22:08:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (213,21,NULL,'Human anatomy and physiology','human-anatomy-and-physiology','https://cdn.kastatic.org/googleusercontent/bPhY2lw8IJYuOCIcKCEBBYAcEKmbkRrq6gOQEVWlYbHTiyaruDtzYYkhDg_v9eQKyFtDAg50SOktsRjsqrltIcHIaw=s128-c','Get introduced to all of the major organ systems of the body. You\'ll learn some general anatomy (roadmap for your body), and how the organs work to keep you alive! Watch some videos, read some articles, try some flashcards, and then quiz yourself!','/science/health-and-medicine/human-anatomy-and-physiology','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (214,21,NULL,'Advanced circulatory system physiology','circulatory-system','https://cdn.kastatic.org/googleusercontent/Sh00eHOXzwWlZSpidVei3AkCAZ_fxuIPyv9u8PcqjKxyzW_vLmeKboAluuVydpBa18Gsm55qqUvqRjR6dnkzkTi2ig=s128-c','Your heart sits in the middle of your chest and pumps blood from about 4 weeks after conception until the day that you die.  It never stops, and over your lifetime it will pump ~175 million liters of blood.  To visualize that, imagine the amount of water that falls over Niagara falls in a few minutes.  Remarkable!  This little pump is the size of your clenched fist and in an adult can weigh about 300 grams.  Watch these videos to learn more about how the heart works, blood flow in arteries and veins, blood pressure, and lymphatics.','/science/health-and-medicine/circulatory-system','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (215,21,NULL,'Circulatory system diseases','circulatory-system-diseases','https://cdn.kastatic.org/googleusercontent/Ruvba3cAx_ouKl37ztq5GRHYGnYMcGu-Jwfu3W92a86x_29jH32MerRoWpCGnIvHAz5VZ-IwQ73RGLtf511ein0PgA=s128-c','With the heart pumping 24 hours a day, 7 days a week, it’s absolutely vital to make sure things are flowing smoothly (pun intended!). Unfortunately, this isn’t always the case, and different parts of the circulatory system can cause problems: your heart, your blood vessels, and even the fluid in your tissues and blood itself can be the issue.  To further complicate things, the underlying reasons for circulatory system problems vary from your genes (nature) to your lifestyle habits (nurture). An understanding of how different diseases can affect your circulatory system is important to combat this growing problem in the world.','/science/health-and-medicine/circulatory-system-diseases','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (216,21,NULL,'Advanced respiratory system physiology','respiratory-system','https://cdn.kastatic.org/googleusercontent/SFoEbSvfNlmqF_QE0aA6Uz6FJRfDbc6NwNrJl1vBKSxNgTgzeHQVuUvIB9lgE8YTdL3p_vvyT9wj3YRR4H8F_Jg=s128-c','Place your hand on your ribs and inhale deeply. You’ll notice that your chest expands and your back straightens. As this occurs, air is rushing through your windpipe and branches off to either your left or right lung. After 20 to 30 more branch points, oxygen in the air ends up in the alveoli where it diffuses into the liquid that surrounds the alveoli, and slips into the blood.  This microscopic gas exchange occurs rapidly, oxygen is taken into the body and carbon dioxide is removed from the body, and then you exhale. Learn more about the intricate and fascinating respiratory system in these videos!','/science/health-and-medicine/respiratory-system','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (217,21,NULL,'Respiratory system diseases','respiratory-system-diseases','https://cdn.kastatic.org/googleusercontent/6V-z7bgrx-M9S27TyEsiBUlx3ULKLrJCbYRZhQkZ3lIAjfq4oexfsAV_RdNgB_ZG-vXH696GuvSrlvTZrPU1DbU=s128-c','Our lungs are composed of a bronchial tree (think of an upside down tree with millions of leaves), blood vessels bringing blood in and out, and a protein-rich fluid that forms a matrix holding everything together!  If any part of this well-balanced organ isn’t working properly, a person can be left feeling short of breath.  The lungs are also exposed to the outside environment, making them prone to infections.  To counter infections, the lungs are lined with cells that have tiny protein bristles which wave back and forth and can literally sweep away dangerous bacteria.  Learn more about diseases of the lungs and how modern medicine helps to keep them healthy!','/science/health-and-medicine/respiratory-system-diseases','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (218,21,NULL,'Advanced hematologic system physiology','advanced-hematologic-system','https://cdn.kastatic.org/googleusercontent/JvipnEpkPpQ6OFLLZc9R_HhbmwBG7xCYekbl7vLlxhw-IaNP4KrWh5uZgJVGeveOD4vXJ_AIbLTLf9WqAQHPRI01=s128-c','It takes between 30 seconds to a minute for your blood to travel from your heart, to your body, and back to the heart again - perhaps a bit longer if the trip is out to your big toe! Our blood is incredibly important for transporting oxygen throughout the body. Hemoglobin, the protein that fills our blood cells, has wonderful mechanisms to allow it to bind to both oxygen and carbon dioxide. This is important for effective and quick transport of the gases around our body. Our blood is about 45% cells and 55% plasma, so the old adage “blood is thicker than water” quite literally holds true in scientific terms! Learn more about how this amazing system works in the following videos.','/science/health-and-medicine/advanced-hematologic-system','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (219,21,NULL,'Hematologic system diseases','hematologic-system-diseases-2','https://cdn.kastatic.org/googleusercontent/JvipnEpkPpQ6OFLLZc9R_HhbmwBG7xCYekbl7vLlxhw-IaNP4KrWh5uZgJVGeveOD4vXJ_AIbLTLf9WqAQHPRI01=s128-c','Blood is incredibly important in combating disease and the healing process after an injury. It acts as a highway for medicine, stops bleeding, fights infections, controls cells from multiplying too fast, and so much more. But things can go wrong with blood too! What if your blood couldn’t clot and stop you from bleeding, or started to clot uncontrollably? What if your red blood cells or white blood cells suddenly disappeared? Blood contains many different types of tissues doing very different jobs, making diseases of the blood produce a variety of symptoms, including continuously feeling tired and bone pain. Learn about the different blood diseases, how they are diagnosed, and the cool ways health professionals treat these conditions.','/science/health-and-medicine/hematologic-system-diseases-2','2016-09-12 22:08:10','2016-09-12 22:08:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (220,21,NULL,'Advanced endocrine system physiology','advanced-endocrine-system','https://cdn.kastatic.org/googleusercontent/JvipnEpkPpQ6OFLLZc9R_HhbmwBG7xCYekbl7vLlxhw-IaNP4KrWh5uZgJVGeveOD4vXJ_AIbLTLf9WqAQHPRI01=s128-c','When you’re nervous before an important speech, or asking someone out on a date, you might feel butterflies in your stomach. This is actually the result of your endocrine system releasing hormones! You can’t really point to any single organ as “the endocrine system”, because it’s actually a family of glands that secrete hormones into the body. Hormones seep into the blood (imagine putting a tea bag into hot water), and as the blood flows around the body, it carries with it these important hormone molecules that interact with specific target cells and organs.  This signaling system helps to keep the entire body well-balanced and on the same page.','/science/health-and-medicine/advanced-endocrine-system','2016-09-12 22:08:11','2016-09-12 22:08:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (221,21,NULL,'Endocrine system diseases','endocrine-system-diseases','https://cdn.kastatic.org/googleusercontent/pO0d-vtH04KTJEHLubdGHkUq2sgE7ZarRIJX3QHLDr1BQ0AiUiP7I6zYex-S4uX6R3ksjYMoRZR8ooJImWh1OcuD=s128-c','','/science/health-and-medicine/endocrine-system-diseases','2016-09-12 22:08:11','2016-09-12 22:08:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (222,21,NULL,'Advanced nervous system physiology','nervous-system-and-sensory-infor','https://cdn.kastatic.org/googleusercontent/kpwhpCDkWcfz1wupHQbHg82A9zh9XWwFDBj1tY0cdeKzzl99O_veUDF43L-N9tOkJFqIsg0Y41V0MiwxonLUn0nf=s128-c','There are billions and billions of neurons in your brain (about 85 billion), and they’re all sending electrical signals throughout your body right now! They tell your eyes to move across this page, how to interpret the words that you read, how to maintain your posture, your heart rate, and your breathing...all of it in a fraction of a second.  In this section, we’ll explore the nature of this vast, complex system, from the cellular level to how it operates at a sensory level. A common misconception is that we only have 5 senses (see, smell, taste, hear, and feel), but we have many more that are nuanced but equally important. Learn more about how our bodies are designed to interact with the world.','/science/health-and-medicine/nervous-system-and-sensory-infor','2016-09-12 22:08:11','2016-09-12 22:08:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (223,21,NULL,'Nervous system diseases','nervous-system-diseases','https://cdn.kastatic.org/googleusercontent/NFlso8EBEpNkTIVOSQ5REFrOnIWSpLb5lbjYM5Q3kuq4dScgAodeahNEXljtoESlXpXACRtfxdfMxOJLqmOzFpKx9A=s128-c','','/science/health-and-medicine/nervous-system-diseases','2016-09-12 22:08:11','2016-09-12 22:08:11',1);
