insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (267,27,NULL,'What\'s next?','whats-next','https://cdn.kastatic.org/googleusercontent/OiMKpRCVWv5RvkIZVqAiYqHF6Zl5WTqfAeBEO7WbFHTL2N5ye6jO2ykD0yKcVBClnziSWChqZ5EXQV90h2a1tQb1=s128-c','Updates on what is coming next.','/partner-content/pixar/whats-next','2016-09-12 22:08:51','2016-09-12 22:08:51',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (268,29,NULL,'Art history basics','art-history-basics','https://cdn.kastatic.org/googleusercontent/4bmzjo2WiC17m55Q8TYPtIFy4y3zg-hhVdo0B6IlWeHMJCKBb4HrgFwm6ZFzdOioNjtFK532qV-Bd8znpVT74Eyb6Q=s128-c','We understand the history of humanity through art. From prehistoric depictions of woolly mammoths to contemporary abstraction, artists have addressed their time and place in history and have expressed universal human truths for tens of thousands of years. ','/humanities/art-history/art-history-basics','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (269,29,NULL,'Prehistoric art in Europe and West Asia','prehistoric-art','https://cdn.kastatic.org/googleusercontent/sEXzlU_TrENDX4jGUDaRmzgz7sKXx4ZlNdufT1wrHE0G3kn00hJRhvBsnH9wn7BiCSYjNzL7nBS0LoDPSBXE0osk=s128-c','You’ve seen the drawing of human evolution showing a procession of monkey, ape, primitive and modern man? Well, somewhere along that line early man could be shown holding a paint brush and a chisel. Mankind has been making art for at least 100,000 years. Why was the earliest art made? What might it have once meant? \"Prehistoric\" refers to a time before writing, which makes these questions particularly difficult to answer—but fascinating to contemplate.','/humanities/art-history/prehistoric-art','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (270,29,NULL,'Art of the ancient Mediterranean','ancient-art-civilizations','https://cdn.kastatic.org/googleusercontent/7F--c-kL2-RYKIaxc4LVlUvcGTqlblrdbWZCGIXlwRXfAkB-wFBdIazQZrApdiv7ixC-V7Z0c49Z4-8X79o2Tb0=s128-c','3000 B.C.E. - 400 C.E.: The Great Pyramids at Giza, the Parthenon in Athens, the Colosseum in Rome and more.','/humanities/art-history/ancient-art-civilizations','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (271,29,NULL,'Art of Medieval Europe','medieval-world','https://cdn.kastatic.org/googleusercontent/ST4rxOtN1s1d3DPi0zP9kh6MYlQAXmgnhW9C20K4-cwi103rhgb0Xe7k6WYBRCVZqSZRBkgFbE8_EhmiYYd8Q2w=s128-c','400-1400: This was the period during which Christianity first flourished in Europe. Christianity—and specifically Catholicism in the Latin West—brought with it new ways of seeing the world while often rejecting the traditions and learning of the ancient world.','/humanities/art-history/medieval-world','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (272,29,NULL,'Art of the Islamic world','art-islam','https://cdn.kastatic.org/googleusercontent/AYObZWVTP0QglLTlDLsBwlLTJ9KFjvLoQuyT-FqxogXHJJ_U9pQkWmsYiZfRQ8fji-yL3MopiP7cTf6jOYug3GnS=s128-c','Studying the Art of the Islamic world is challenging—partially because of the large geographic and chronological scope of Islam. Islam has been a major religion and cultural force for over fourteen centuries and continues to be so today. ','/humanities/art-history/art-islam','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (273,29,NULL,'Renaissance & Reformation in Europe','renaissance-reformation','https://cdn.kastatic.org/googleusercontent/r0sSGfI-p6sASHIJZyAzFv-n_EDE5AqzkIu9t2G0SM0jtcoVnfnmk-LS39Iw8AgHKvMS-6GE49HNK-LAMAReA3li=s128-c','1400-1600: In part, the Renaissance was a rebirth of interest in ancient Greek and Roman culture. It was also a period of economic prosperity in Europe—particularly in Italy and in Northern Europe. In 1517 a German theologian and monk, Martin Luther, challenged the authority of the Pope and sparked the Protestant Reformation. By challenging the power of the Church, and asserting the authority of individual conscience, the Reformation laid the foundation for the value that modern culture places on the individual. ','/humanities/art-history/renaissance-reformation','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (274,29,NULL,'Baroque to Neoclassical art in Europe','monarchy-enlightenment','https://cdn.kastatic.org/googleusercontent/379zlfrJZzUzidrLOFic1frqh3yQeXRkoZr3W1cbYRumNoFRWeiciWcA4YLLBeYil8wD-btmITJeAtDl9vUHwhUVWA=s128-c','1600-1800: The monarchs of Europe embraced the most ornate elements of 17th-century art. Rulers invested vast resources on elaborate church facades, stunning, gold-covered chapels and strikingly-realistic painting and sculpture. While in the newly independent Dutch Republic, a market emerged to meet the Protestant tastes of the growing merchant class. By the 18th century, Voltaire, Rousseau and other intellectuals had put forward Enlightenment ideas that would spark an age of revolution and usher in the modern world.','/humanities/art-history/monarchy-enlightenment','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (275,29,NULL,'Art of the Americas to World War I','art-americas','https://cdn.kastatic.org/googleusercontent/tEGftZnQgRSEvHx7SHYvY4VqNkDTk9rBXzV3H6rXWjd_znTk-r-WS1awA1cxbQ1zg3jSfFYvHwer3CWBKc88gbEDGg=s128-c','Here\'s where you\'ll find art from the ancient cultures of North and South America (and what we call Mesoamerica) through the period of European colonization and independence—and into the 19th century.','/humanities/art-history/art-americas','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (276,29,NULL,'Art in 19th century Europe','becoming-modern','https://cdn.kastatic.org/googleusercontent/1vGf2vbBPF9RR-DPZbYorM17KPCKduXf-Q2W8d-LJrv6YAd1HuIFZ1jyuWSTYIozWjWw5QrEOA0WVDOY6EzthSs=s128-c','People use the term “modern” in a variety of ways—often very loosely, with a lot of implied associations of new, contemporary, up-to-date, and technological. The development of capitalism, urban culture, technology, and a growing middle class had an enormous impact on art.','/humanities/art-history/becoming-modern','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (277,29,NULL,'Expressionism to Pop Art','art-1010','https://cdn.kastatic.org/googleusercontent/zm0t6n4hyGZgjyKoY7LRtUvDz6HbH8EeP6x-dGeTEe1lSmE2JM5HC9Da8GeiVNPpmAWdPDaXy8kp5fN2df3h5UqD=s128-c','This is the era of \"isms\"—Fauvism, Expressionism, Cubism, Surrealism, Abstract Expressionism and more. The avant-garde is unleashed in the 20th century and explores both abstraction and representation as a means to \"represent\" the radical changes, violence, alienation, and beauty that characterize the century.','/humanities/art-history/art-1010','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (278,29,NULL,'Global contemporary art','global-culture','https://cdn.kastatic.org/googleusercontent/Q8rNN4x3c36GKN6de5huzh0F-nu8CmGxFAS9qsTjhmLucwyAXCasi7XhP77ksMdRsRPCmqZ1NlBqa15GyyV_ZOo=s128-c','1970-the present: Beuys, Hirst, Hadid and more.','/humanities/art-history/global-culture','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (279,29,NULL,'Art of Asia','art-asia','https://cdn.kastatic.org/googleusercontent/NRKvZB9KsljG6erfCvpo5nx21iMQBMpy_1quFjr9ilGvp6FwI41r0aoQku7_pIckrk0KOVWCW2xTs0I7QiznIAiJ=s128-c','This section looks at the art and cultures of Imperial China (for ancient China please see the section, \"Ancient Art and Civilizations\" above), Hindu and Buddhist from South Asia, the great temple and palace complexes of Southeast Asia, and the art and architecture of Korea and Japan. Dr. Beth Harris and Dr. Steven Zucker of Smarthistory together with leading art historians, and our museum partners have created hundreds of short engaging conversational videos and articles, making Khan Academy one of the most accessible and extensive resources for the study of the history of art.','/humanities/art-history/art-asia','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (280,29,NULL,'Art of Africa','art-africa','https://cdn.kastatic.org/googleusercontent/0oBzcC4uYZyKyhSI83N5B-ZVV2-IXE7utbMers5DGvV0PRL94R-2yAm4cSBaTaCnJZs3_RuUNEExZZS_mwJMdHSi-g=s128-c','Discover the diverse arts of Africa from select cultures and time periods on the world’s second largest continent.','/humanities/art-history/art-africa','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (281,29,NULL,'Art of Oceania','art-oceania','https://cdn.kastatic.org/googleusercontent/m6vafU_z2dSqL_ewtPTMbIJr0dMy2OOckJ6FxoXcGfplidqx3qMSebrhJw0AflxSKV-TvtCS_Wda_icrIPlqAr_w=s128-c','Oceania is a vast area of the Pacific Ocean that includes the island regions of Melanesia, Micronesia, and Polynesia.','/humanities/art-history/art-oceania','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (282,29,NULL,'For teachers','art-history-for-teachers','https://cdn.kastatic.org/googleusercontent/oGDFhA8uf7mR7QAVWbgAIe5bvv9a-IhvCahCnlgj5n6y5XTYoe4lK77vWFOmZfSGHUNfEoTdojdfqdBKjc6yYkb_3A=s128-c','Look here for syllabi, sample discussion questions, and other useful resources for teachers.','/humanities/art-history/art-history-for-teachers','2016-09-12 22:08:58','2016-09-12 22:08:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (283,30,NULL,'The parts of speech','partsofspeech','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','Nouns, verbs, you name it!  These tutorials cover the English parts of speech: the building blocks of sentences.  (More is coming soon!  To quote user @Dhanishthaghosh, \"Patience bears a golden fruit!\")','/humanities/grammar/partsofspeech','2016-09-12 22:09:05','2016-09-12 22:09:05',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (284,30,NULL,'Punctuation','punctuation','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','Punctuation is the collection of squiggles, dots, and lines that we use to separate sentences and their parts.  Is a question mark punctuation?  Yes, and so is an exclamation point!','/humanities/grammar/punctuation','2016-09-12 22:09:05','2016-09-12 22:09:05',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (285,30,NULL,'Syntax','syntax','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','Syntax involves arranging words to create logical phrases, clauses, and sentences. This is a big topic, so we\'ll be covering a lot, including: dependent and independent clauses; simple, complex, compound, and compound-complex sentences; subject-verb agreement; dangling modifiers; and much more!','/humanities/grammar/syntax','2016-09-12 22:09:05','2016-09-12 22:09:05',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (286,31,NULL,'Music basics','music-basics2','https://cdn.kastatic.org/googleusercontent/9-6AOgMkdzsmPlH2XOJ-yjqFlgdmk_lW2Tye6BqAPkaMuAPqdyt7LHRGAhxvhK1sDaq_zEgk3FpJz_Ybhf0WOeya=s128-c','The basic principles of music are explained in plain language with helpful graphics and live video demonstrations. If you have ever wondered “How does music work?” than you’ll find answers here. Presented by the All-Star Orchestra music director Gerard Schwarz.','/humanities/music/music-basics2','2016-09-12 22:09:11','2016-09-12 22:09:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (287,31,NULL,'Masterpieces old and new','music-masterpieces-old-new','https://cdn.kastatic.org/googleusercontent/cZq5RThMebkm-mv3Y50euuyktBxDGrZsXVtSE5Zi6_Y9a5bsyka2ywsSMMgxfVJIug4ec-Y7qr4ndSK4wzQP2aFUSQ=s128-c','Discover what makes a masterpiece by learning how classical works are composed. Music Director Gerard Schwarz explains the history, context and compositional techniques behind famous orchestral works, from Beethoven to leading composers of the present day.','/humanities/music/music-masterpieces-old-new','2016-09-12 22:09:11','2016-09-12 22:09:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (288,31,NULL,'Instruments of the orchestra','music-instruments-orchestra','https://cdn.kastatic.org/googleusercontent/kwNxjXaR41DP4ej9n9coZcHm1gd6AYuDyFl19ycF3ms5bmslIMX9lKSg5GXFlA0WhSEJA1z6qbVFanfVyeNhrOVA=s128-c','Learn about the different instruments of the orchestra as they are presented and demonstrated by  principal players from famous symphony orchestras.','/humanities/music/music-instruments-orchestra','2016-09-12 22:09:11','2016-09-12 22:09:11',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (289,32,NULL,'The historian\'s toolkit','historians-toolkit','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','Learning about history isn\'t all grabbing golden idols from caves and running away from giant boulders. In this tutorial, learn the skills that historians use to interpret primary documents, write strong arguments, and answer questions about the past. ','/humanities/us-history/historians-toolkit','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (290,32,NULL,'Worlds collide (1491-1607)','precontact-and-early-colonial-era','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','The history of North America began long before Columbus set foot in a new world. But once he did, things would never be the same.','/humanities/us-history/precontact-and-early-colonial-era','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (291,32,NULL,'The road to revolution (1754-1800)','road-to-revolution','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','From colony to nation. Explore the dramatic events that separated the United States from Britain and the trials of the young republic and its citizens. ','/humanities/us-history/road-to-revolution','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (292,32,NULL,'The early republic (1800-1848)','the-early-republic','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','In the early nineteenth century, the United States expanded rapidly, fueled by new technology, growing markets, and the extension of democracy to all white men. But this prosperity came at a cost, gobbling up the lands of Native Americans in the West and the labor of enslaved people in the South. ','/humanities/us-history/the-early-republic','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (293,32,NULL,'The Civil War era (1844-1877)','civil-war-era','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','A house divided against itself cannot stand. The United States could no longer remain half-slave and half-free, and the ensuing war over the fate of slavery ripped apart the nation. ','/humanities/us-history/civil-war-era','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (294,32,NULL,'The Gilded Age (1865-1898)','the-gilded-age','https://cdn.kastatic.org/googleusercontent/uacHqpW7yl2F3QvFllRxg7FQ-kaciHK5IUQJncxkDd3sjfcXNVmrI0Y8j3qjrJ0eE9PgblqTWw1roemip2lA4eg=s128-c','After the Civil War, the United States emerged as the world\'s foremost industrial power. With that came great wealth and great poverty. ','/humanities/us-history/the-gilded-age','2016-09-12 22:09:18','2016-09-12 22:09:18',1);
