insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (360,45,NULL,'CAHSEE','cahsee','https://cdn.kastatic.org/googleusercontent/t6rxlsTIrJOfa5w6o_S4M77VSgf11ZV1sQ-97Olffxy4WlFj6G5j-7srFCvzB_A_7DgoTvZZSzbLhFaOcqYWXvBJ=s128-c','Sal working through the 53 problems from the practice test available at http://www.cde.ca.gov/ta/tg/hs/documents/mathpractest.pdf for the CAHSEE (California High School Exit Examination).  Clearly useful if you\'re looking to take that exam.  Probably still useful if you want to make sure you have a solid understanding of basic high school math.','/test-prep/cahsee-subject/cahsee','2016-09-12 22:10:29','2016-09-12 22:10:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (361,46,NULL,'Start here','introduction-ap-arthistory','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Here\'s where you\'ll find a list of resources for all 250 AP objects in one place. Also some helpful material about dates and maps and how to look at art.','/humanities/ap-art-history/introduction-ap-arthistory','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
