insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (363,46,NULL,'Global prehistory: 30,000-500 B.C.E. ','global-prehistory-ap','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Human expression existed across the globe before the written record. While prehistoric art of Europe has been the focus of many introductions to the history of art, very early art is found worldwide and shares certain features, particularly concern with the natural world and humans’ place within it.\n\nBy permission, © 2013 The College Board','/humanities/ap-art-history/global-prehistory-ap','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (364,46,NULL,'Ancient Mediterranean: 3500 B.C.E.-300 C.E. ','ancient-mediterranean-ap','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Artistic traditions of the ancient Near East and dynastic Egypt focused on representing royal figures and divinities and on the function of funerary and palatial complexes within their cultural contexts. Works of art illustrate the active exchange of ideas and reception of artistic styles among the Mediterranean cultures and the subsequent influence on the classical world.\n\nBy permission, © 2013 The College Board','/humanities/ap-art-history/ancient-mediterranean-ap','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (365,46,NULL,'Early Europe and Colonial Americas: 200-1750 C.E. ','early-europe-and-colonial-americas','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','','/humanities/ap-art-history/early-europe-and-colonial-americas','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (366,46,NULL,'Later Europe and Americas: 1750-1980 C.E.','later-europe-and-americas','https://cdn.kastatic.org/googleusercontent/379zlfrJZzUzidrLOFic1frqh3yQeXRkoZr3W1cbYRumNoFRWeiciWcA4YLLBeYil8wD-btmITJeAtDl9vUHwhUVWA=s128-c','From the mid-1700s to 1980 C.E., Europe and the Americas experienced rapid change and innovation. Art existed in the context of dramatic events such as industrialization, urbanization, economic upheaval, migrations, and wars. Countries and governments were re-formed; women’s and civil rights’ movements catalyzed social change.\n\nArtists assumed new roles in society. Styles of art proliferated and often gave rise to artistic movements. Art and architecture exhibited a diversity of styles, forming an array of “isms.” Works of art took on new roles and functions in society and were experienced by audiences in new ways. Art of this era often proved challenging for audiences and patrons to immediately understand.','/humanities/ap-art-history/later-europe-and-americas','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (367,46,NULL,'Indigenous Americas','indigenous-americas','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Art of the Indigenous Americas is among the world’s oldest artistic traditions. While its roots lie in northern Asia, it developed independently between c. 10,000 B.C.E. and 1492 C.E., which marked the beginning of the European invasions. Regions and cultures are referred to as the Indigenous Americas to signal the priority of First Nations cultural traditions over those of the colonizing and migrant peoples that have progressively taken over the American continents for the last 500 years.','/humanities/ap-art-history/indigenous-americas','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (368,46,NULL,'Africa: 1100-1980 C.E.','africa-ap','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Human life, which is understood to have begun in Africa, developed over millions of years and radiated beyond the continent of Africa. The earliest African art dates to 77,000 years ago. While interpretation of this art is conjectural at best, the clarity and strength of design and expression in the work is obvious.','/humanities/ap-art-history/africa-ap','2016-09-12 22:10:37','2016-09-12 22:10:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (369,46,NULL,'West and central Asia: 500 B.C.E.-1980 C.E.','west-and-central-asia','https://cdn.kastatic.org/googleusercontent/AYObZWVTP0QglLTlDLsBwlLTJ9KFjvLoQuyT-FqxogXHJJ_U9pQkWmsYiZfRQ8fji-yL3MopiP7cTf6jOYug3GnS=s128-c','The arts of West and Central Asia play a key role in the history of world art, giving form to the vast cultural interchanges that have occurred in these lands that link the European and Asian peoples.','/humanities/ap-art-history/west-and-central-asia','2016-09-12 22:10:38','2016-09-12 22:10:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (370,46,NULL,'South, East, and Southeast Asia: 300 B.C.E. - 1980 C.E.','south-east-se-asia','https://cdn.kastatic.org/googleusercontent/2viRLmVGGB4GPsAcdS0isQjqW0Td5HgVlHpNc-6rZ_t1JRfIqypoqrs5DsYWflSOU697vT8IAdoGrJHQDram8p7M=s128-c','The arts of South, East, and Southeast Asia represent some of the world’s oldest, most diverse, and most sophisticated visual traditions.','/humanities/ap-art-history/south-east-se-asia','2016-09-12 22:10:38','2016-09-12 22:10:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (371,46,NULL,'The Pacific ','the-pacific','https://cdn.kastatic.org/googleusercontent/m6vafU_z2dSqL_ewtPTMbIJr0dMy2OOckJ6FxoXcGfplidqx3qMSebrhJw0AflxSKV-TvtCS_Wda_icrIPlqAr_w=s128-c','The arts of the Pacific vary by virtue of ecological situations, social structure, and impact of external influences, such as commerce, colonialism, and missionary activity. Created in a variety of media, Pacific arts are distinguished by the virtuosity with which materials are used and presented.','/humanities/ap-art-history/the-pacific','2016-09-12 22:10:38','2016-09-12 22:10:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (372,46,NULL,'Global contemporary: 1980-present ','global-contemporary','https://cdn.kastatic.org/googleusercontent/cxnguYvIapHA9txdu_eQv2TpRuhP52Iz-2_u3JYkuysiGHS3VMxUtCDa3V9nej9CW7__FxqphaXUrW_4MwXaTT-e=s128-c','Global contemporary art is characterized by a transcendence of traditional conceptions of art and is supported by technological developments and global awareness. Digital technology in particular provides increased access to imagery and contextual information about diverse artists and artworks throughout history and across the globe.','/humanities/ap-art-history/global-contemporary','2016-09-12 22:10:38','2016-09-12 22:10:38',1);
