insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (814,3,13,'Adding decimals','add-decimals',NULL,'You get the general idea of decimals and what the digits in different places represent (place value). Now you\'re ready to do something with the decimals. Adding and subtracting is a good place to start. This will allow you to add your family\'s expenses to figure out if your little brother is laundering money (perhaps literally). Have fun!  Common Core Standard: 5.NBT.B.7','/math/arithmetic-home/arith-review-decimals/add-decimals/a/adding-decimals-without-the-standard-algorithm','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
