insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (819,3,13,'Multiplying decimals','mult-decimals',NULL,'The real world is seldom about whole numbers. If you precisely measure anything, you\'re likely to get a decimal. If you don\'t know how to multiply these decimals, then you won\'t be able to do all the powerful things that multiplication can do in the real world (figure out your commission as a robot possum salesperson, determining how much shag carpet you need for your secret lair, etc.).','/math/arithmetic-home/arith-review-decimals/mult-decimals/v/intro-to-multiplying-decimals','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (820,3,13,'Multiplying decimals: standard algorithm ','multiplying-decimals-sa',NULL,'You\'ve already multiplied decimals in the 5th grade. We\'ll show you some ways to handle slightly more complicated problems. Now you\'ll become super good at it with a bit more practice!  Common Core Standard: 6.NS.B.3','/math/arithmetic-home/arith-review-decimals/multiplying-decimals-sa/v/more-involved-multiplying-decimals-example','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (821,3,13,'Dividing decimals','div-decimals',NULL,'In this tutorial, we\'ll extend our division skills to include decimals! We get into some pretty fun stuff here including dividing decimals by whole numbers, and dividing whole numbers by decimals. Finally...dividing decimals by decimals.  Common Core Standard 5.NBT.B.7','/math/arithmetic-home/arith-review-decimals/div-decimals/v/dividing-completely-to-get-decimal-answer','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (822,3,13,'Dividing decimals: standard algorithm ','dividing-decimals-sa',NULL,'Learn long division with decimals. Solve problems like 30.24 divided by 0.42.','/math/arithmetic-home/arith-review-decimals/dividing-decimals-sa/v/dividing-decimals-with-hundredths','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (823,3,13,'Significant figures','arithmetic-significant-figures-tutorial',NULL,'There is a strong temptation in life to appear precise, even when you are aren\'t accurate.   If you  precisely measure one dimension of a carpet to be 3.256 meters and eyeball the other dimensional to be \"roughly 2 meters\", can you really claim that the area is  6.512 square meters (3.256 x 2)?  Isn\'t that a little misleading?  \n\nThis tutorial gets us thinking about this conundrum and gives us the best practices that scientists and engineers use to not mislead each other.','/math/arithmetic-home/arith-review-decimals/arithmetic-significant-figures-tutorial/v/significant-figures','2016-09-12 22:38:58','2016-09-12 22:38:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (824,4,14,'Overview and history of algebra','alg-overview-of-algebra',NULL,'Did you realize that the word \"algebra\" comes from Arabic (just like \"algorithm\" and \"al jazeera\" and \"Aladdin\")?  And what is so great about algebra anyway?  \n\nThis tutorial doesn\'t explore algebra so much as it introduces the history and ideas that underpin it.','/math/algebra-home/alg-intro-to-algebra/alg-overview-of-algebra/v/origins-of-algebra','2016-09-12 22:39:06','2016-09-12 22:39:06',1);
