insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1009,4,26,'Direct and inverse variation','alg-direct-and-inverse-variation',NULL,'Learn about direct and inverse variation, which are two types of relationships between two quantities. Direct variation is simply a proportional relationship, but inverse variation is more complicated and interesting. Gain some experience with it before we dive deeper into the world of rational functions.','/math/algebra-home/alg-rational-expr-eq-func/alg-direct-and-inverse-variation/v/direct-and-inverse-variation','2016-09-12 22:40:39','2016-09-12 22:40:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1010,4,26,'End behavior of rational functions','alg-end-behavior-of-rational-functions',NULL,'Learn about the ways in which rational functions behave as x approaches positive or negative infinity. This gets interesting Learn how to determine this behavior for any kind of rational function.','/math/algebra-home/alg-rational-expr-eq-func/alg-end-behavior-of-rational-functions/v/end-behavior-of-rational-functions','2016-09-12 22:40:39','2016-09-12 22:40:39',1);
