insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1108,5,35,'Angle introduction','geometry-angle-intro',NULL,'What is an angle and how do we label, measure and construct them? Great question. Luckily we have all your answers! To begin our journey towards understanding angles, we\'ll learn terms like vertex, acute, obtuse, circle arc, circumference, and protractor. These basic angle concepts will get you on the road to angle domination!  ','/math/geometry-home/geometry-angles/geometry-angle-intro/v/angle-basics','2016-09-12 22:42:20','2016-09-12 22:42:20',1);
