insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1112,5,35,'Angle types','angle-types',NULL,'Now that we know what angles are, let\'s dig a bit deeper and classify them and understand their properties better. Acute, obtuse, right, adjacent--all are angles that we will be become familiar with. ','/math/geometry-home/geometry-angles/angle-types/e/angle_types','2016-09-12 22:42:20','2016-09-12 22:42:20',1);
