insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1114,5,35,'Angles between intersecting lines','geometry-angles-between-lines',NULL,'This tutorial introduces us to angles.  It includes how we measure them, how angles relate to each other and properties of angles created from various types of intersecting lines.  Mr. Angle is actually far more fun than you might initially presume him to be.','/math/geometry-home/geometry-angles/geometry-angles-between-lines/v/angles-formed-by-parallel-lines-and-transversals','2016-09-12 22:42:20','2016-09-12 22:42:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1115,5,35,'Sal\'s old angle videos','old-angles',NULL,'These are some of the classic, original angle video that Sal had done way back when (like 2007).  Other tutorials are more polished than this one, but this one has charm.  Also not bad if you\'re looking for more examples of angles between intersected lines, transversals and parallel lines.','/math/geometry-home/geometry-angles/old-angles/v/introduction-to-angles-old','2016-09-12 22:42:20','2016-09-12 22:42:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1116,5,36,'Properties of shapes','geometry-properties-shapes',NULL,'Learn how different shapes have different numbers of corners, different side lengths, and so on. Use these properties to help you recognize shapes.','/math/geometry-home/geometry-shapes/geometry-properties-shapes/v/sides-corners','2016-09-12 22:42:25','2016-09-12 22:42:25',1);
