insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1148,5,41,'Solid geometry','volume-tutorial',NULL,'Tired of perimeter and area and now want to measure 3-D space-take-upness.  Well you\'ve found the right tutorial.  Enjoy!','/math/geometry-home/geometry-volume-surface-area/volume-tutorial/e/solid_geometry','2016-09-12 22:43:03','2016-09-12 22:43:03',1);
