insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1150,5,41,'Cross sections of 3D objects','cross-sections',NULL,'It\'s pretty difficult to work, mentally, with 3D objects. One way of overcoming that difficulty is in thinking about cross sections of such objects. These are 2D slices of 3D objects, and so we\'re back with the familiar 2D world.','/math/geometry-home/geometry-volume-surface-area/cross-sections/v/vertical-slice-of-rectangular-pyramid','2016-09-12 22:43:03','2016-09-12 22:43:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1151,5,41,'Koch snowflake fractal','koch-snowflake',NULL,'Named after Helge von Koch, the Koch snowflake is one of the first fractals to be discovered. It is created by adding smaller and smaller equilateral bumps to an existing equilateral triangle.  Quite amazingly, it produces a figure of infinite perimeter and finite area!','/math/geometry-home/geometry-volume-surface-area/koch-snowflake/v/koch-snowflake-fractal','2016-09-12 22:43:03','2016-09-12 22:43:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1152,5,41,'Heron\'s formula','heron-formula-tutorial',NULL,'Named after Heron of Alexandria, Heron\'s formula is a power (but often overlooked) method for finding the area of ANY triangle.  In this tutorial we will explain how to use it and then prove it!','/math/geometry-home/geometry-volume-surface-area/heron-formula-tutorial/v/heron-s-formula','2016-09-12 22:43:03','2016-09-12 22:43:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1153,5,42,'The Pythagorean theorem','pyth-theor',NULL,'Named after the Greek philosopher who lived nearly 2600 years ago, the Pythagorean theorem is as good as math theorems get (Pythagoras also started a religious movement).  It\'s simple.  It\'s beautiful.  It\'s powerful.\n\nCommon Core Standards: 8.G.B.7, 8.G.B.8','/math/geometry-home/geometry-pythagorean-theorem/pyth-theor/v/the-pythagorean-theorem','2016-09-12 22:43:09','2016-09-12 22:43:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1154,5,42,'Pythagorean theorem proofs','pythagorean-proofs',NULL,'The Pythagorean theorem is one of the most famous ideas in all of mathematics.  This tutorial proves it.  Then proves it again... and again... and again.  More than just satisfying any skepticism of whether the Pythagorean theorem is really true (only one proof would be sufficient for that), it will hopefully open your mind to new and beautiful ways to prove something very powerful.\n\nCommon Core Standard: 8.G.B.6','/math/geometry-home/geometry-pythagorean-theorem/pythagorean-proofs/v/garfield-s-proof-of-the-pythagorean-theorem','2016-09-12 22:43:09','2016-09-12 22:43:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1155,5,42,'Pursuit of a Pythagorean proof','pursuit-of-a-pythagorean-proof',NULL,'An exploration which allows the reader to choose his or her own path to a proof of the Pythagorean theorem.?','/math/geometry-home/geometry-pythagorean-theorem/pursuit-of-a-pythagorean-proof/a/pursuit-of-pythagorean-proof-start','2016-09-12 22:43:09','2016-09-12 22:43:09',1);
