insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1157,5,43,'Translations','geo-translations',NULL,'Learn how to find the translation that maps a given figure to another, and how to manually draw the image of a translation.','/math/geometry-home/transformations/geo-translations/v/drawing-image-of-translation','2016-09-12 22:43:16','2016-09-12 22:43:16',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1158,5,43,'Rotations','geo-rotations',NULL,'Learn how to find the rotation that maps a given figure to another, and how to manually draw the image of a rotation.','/math/geometry-home/transformations/geo-rotations/v/defining-rotation-example','2016-09-12 22:43:16','2016-09-12 22:43:16',1);
