insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1239,6,55,'Solving advanced sinusoidal equations','advanced-sinusoidal-equations',NULL,'Learn how to solve equations of the form a*sin(bx+c)=d or a*cos(bx+c)=d where a, b, c, and d are any number. For example, solve 5*sin(2x-1)=3.','/math/trigonometry/trig-equations-and-identities/advanced-sinusoidal-equations/e/solve-advanced-sinusoidal-equations','2016-09-12 22:44:36','2016-09-12 22:44:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1240,6,55,'Solving sinusoidal models','solving-sinusoidal-models',NULL,'Solve word problems that involve sinusoidal modeling functions.','/math/trigonometry/trig-equations-and-identities/solving-sinusoidal-models/v/inverse-trig-with-model','2016-09-12 22:44:36','2016-09-12 22:44:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1241,6,55,'Introduction to the trigonometric angle addition identities','intro-to-trig-angle-addition-identities',NULL,'Learn about the trigonometric angle addition identities, which help us discuss the trig values of sums of angles in terms of the trig values of the individual angles. For example, we can express sin(x+y) in terms of sin(x), sin(y), cos(x), and cos(y).','/math/trigonometry/trig-equations-and-identities/intro-to-trig-angle-addition-identities/v/trigonometry-identity-review-fun','2016-09-12 22:44:36','2016-09-12 22:44:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1242,6,55,'Using trigonometric identities to solve problems','using-trig-identities',NULL,'Learn about different ways in which trigonometric identities, such as the Pythagorean identity and the angle addition identities, can be used to solve problems.','/math/trigonometry/trig-equations-and-identities/using-trig-identities/v/sine-angle-addition-2','2016-09-12 22:44:36','2016-09-12 22:44:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1243,6,55,'Challenging trigonometry problems','challenge-trig-problems',NULL,'See Sal solve a bunch of very challenging geometry and algebra problems that involve trigonometry.','/math/trigonometry/trig-equations-and-identities/challenge-trig-problems/v/2003-aime-ii-problem-11-avi','2016-09-12 22:44:36','2016-09-12 22:44:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1244,7,56,'Statistics overview','statistics-overview',NULL,'Statistics gives us the tools to effectively answer questions using data. So the first step in actually doing statistics is to form a question! This tutorial looks at what type of questions we deal with in statistics.','/math/statistics-probability/displaying-describing-data/statistics-overview/v/understanding-statistical-questions','2016-09-12 22:44:43','2016-09-12 22:44:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1245,7,56,'Categorical data displays','categorical-data-displays',NULL,'Categorical data comes from classifying information into categories. To understand categorical data, we often display it in bar graphs and pie graphs.','/math/statistics-probability/displaying-describing-data/categorical-data-displays/v/reading-pictographs','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1246,7,56,'Two-way tables for categorical data','two-way-tables-for-categorical-data',NULL,'Two-way tables show how individuals are classified in terms of two categorical variables. We use two-way tables to see if there is an association between two variables.','/math/statistics-probability/displaying-describing-data/two-way-tables-for-categorical-data/v/two-way-frequency-tables-and-venn-diagrams','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1247,7,56,'Dot plots and frequency tables','dot-plots-frequency-tables',NULL,'Dot plots and frequency tables both display how often different values occur in a set of numerical data.','/math/statistics-probability/displaying-describing-data/dot-plots-frequency-tables/v/ways-to-represent-data','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1248,7,56,'Histograms','histogram',NULL,'Histograms display how many numerical data points there are in each \"bucket\" of values.','/math/statistics-probability/displaying-describing-data/histogram/v/histograms-intro','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1249,7,56,'Comparing features of distributions','comparing-features-distributions',NULL,'Some features we look at in a distribution are its overall shape, unusual points called outliers, clusters of data, and gaps where there is no data at all.','/math/statistics-probability/displaying-describing-data/comparing-features-distributions/v/shapes-of-distributions','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1250,7,56,'Stem-and-leaf plots','stem-leaf-plots',NULL,'Stem-and-leaf plots are another way to plot numerical data. They combine the best features of dot plots and histograms. Turn your head sideways and look at a stem-and-leaf plot and you\'ll see what we mean.','/math/statistics-probability/displaying-describing-data/stem-leaf-plots/v/u08-l1-t2-we3-stem-and-leaf-plots','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1251,7,56,'Line graphs','line-graphs',NULL,'Line graphs show change over time in a numerical variable.','/math/statistics-probability/displaying-describing-data/line-graphs/v/u08-l1-t2-we2-reading-line-graphs','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1252,7,56,'Mean and median: The basics','mean-median-basics',NULL,'Mean and median are the most commonly used tools to measure the center of a distribution. They give us an idea of the typical value in a set of numerical data.','/math/statistics-probability/displaying-describing-data/mean-median-basics/v/statistics-the-average','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1253,7,56,'More on mean and median','more-mean-median',NULL,'Once you know the basics of how to calculate the mean and median, you can start to think deeper about more advanced concepts. This tutorial covers how to find the mean and median from a data display, how new data points impact the mean and median, and how to find missing values given a mean.','/math/statistics-probability/displaying-describing-data/more-mean-median/e/calculating-the-mean-from-various-data-displays','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1254,7,56,'Range, Interquartile range (IQR), Mean absolute deviation (MAD)','range-iqr-mad',NULL,'The most basic ways to measure spread in a distribution of numerical data are with range, interquartile range, and mean absolute deviation.','/math/statistics-probability/displaying-describing-data/range-iqr-mad/v/range-and-mid-range','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1255,7,56,'Box and whisker plots','box-whisker-plots',NULL,'Box and whisker plots show the median, quartiles, and more information about a set of numerical data.','/math/statistics-probability/displaying-describing-data/box-whisker-plots/v/box-and-whisker-plot-exercise-example','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1256,7,56,'Population variance and standard deviation','pop-variance-standard-deviation',NULL,'Standard deviation tells us how far away from the mean each data point typically is in a set of numbers. How we calculate standard deviation depends on whether the data represents an entire population or came from a sample. This tutorial focuses on the data that represents an entire population.','/math/statistics-probability/displaying-describing-data/pop-variance-standard-deviation/v/range-variance-and-standard-deviation-as-measures-of-dispersion','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1257,7,56,'Sample variance and standard deviation','sample-standard-deviation',NULL,'Standard deviation tells us how far away from the mean each data point typically is in a set of numbers. How we calculate standard deviation depends on whether the data represents an entire population or came from a sample. This tutorial focuses on how we deal with standard deviation coming from sample data.','/math/statistics-probability/displaying-describing-data/sample-standard-deviation/v/sample-variance','2016-09-12 22:44:44','2016-09-12 22:44:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1258,7,57,'Describing location in a distribution','describing-location-in-a-distribution',NULL,'A z-score measures a data point\'s location by showing exactly how many standard deviations away from the mean the data point is. ','/math/statistics-probability/modeling-distributions-of-data/describing-location-in-a-distribution/v/ck12-org-normal-distribution-problems-z-score','2016-09-12 22:44:51','2016-09-12 22:44:51',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1259,7,57,'Normal distributions','normal-distributions-library',NULL,'Normal distributions are the most fundamental family of distributions in all of statistics. You\'ll learn how to calculate area in a normal curve to find what percent of data falls where in the distribution. You\'ll also have the opportunity to go deeper with simulations that show how normal distributions really work.','/math/statistics-probability/modeling-distributions-of-data/normal-distributions-library/v/ck12-org-normal-distribution-problems-qualitative-sense-of-normal-distributions','2016-09-12 22:44:51','2016-09-12 22:44:51',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1260,7,58,'Scatterplots and correlation','scatterplots-and-correlation',NULL,'Constructing and interpreting scatterplots is the focus of this tutorial. You\'ll learn how to make a good scatterplot, and then how to describe what you see using positive and negative correlation and outliers.','/math/statistics-probability/describing-relationships-quantitative-data/scatterplots-and-correlation/v/constructing-scatter-plot','2016-09-12 22:44:57','2016-09-12 22:44:57',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1261,7,58,'Regression','regression-library',NULL,'Regression is fitting a line or curve to a pattern we see in a scatterplot. This tutorial focuses on linear regression, where you\'ll learn how to fit a line to data, describe features of that line, and use the line to make predictions.','/math/statistics-probability/describing-relationships-quantitative-data/regression-library/v/fitting-a-line-to-data','2016-09-12 22:44:57','2016-09-12 22:44:57',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1262,7,58,'Residuals, least-squares regression, and r-squared','residuals-least-squares-rsquared',NULL,'Residuals measure how far away each data point is from a line that has been fit to the data in a scatterplot. A least-squares regression line tries to fit the data the best it can by making these residuals as small as possible, and we can measure how well a line fits using r-squared. This tutorial explores a few of the more advanced topics in linear regression.','/math/statistics-probability/describing-relationships-quantitative-data/residuals-least-squares-rsquared/a/introduction-to-residuals','2016-09-12 22:44:58','2016-09-12 22:44:58',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1263,7,59,'Sampling and surveys','sampling-and-surveys',NULL,'Taking a sample and giving a survey is one of the most common ways to get information about a large population. Doing every step well is important though, otherwise your claims may be not accurate. This tutorial covers some of the basics of samples and surveys.','/math/statistics-probability/designing-studies/sampling-and-surveys/v/reasonable-samples','2016-09-12 22:45:06','2016-09-12 22:45:06',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1264,7,59,'Experiments','experiments-stats-library',NULL,'An experiment assigns some subjects to receive a treatment while others don\'t get the treatment. Experiments also try to control for other variables so we can see if there\'s a cause-effect relationship between the treatment and some response. Learn more about the differences between observational studies and experiments in this tutorial.','/math/statistics-probability/designing-studies/experiments-stats-library/v/correlation-and-causality','2016-09-12 22:45:06','2016-09-12 22:45:06',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1265,7,60,'Basic theoretical probability','basic-theoretical-probability',NULL,'Basic theoretical probability is mapping out how many outcomes are possible and seeing how many of those outcomes relate to the probability we\'re trying to find. ','/math/statistics-probability/probability-library/basic-theoretical-probability/v/basic-probability','2016-09-12 22:45:14','2016-09-12 22:45:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1266,7,60,'Probability using sample spaces','probability-sample-spaces',NULL,'A sample space shows every possible equally likely outcome in some chance process like flipping a coin or rolling a pair of dice. Sample spaces can help us find the probability of certain events like rolling doubles or getting heads twice in three flips.','/math/statistics-probability/probability-library/probability-sample-spaces/v/events-and-outcomes-3','2016-09-12 22:45:14','2016-09-12 22:45:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1267,7,60,'Experimental probability','experimental-probability-lib',NULL,'Experimental probability estimates the theoretical probability of an event by repeating trials over and over and keeping track of what percent of the trials the event actually happens. For example, spin a coin over and over and see if it lands on \"heads\" 50% of the time (you may be surprised).','/math/statistics-probability/probability-library/experimental-probability-lib/v/experimental-probability','2016-09-12 22:45:14','2016-09-12 22:45:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1268,7,60,'Basic set operations','basic-set-ops',NULL,'Basic set operations and set notation are the language of more advanced probability and logic. Learn how to express intersections, unions, complements, and subsets symbolically.','/math/statistics-probability/probability-library/basic-set-ops/v/intersection-and-union-of-sets','2016-09-12 22:45:14','2016-09-12 22:45:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1269,7,60,'Addition rule for probability','addition-rule-lib',NULL,'The addition rule says that we can find the probably of one event or another event happening by adding their probabilities, but we have to be careful and subtract the overlapping probability of both events happening at the same time.','/math/statistics-probability/probability-library/addition-rule-lib/v/probability-with-playing-cards-and-venn-diagrams','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1270,7,60,'Multiplication rule for independent events','multiplication-rule-independent',NULL,'The multiplication rule says that we can multiply the probability of independent events to find the probably of all of the events happening. You\'ll learn how to find the probability of getting three heads in a row when tossing a coin, or a basketball player making five shots in a row.','/math/statistics-probability/probability-library/multiplication-rule-independent/v/compound-sample-spaces','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1271,7,60,'Multiplication rule for dependent events','multiplication-rule-dependent',NULL,'The multiplication rule works for dependent events, we just have to adjust each probability based on the previous event occurring. You\'ll learn how to find the probability of events like drawing two red cards in a row from a deck of cards.','/math/statistics-probability/probability-library/multiplication-rule-dependent/v/introduction-to-dependent-probability','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1272,7,60,'Conditional probability and independence','conditional-probability-independence',NULL,'Conditional probability is the likelihood of some event occurring given some other event has occurred. You\'ll learn how to calculate conditional probability intuitively and with a formula called Bayes\' theorem.','/math/statistics-probability/probability-library/conditional-probability-independence/v/calculating-conditional-probability','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1273,7,60,'Counting principle and factorial','counting-principle-factorial',NULL,'The counting principal tells us that we can multiply the number of choices together to get a count of the total possible outcomes. If you own two pairs of pants and three shirts, then you have six possible outfits.','/math/statistics-probability/probability-library/counting-principle-factorial/v/tree-diagram-to-count-outcomes','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1274,7,60,'Permutations','permutation-lib',NULL,'Permutations count how many ways you can select and arrange items or people from a larger group. For example, how many different ways can we award a gold, silver, and bronze medal to three runners out of a group of 10?','/math/statistics-probability/probability-library/permutation-lib/v/permutation-formula','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1275,7,60,'Combinations','combinations-lib',NULL,'Combinations are similar to permutations. Combinations count how many ways we can pick a group of people or things from a larger group where order doesn\'t matter. For example, how many different groups of 3 people can a company with 10 employees send on a business trip?','/math/statistics-probability/probability-library/combinations-lib/v/introduction-to-combinations','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1276,7,60,'Combinatorics and probability','combinatorics-probability',NULL,'Once we understand the counting principle, permutations, and combinations, we can use those concepts to calculate some pretty advanced probabilities!','/math/statistics-probability/probability-library/combinatorics-probability/v/probability-using-combinations','2016-09-12 22:45:15','2016-09-12 22:45:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1277,7,61,'Discrete and continuous random variables and probability models','discrete-and-continuous-random-variables',NULL,'Discrete random variables occur in situations where we can list every possible outcome of a process, and assign each outcome a probability in a probability model. For example, how many heads will we get in three flips?','/math/statistics-probability/random-variables-stats-library/discrete-and-continuous-random-variables/v/random-variables','2016-09-12 22:45:22','2016-09-12 22:45:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1278,7,61,'Expected value','expected-value-lib',NULL,'Expected value gives us the mean or long-term average number of success we can expect over time if we repeat a process over and over. For example, how much money can someone expect to win on average for each lottery ticket they buy?','/math/statistics-probability/random-variables-stats-library/expected-value-lib/v/term-life-insurance-and-death-probability','2016-09-12 22:45:22','2016-09-12 22:45:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1279,7,61,'Transforming and combining random variables','transforming-and-combining-random-variables',NULL,'Transforming and combining random variables produces predictable changes in the expected value and variance of the outcomes.','/math/statistics-probability/random-variables-stats-library/transforming-and-combining-random-variables/v/variance-of-differences-of-random-variables','2016-09-12 22:45:22','2016-09-12 22:45:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1280,7,61,'Binomial random variables','binomial-random-variables',NULL,'Binomial random variables are a special type of variable that comes up we repeat a process for a set number of independent trials, with each trial having the same probability of success, and we count how many successes we get at the end of the trials. For example, how many shots will a basketball player make in a series of 10 shots?','/math/statistics-probability/random-variables-stats-library/binomial-random-variables/v/binomial-distribution','2016-09-12 22:45:22','2016-09-12 22:45:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1281,7,61,'Poisson distribution','poisson-distribution',NULL,'The Poisson distribution is related to the binomial setting, but looks at the number of successes over time instead of over many repeated trials.','/math/statistics-probability/random-variables-stats-library/poisson-distribution/v/poisson-process-1','2016-09-12 22:45:22','2016-09-12 22:45:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1282,7,62,'Sample proportions','sample-proportions',NULL,'Learn how to find the mean and variance of the sampling distribution of a sample proportion. ','/math/statistics-probability/sampling-distributions-library/sample-proportions/v/mean-and-variance-of-bernoulli-distribution-example','2016-09-12 22:45:29','2016-09-12 22:45:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1283,7,62,'Sample means','sample-means',NULL,'Learn how to find the mean and standard deviation of the sampling distribution of a sample mean. Also, learn about the central limit theorem and why it is one of the most important theorems in statistics.','/math/statistics-probability/sampling-distributions-library/sample-means/v/central-limit-theorem','2016-09-12 22:45:29','2016-09-12 22:45:29',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1284,7,63,'Estimating a population proportion','estimating-population-proportion',NULL,'A confidence interval for a proportion estimates what percent of a population fits some category based on a results from a sample. For example, if 70 adults own a cell phone in a random sample of 100 adults, we can be fairly confident that the true percentage in the population is somewhere between 61% and 79%. Go through this tutorial to learn how to calculate confidence intervals like this.','/math/statistics-probability/confidence-intervals-one-sample/estimating-population-proportion/v/confidence-interval-example','2016-09-12 22:45:35','2016-09-12 22:45:35',1);
