insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1291,7,66,'Chi-square goodness-of-fit tests','chi-square-goodness-of-fit-tests',NULL,'A chi-square goodness-of-fit test examines how well a sample of categorical data fits a hypothesized distribution. For example, take a sample of people and ask them what day of the week they were born. You probably won\'t get the same number of people born on each day, but does that offer strong evidence that the distribution isn\'t even for everyone?','/math/statistics-probability/inference-categorical-data-chi-square-tests/chi-square-goodness-of-fit-tests/v/chi-square-distribution-introduction','2016-09-12 22:45:54','2016-09-12 22:45:54',1);
