insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1295,8,69,'Limits introduction','limits-introduction-calc',NULL,'A limit is the value that a function or sequence \"approaches\" as the input or index approaches some value. In this tutorial, we supply an intuitive understanding of limits.','/math/calculus-home/limits-and-continuity-calc/limits-introduction-calc/a/ap-calculus-ab-prerequisites','2016-09-12 22:46:16','2016-09-12 22:46:16',1);
