insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1299,8,69,'Formal definition of limits (epsilon-delta)','formal-definition-of-limits-calc',NULL,'Now that you have an intuitive understanding of limits, let\'s do what mathematicians do best and define them rigorously! This definition may be hard to grasp at first, but its beauty will get you in the end.','/math/calculus-home/limits-and-continuity-calc/formal-definition-of-limits-calc/v/limit-intuition-review','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
