insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1301,8,69,'Continuity at a point','continuity-at-a-point-calc',NULL,'A function is continuous at a point if its limit at that point exists and is equal to the actual function\'s value at that point.','/math/calculus-home/limits-and-continuity-calc/continuity-at-a-point-calc/v/limits-to-define-continuity','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1302,8,69,'Limits of combined and composite functions','limits-of-combined-and-composite-functions-calc',NULL,'Practice your basic understanding of limits and continuity with more complex cases.','/math/calculus-home/limits-and-continuity-calc/limits-of-combined-and-composite-functions-calc/v/limit-properties','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1303,8,69,'Continuous functions','continuous-functions-calc',NULL,'Continuous functions are continuous at all of the points in their domains. In essence, these are functions whose graphs can be drawn with a single brush stroke.','/math/calculus-home/limits-and-continuity-calc/continuous-functions-calc/v/functions-continuous-on-all-numbers','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1304,8,69,'Intermediate value theorem','intermediate-value-theorem-calc',NULL,'The intermediate value theorem states that if a continuous function, f, with an interval, [a, b], as its domain, takes values f(a) and f(b) at each end of the interval, then it also takes any value between f(a) and f(b) at some point within the interval. This is a basic but important property of all continuous functions.','/math/calculus-home/limits-and-continuity-calc/intermediate-value-theorem-calc/v/intermediate-value-theorem','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1305,8,69,'Review: Continuity','continuity-review-calc',NULL,'Review your understanding of continuity with some challenge problems.','/math/calculus-home/limits-and-continuity-calc/continuity-review-calc/e/continuity-challenge','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1306,8,69,'Limits from equations (direct substitution)','limits-from-equations-calc',NULL,'Take your first steps in finding limits algebraically. For example, find the limit of x²+5x at x=2, or determine whether the limit of 2x/(x+1) at x=-1 exists.','/math/calculus-home/limits-and-continuity-calc/limits-from-equations-calc/v/limit-by-substitution','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1307,8,69,'Limits from equations (factoring & rationalizing)','limits-with-factoring-and-rationalizing-calc',NULL,'There are some limits that want us to work a little before we find them. Learn about two main methods of dealing with such limits: factorization and rationalization. For example, find the limit of (x²-1)/(x-1) at x=1.','/math/calculus-home/limits-and-continuity-calc/limits-with-factoring-and-rationalizing-calc/v/limit-example-1','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1308,8,69,'Squeeze theorem','squeeze-theorem-calc',NULL,'The Squeeze theorem (or Sandwich theorem) states that for any three functions f, g, and h, if f(x)?g(x)?h(x) for all x-values on an interval except for a single value x=a, and the limits of f and h at x=a are equal to L, then the limit of g at x=a must be equal to L as well. This may seem simple but it\'s pure genius. Learn how it helps us find tricky limits like sin(x)/x at x=0.','/math/calculus-home/limits-and-continuity-calc/squeeze-theorem-calc/v/squeeze-sandwich-theorem','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1309,8,69,'Limits of trigonometric functions','limits-of-trigonometric-functions-calc',NULL,'Find limits of trigonometric functions by manipulating the functions (using trigonometric identities) into expressions that are nicer to handle. For example, find the limit of sin(x)/sin(2x) at x=0.','/math/calculus-home/limits-and-continuity-calc/limits-of-trigonometric-functions-calc/v/trig-limit-using-pythagorean-identity','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1310,8,69,'Limits of piecewise functions','limits-of-piecewise-functions-calc',NULL,'Remember one-sided limits? Well, these are very useful when dealing with piecewise functions. For example, analyze the limit at x=2 of the function that gives (x-2)² for values lower than 2 and 2-x² for values lager than 2.','/math/calculus-home/limits-and-continuity-calc/limits-of-piecewise-functions-calc/v/limit-of-piecewise-function-that-is-defined','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1311,8,69,'Removable discontinuities','removable-discontinuities-calc',NULL,'Removable discontinuities are points where a function isn\'t continuous but can become continuous with a small adjustment. Analyze such points and determine what adjustments should be made to \"remove\" them.','/math/calculus-home/limits-and-continuity-calc/removable-discontinuities-calc/v/defining-a-function-at-a-point-to-make-it-continuous','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1312,8,69,'Review: Limits from equations','limits-from-equations-review-calc',NULL,'Review your limit-evaluation skills with some challenge problems.','/math/calculus-home/limits-and-continuity-calc/limits-from-equations-review-calc/e/limits-from-equations-challenge','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1313,8,69,'Unbounded limits (vertical asymptotes)','unbounded-limits-calc',NULL,'Some limits don\'t approach a specific value, but instead become boundlessly large as they approach the limiting value. For example, the limit of 1/x as we approach x=0 from the right. Learn about this type of limits and how it relates to vertical asymptotes.','/math/calculus-home/limits-and-continuity-calc/unbounded-limits-calc/v/limits-and-infinity','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1314,8,69,'Limits at infinity (horizontal asymptotes)','limits-at-infinity-calc',NULL,'For some expressions, as we increase x infinitely, approach a finite value. For example, 1/x approaches 0 as x becomes infinitely large. Learn about these types of limits, and how they relate to horizontal asymptotes.','/math/calculus-home/limits-and-continuity-calc/limits-at-infinity-calc/v/limits-and-infinity','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1315,8,69,'Review: Infinite limits','infinite-limits-review-calc',NULL,'Review your understanding of infinite limits with some challenge problems.','/math/calculus-home/limits-and-continuity-calc/infinite-limits-review-calc/e/infinite-limits-challenge','2016-09-12 22:46:17','2016-09-12 22:46:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1316,8,70,'Introduction to differential calculus','intro-to-diff-calculus-calc',NULL,'What\'s differential calculus all about? An answer to this question lies just right here.','/math/calculus-home/taking-derivatives-calc/intro-to-diff-calculus-calc/v/newton-leibniz-and-usain-bolt','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1317,8,70,'Derivative as slope of tangent line','derivative-as-tangent-slope-calc',NULL,'One way of thinking about the derivative (of a function at a point) is as the slope of the tangent line (to the function\'s graph at that point). Get comfortable with this approach here.','/math/calculus-home/taking-derivatives-calc/derivative-as-tangent-slope-calc/v/derivative-as-slope-of-tangent-line','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1318,8,70,'Derivative as instantaneous rate of change','derivative-as-instantaneous-rate-of-change-calc',NULL,'One way of thinking about the derivative is as instantaneous rate of change. This is quite incredible because rate of change is usually found over a period of time, and not at an instant. Get comfortable with this approach here.','/math/calculus-home/taking-derivatives-calc/derivative-as-instantaneous-rate-of-change-calc/v/slopes-of-secant-lines','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1319,8,70,'Secant lines','secant-lines-calc',NULL,'Gain some experience working with secant lines. This will help us on our journey to find a formal definition for the derivative.','/math/calculus-home/taking-derivatives-calc/secant-lines-calc/v/slope-of-a-line-secant-to-a-curve','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1320,8,70,'Derivative as a limit','derivative-as-a-limit-calc',NULL,'The derivative is actually a special kind of limit! This is yet another important stop on our continuing journey towards a formal definition of the derivative.','/math/calculus-home/taking-derivatives-calc/derivative-as-a-limit-calc/v/calculus-derivatives-1-new-hd-version','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1321,8,70,'Formal definition of derivative','formal-definition-of-derivative-calc',NULL,'There are two ways to define the derivative of function f at point x=a. The formal definition is the limit of [f(a+h)-f(x)]/h as h approaches 0, and the alternative definition is the limit of [f(x)-f(a)]/(x-a) as x approaches a. Make introduction with these two definitions.','/math/calculus-home/taking-derivatives-calc/formal-definition-of-derivative-calc/v/alternate-form-of-the-derivative','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1322,8,70,'Using the formal definition of derivative','using-the-formal-definition-of-derivative-calc',NULL,'Learn how we can use their formal definition in order to find the derivatives of specific functions. For example, we find the derivative of f(x)=x² at x=3, or for any x-value.','/math/calculus-home/taking-derivatives-calc/using-the-formal-definition-of-derivative-calc/v/calculus-derivatives-2-new-hd-version','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1323,8,70,'Differentiability','differentiability-calc',NULL,'The derivative of a function isn\'t necessarily defined at every point. Learn about the conditions for the derivative to exist, and specifically about how continuity fits with this story (spoiler: for a function to be differentiable at a point it must be continuous at that point, but the other way isn\'t necessary).','/math/calculus-home/taking-derivatives-calc/differentiability-calc/v/differentiability-at-a-point-graphical','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1324,8,70,'Derivative as a function','derivative-as-a-function-calc',NULL,'This may blow your mind, but the derivative of a function is a function in itself! Get comfortable in thinking about the derivative as a function that is separate from, but tightly related to, its original function.','/math/calculus-home/taking-derivatives-calc/derivative-as-a-function-calc/v/intuitively-drawing-the-derivative-of-a-function','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1325,8,70,'Review: Derivative basics','derivative-basics-review-calc',NULL,'Review your conceptual understanding of derivatives with some challenge problems.','/math/calculus-home/taking-derivatives-calc/derivative-basics-review-calc/e/derivatives-basics-challenge','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1326,8,70,'Basic differentiation rules','basic-differentiation-rules-calc',NULL,'If you ever tried to find derivatives using their formal definition, you probably know how tedious that may be. Fortunately, we have ways for finding derivatives much quicker, using differentiation rules! Make your first steps in this fascinating world by working with the more basic rules. For example, the derivative of [f(x)+g(x)] is f\'(x)+g\'(x), and the derivative of k?f(x) is k?f\'(x).','/math/calculus-home/taking-derivatives-calc/basic-differentiation-rules-calc/v/constant-derivative-rule','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1327,8,70,'Power rule','power-rule-calc',NULL,'The power rule says that the derivative of x? is n?x??¹. It allows us to quickly find the derivative of any polynomial, and it doesn\'t even stop there! Make introduction with this simple but powerful rule.','/math/calculus-home/taking-derivatives-calc/power-rule-calc/v/power-rule','2016-09-12 22:46:27','2016-09-12 22:46:27',1);
