insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1334,8,70,'Product rule','product-rule-calc',NULL,'The product rule says that the derivative of the product f(x)g(x) is f\'(x)g(x)+f(x)g\'(x). This helps us find the derivative of a function which is a product of two other, more basic, functions.','/math/calculus-home/taking-derivatives-calc/product-rule-calc/v/applying-the-product-rule-for-derivatives','2016-09-12 22:46:28','2016-09-12 22:46:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1335,8,70,'Quotient rule','quotient-rule-calc',NULL,'The quotient rule says that the derivative of the quotient f(x)/g(x) is [f\'(x)g(x)-f(x)g\'(x)]/g²(x). This helps us find the derivative of a function which is a quotient of two other, more basic, functions.','/math/calculus-home/taking-derivatives-calc/quotient-rule-calc/e/differentiate-quotients','2016-09-12 22:46:28','2016-09-12 22:46:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1336,8,70,'Chain rule','chain-rule-calc',NULL,'The chain rule says that the derivative of the composite function f(g(x)) is f\'(g(x))?g\'(x). This helps us find the derivative of a composite function. It may be slightly hard to grasp, but its importance cannot be overstated!','/math/calculus-home/taking-derivatives-calc/chain-rule-calc/v/chain-rule-introduction','2016-09-12 22:46:28','2016-09-12 22:46:28',1);
