insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1365,8,71,'Linear approximation','linear-approximation-calc',NULL,'The method of linear approximation (also called local linearization) allows us to approximate a function at hairy x-values using the line tangent to the function\'s graph at strategic points.','/math/calculus-home/derivative-applications-calc/linear-approximation-calc/v/local-linearization-intro','2016-09-12 22:46:36','2016-09-12 22:46:36',1);
