insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1372,8,71,'L\'Hôpital\'s rule','lhopitals-rule-calc',NULL,'L\'Hôpital\'s rule provides us with an easy, almost magical way of finding indeterminate limits of quotients of functions using the functions\' derivatives. In short, the rule says that if the limits of functions f and g at x=a are 0 (or ?) and the limit of f\'(x)/g\'(x) at x=a is equal to L, then the limit of f(x)/g(x) at x=a is also equal to L.','/math/calculus-home/derivative-applications-calc/lhopitals-rule-calc/v/introduction-to-l-hopital-s-rule','2016-09-12 22:46:37','2016-09-12 22:46:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1373,8,71,'Review: Derivative applications','derivative-applications-review-calc',NULL,'Review your understanding of the various applications of differential calculus with some challenge problems.','/math/calculus-home/derivative-applications-calc/derivative-applications-review-calc/e/derivative-applications-challenge','2016-09-12 22:46:37','2016-09-12 22:46:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1374,8,72,'Antiderivatives','antiderivatives-calc',NULL,'If f\' is the derivative of f, then f is the antiderivative of f\'. To find the antiderivative of a function we need to perform some kind of reverse differentiation. Learn about it here.','/math/calculus-home/integration-calc/antiderivatives-calc/v/antiderivatives-and-indefinite-integrals','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1375,8,72,'Indefinite integrals intro','indefinite-integrals-intro-calc',NULL,'Indefinite integrals are the way integral calculus deals with antiderivatives. Gain some practice in finding various indefinite integrals.','/math/calculus-home/integration-calc/indefinite-integrals-intro-calc/v/indefinite-integrals-of-x-raised-to-a-power','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1376,8,72,'Indefinite integrals of common functions','indefinite-integrals-of-common-functions-calc',NULL,'Indefinite integrals (or antiderivatives) are really just backward differentiation. Therefore, the indefinite integral of e? is e?+c, the indefinite integral of 1/x is ln(x)+c, the indefinite integral of sin(x) is -cos(x)+c, and the indefinite integral of cos(x) is sin(x)+c.','/math/calculus-home/integration-calc/indefinite-integrals-of-common-functions-calc/v/basic-trig-and-exponential-antiderivatives','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1377,8,72,'Review: Indefinite integrals & antiderivatives','indefinite-integrals-and-antiderivatives-review-calc',NULL,'Review your understanding of indefinite integrals and antiderivatives with some challenge problems.','/math/calculus-home/integration-calc/indefinite-integrals-and-antiderivatives-review-calc/e/graphs-of-antiderivatives','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1378,8,72,'Definite integral as area','definite-integral-as-area-calc',NULL,'Introducing the definite integral. It\'s basically a way to represent the area under a given curve with left-hand and right-hand bounds.','/math/calculus-home/integration-calc/definite-integral-as-area-calc/a/understanding-the-fundamental-theorem-of-calculus','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1379,8,72,'Definite integral properties','definite-integral-properties-calc',NULL,'Definite integrals behave is specific ways with shared properties. For example, the sum of the definite integrals of a function f from a to b and from b to c is equal to the definite integral of f from a to c. Learn about these properties and use them in order to evaluate integrals.','/math/calculus-home/integration-calc/definite-integral-properties-calc/v/integrating-scaled-function','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1380,8,72,'Review: Definite integral basics','definite-integral-basics-review-calc',NULL,'Review your understanding of the basics of definite integrals with some challenge problems.','/math/calculus-home/integration-calc/definite-integral-basics-review-calc/e/definite-integral-basics-challenge','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1381,8,72,'Riemann sums','rieman-sums-calc',NULL,'Riemann sums is the name of a family of methods we can use to approximate the area under a curve. Learn about the different ways and how they are constructed.','/math/calculus-home/integration-calc/rieman-sums-calc/v/simple-riemann-approximation-using-rectangles','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1382,8,72,'Riemann sums with sigma notation','riemann-sums-with-sigma-notation-calc',NULL,'The larger the number of terms in a Riemann sum, the more accurate it is. But as the number of terms increases, we need better ways to write them down. This is where sigma notation comes in very handy.','/math/calculus-home/integration-calc/riemann-sums-with-sigma-notation-calc/v/practice-approximating-area-under-curve','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1383,8,72,'Trapezoidal rule','trapezoidal-rule-calc',NULL,'The trapezoidal rule is yet another way to approximate the area under a curve. Learn about they way it\'s constructed and how accurate it is comparing the rectangular Riemann sums.','/math/calculus-home/integration-calc/trapezoidal-rule-calc/v/rectangular-and-trapezoidal-riemann-approximations','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1384,8,72,'Definite integral as the limit of a Riemann sum','definite-integral-as-the-limit-of-a-riemann-sum-calc',NULL,'The definite integral of a function is the limit of a sequence of Riemann sums where the intervals become infinitely small. Learn more about this formal definition.','/math/calculus-home/integration-calc/definite-integral-as-the-limit-of-a-riemann-sum-calc/v/riemann-sums-and-integrals','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1385,8,72,'Review: Riemann sums','riemann-sums-review-calc',NULL,'Review your understanding of Riemann sums with some challenge problems.','/math/calculus-home/integration-calc/riemann-sums-review-calc/e/riemann-sums-challenge','2016-09-12 22:46:44','2016-09-12 22:46:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1386,8,72,'Functions defined by integrals','functions-defined-by-integrals-calc',NULL,'Get comfortable with functions that for any input x, return the area under a curve between a given bound and x. This is an important stop on our journey to relating differential and integral calculus.','/math/calculus-home/integration-calc/functions-defined-by-integrals-calc/v/evaluating-function-defined-by-integral','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1387,8,72,'Fundamental theorem of calculus','fundamental-theorem-of-calculus-calc',NULL,'The fundamental theorem of calculus is definitely one of the most important theorems of all time! It relates differential calculus and the concept of the derivative with integral calculus and the concept of the integral. Learn all about it here!','/math/calculus-home/integration-calc/fundamental-theorem-of-calculus-calc/v/fundamental-theorem-of-calculus','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1388,8,72,'Fundamental theorem of calculus: chain rule','fundamental-theorem-of-calculus-chain-rule-calc',NULL,'Get better acquainted with the fundamental theorem of calculus, and apply it for a class of seemingly hairy problems.','/math/calculus-home/integration-calc/fundamental-theorem-of-calculus-chain-rule-calc/v/applying-the-fundamental-theorem-of-calculus','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1389,8,72,'Definite integral evaluation','definite-integral-evaluation-calc',NULL,'The fundamental theorem tells us how we can evaluate definite integrals. Now let us  put this to use and evaluate some!','/math/calculus-home/integration-calc/definite-integral-evaluation-calc/v/connecting-the-first-and-second-fundamental-theorems-of-calculus','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1390,8,72,'Definite integrals of piecewise functions','definite-integrals-of-piecewise-functions-calc',NULL,'Evaluate definite integrals of piecewise functions. This is where the definite integral properties come into good use.','/math/calculus-home/integration-calc/definite-integrals-of-piecewise-functions-calc/v/definite-integrals-of-piecewise-functions','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1391,8,72,'Improper integrals','improper-integrals-calc',NULL,'Improper integrals are like definite integrals, only unbounded from the left and/or right. And yes, some of them have finite values. An unbounded area with a finite size? That\'s calculus to you!','/math/calculus-home/integration-calc/improper-integrals-calc/v/introduction-to-improper-integrals','2016-09-12 22:46:45','2016-09-12 22:46:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1392,8,73,'Integration by parts','integration-by-parts-calc',NULL,'Learn how to use the product rule in order to find the integral of a product of functions (sadly this is more complicated than using the product rule the regular way).','/math/calculus-home/integration-techniques-calc/integration-by-parts-calc/v/deriving-integration-by-parts-formula','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1393,8,73,'Partial fraction expansion','partial-fraction-expansion-calc',NULL,'Learn a useful algebraic tool to find the integrals of some rational functions.','/math/calculus-home/integration-techniques-calc/partial-fraction-expansion-calc/v/partial-fraction-expansion-to-integrate','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1394,8,73,'u-substitution','u-substitution-calc',NULL,'u-substitution is an extremely useful technique. Harnessing the power of the chain rule, it allows us to define a new variable (common denoted by the letter u) as a function of x, and obtain a new expression which is (hopefully) easier to integrate.','/math/calculus-home/integration-techniques-calc/u-substitution-calc/v/u-substitution','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1395,8,73,'Reverse chain rule','reverse-chain-rule-calc',NULL,'Reverse chain rule is another, faster way to think about u-substitution.','/math/calculus-home/integration-techniques-calc/reverse-chain-rule-calc/v/reverse-chain-rule-introduction','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1396,8,73,'Integration using trigonometric identities','integration-using-trigonometric-identities-calc',NULL,'Some integrals that contain trig functions demand that we manipulate those functions using trig identities in order to find the integral.','/math/calculus-home/integration-techniques-calc/integration-using-trigonometric-identities-calc/v/using-trig-identity-to-use-u-substitution','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1397,8,73,'Trigonometric substitution','trigonometric-substitution-calc',NULL,'Another super useful technique for computing integrals involves replacing variables with trigonometric functions. This can make things seem a little more complicated at first, but with the help of trigonometric identities, this technique makes certain integrals solvable.','/math/calculus-home/integration-techniques-calc/trigonometric-substitution-calc/v/introduction-to-trigonometric-substitution','2016-09-12 22:46:52','2016-09-12 22:46:52',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1398,8,74,'Area & net change','area-and-net-change-calc',NULL,'The area under a rate function gives the net change. This result of the fundamental theorem of calculus is being put here to use with some real-world problems.','/math/calculus-home/integration-applications-calc/area-and-net-change-calc/v/area-under-rate-function-example','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1399,8,74,'Rectilinear motion (integral calc)','rectilinear-motion-integral-calc',NULL,'Solve problems about motion along a line using the power of integral calculus. For example, given the velocity of a particle as a function of time v(t), find how much the particle has traveled over a given time period.','/math/calculus-home/integration-applications-calc/rectilinear-motion-integral-calc/v/antiderivative-acceleration','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1400,8,74,'Area between curves','area-between-curves-calc',NULL,'By integrating the difference of two functions, you can find the area between them.','/math/calculus-home/integration-applications-calc/area-between-curves-calc/e/area-between-a-curve-and-an-axis','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1401,8,74,'Average value of a function','average-value-of-a-function-calc',NULL,'We usually calculate the average of N terms by summing them up and dividing by N. How do you find the average of infinitely many terms? For example, the average of a function f(x) for all x-values between 0 and 1? Integral calculus to the rescue!','/math/calculus-home/integration-applications-calc/average-value-of-a-function-calc/v/average-function-value-closed-interval','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1402,8,74,'Arc length','arc-length-calc',NULL,'Integral calculus isn\'t only useful for finding area. For example, it can also be used to find lengths of one-dimensional curves. Learn all about it here.','/math/calculus-home/integration-applications-calc/arc-length-calc/v/arc-length-formula','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1403,8,74,'Solids with known cross sections','solids-with-known-cross-sections-calc',NULL,'Let\'s leverage the definite integral to find volumes of figures where we know what their cross sections look like. It is surprisingly fun.','/math/calculus-home/integration-applications-calc/solids-with-known-cross-sections-calc/v/volume-solid-semicircle-cross-section','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1404,8,74,'Disk method','disk-method-calc',NULL,'You know how to use definite integrals to find areas under curves. We now take that idea for \"spin\" by thinking about the volumes of things created when you rotate functions around various lines.','/math/calculus-home/integration-applications-calc/disk-method-calc/v/disk-method-around-x-axis','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1405,8,74,'Washer method','washer-method-calc',NULL,'Washer method is an extension of the Disk method for finding the volumes of more elaborate solids of revolution.','/math/calculus-home/integration-applications-calc/washer-method-calc/v/disc-method-washer-method-for-rotation-around-x-axis','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1406,8,74,'Shell method','shell-method-calc',NULL,'Shell method offers us another way of calculating the volume of solids of revolution. Some solids are more easily found with the shell method.','/math/calculus-home/integration-applications-calc/shell-method-calc/v/shell-method-for-rotating-around-vertical-line','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1407,8,74,'Area defined by polar graphs','area-defined-by-polar-graphs-calc',NULL,'We\'re used to finding the area under curves in the Cartesian plane, but integration can be used to find area defined by polar curves too.','/math/calculus-home/integration-applications-calc/area-defined-by-polar-graphs-calc/v/formula-area-polar-graph','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1408,8,74,'Arc length of polar graphs','arc-length-of-polar-graphs-calc',NULL,'You may already be familiar with finding arc length of graphs that are defined in terms of rectangular coordinates. We\'ll now extend our knowledge of arc length to include polar graphs.','/math/calculus-home/integration-applications-calc/arc-length-of-polar-graphs-calc/v/polar-arc-length-formula','2016-09-12 22:46:59','2016-09-12 22:46:59',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1409,8,75,'Sequences','sequences-calc',NULL,'In this tutorial, we\'ll review what sequences are, associated notation and convergence/divergence of sequences.','/math/calculus-home/series-calc/sequences-calc/v/explicit-and-recursive-definitions-of-sequences','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1410,8,75,'Sequence convergence and divergence','seq-conv-diverg-calc',NULL,'Now that we understand what a sequence is, we\'re going to think about what happens to the terms of a sequence at infinity (do they approach 0, a finite value, or +- infinity?).','/math/calculus-home/series-calc/seq-conv-diverg-calc/v/convergent-and-divergent-sequences','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1411,8,75,'Series','series-calculus',NULL,'You\'re familiar with sequences and have been eager to sum them up.  Well wait no longer!  In this tutorial, we\'ll see that series are just sums of sequences and familiarize ourselves with the notation.\n','/math/calculus-home/series-calc/series-calculus/v/sigma-notation-sum','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1412,8,75,'Geometric series','geo-series-calc',NULL,'Whether you are computing mortgage payments or calculating the distance traveled by a bouncing ball, geometric  series show up in life far more than you imagine.  This tutorial will review all the important concepts and more!','/math/calculus-home/series-calc/geo-series-calc/v/series-as-sum-of-sequence','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1413,8,75,'Tests for convergence and divergence','convergence-divergence-tests-calc',NULL,'Here you will learn several \"tests\" which determine whether certain series converge or diverge.','/math/calculus-home/series-calc/convergence-divergence-tests-calc/v/divergence-test','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1414,8,75,'Estimating infinite series','estimating-infinite-series-calc',NULL,'We\'ve spent a lot of time thinking about whether a series converges or diverges.  But, even if we can determine that a series converges, how can we figure out what it converges to?  This tutorial will show techniques of estimating what a series converges to, and for determining how good our estimates are.  This is super useful because most series can\'t be precisely evaluated (like we were able to do with infinite geometric series).','/math/calculus-home/series-calc/estimating-infinite-series-calc/v/integrals-estimating-p-series','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1415,8,75,'Power series function representation using algebra','power-series-calc',NULL,'Now that we\'re familiar with the idea of an infinite series, we can now think about functions that are defined using infinite series.  In particular, we\'ll begin to look at the power series representation of a function, which are kind of like infinite polynomials.  In later tutorials, we\'ll use calculus to find expressions of several common functions as power series.','/math/calculus-home/series-calc/power-series-calc/v/power-series-radius-interval-convergence','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1416,8,75,'Maclaurin series and Euler\'s identity','maclaurin-taylor-calc',NULL,'In this tutorial, we will learn to approximate differentiable functions with polynomials.  Beyond just being super cool, this can be useful for approximating functions so that they are easier to calculate, differentiate or integrate.  So whether you will have to write simulations or become a bond trader (bond traders use polynomial approximation to estimate changes in bond prices given interest rate changes and vice versa), this tutorial could be fun.\n\nIf that isn\'t motivation enough, we also come up with one of the most epic and powerful conclusions in all of mathematics in this tutorial: Euler\'s identity.','/math/calculus-home/series-calc/maclaurin-taylor-calc/v/maclauren-and-taylor-series-intuition','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1417,8,75,'Taylor series approximations','taylor-series-calc',NULL,'As we\'ve already seen, Maclaurin series are special cases of Taylor series centered at 0.  We\'ll now focus on more generalized Taylor series.','/math/calculus-home/series-calc/taylor-series-calc/v/visualizing-taylor-series-approximations','2016-09-12 22:47:09','2016-09-12 22:47:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1418,8,76,'AP Calculus AB questions','ap-calculus-ab-questions',NULL,'Many of you are planning on taking the Calculus AB advanced placement exam.  These are example problems taken directly from previous years\' exams. Even if you aren\'t taking the exam, these are very useful problem for making sure you understand your calculus (as always, best to pause the videos and try them yourself before Sal does).','/math/calculus-home/ap-calc-topic/ap-calculus-ab-questions/v/sal-interviews-the-ap-calculus-lead-at-college-board','2016-09-12 22:47:15','2016-09-12 22:47:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1419,8,76,'AP Calculus BC questions','ap-calculus-bc-questions',NULL,'The Calculus BC AP exam is a super set of the AB exam.  It covers everything in AB as well as some of the more advanced topics in integration, sequences and function approximation.  This tutorial is great practice for anyone looking to test their calculus mettle!','/math/calculus-home/ap-calc-topic/ap-calculus-bc-questions/v/2015-ap-calculus-bc-2a','2016-09-12 22:47:16','2016-09-12 22:47:16',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1420,9,77,'Intro to differential equations','differential-equations-intro',NULL,'How is a differential equation different from a regular one?  Well, the solution is a function (or a class of functions), not a number.  How do you like me now (that is what the differential equation would say in response to your shock)!','/math/differential-equations/first-order-differential-equations/differential-equations-intro/v/differential-equation-introduction','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1421,9,77,'Separable equations','separable-equations',NULL,'Arguably the \'easiest\' class of differential equations.  Here we use our powers of algebra to \"separate\" the y\'s from the x\'s on two different sides of the equation and then we just integrate!','/math/differential-equations/first-order-differential-equations/separable-equations/v/separable-differential-equations-introduction','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1422,9,77,'Modeling with differential equations','modeling-with-differential-equations',NULL,'Now that you know how to find solutions to separable differential equations, we can use this skill to model some real world phenomena like population growth and how things might cool down.','/math/differential-equations/first-order-differential-equations/modeling-with-differential-equations/v/modeling-population-with-simple-differential-equation','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1423,9,77,'Logistic differential equation and function','logistic-differential-equation',NULL,'Can population grow exponentially forever?  Malthus would say \"no\".  Well how do you model that mathematically.  The logistic differential equation and logistic function are there to rescue us!','/math/differential-equations/first-order-differential-equations/logistic-differential-equation/v/modeling-population-with-differential-equations','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1424,9,77,'Euler\'s Method','eulers-method-tutorial',NULL,'Most differential equations cannot be solved by \"hand\" (through analytical means).  However, this doesn\'t mean that we have to give up!  As we\'ll see in this tutorial, we can use numerical methods to approximate a solution to a differential equation.  In particular, we\'ll learn about Euler\'s Method which is a fairly intuitive way to do this!','/math/differential-equations/first-order-differential-equations/eulers-method-tutorial/v/eulers-method','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1425,9,77,'Exact equations and integrating factors','exact-equations',NULL,'A very special class of often non-linear differential equations.  If you know a bit about partial derivatives, this tutorial will help you know how to \'exactly\' solve these!','/math/differential-equations/first-order-differential-equations/exact-equations/v/exact-equations-intuition-1-proofy','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1426,9,77,'Homogeneous equations','homogeneous-equations',NULL,'In this equations, all of the fat is fully mixed in so it doesn\'t collect at the top.  No (that would be homogenized equations).\n\nActually, the term \"homogeneous\" is way overused in differential equations.  In this tutorial, we\'ll look at equations of the form y\'=(F(y/x)).','/math/differential-equations/first-order-differential-equations/homogeneous-equations/v/first-order-homegenous-equations','2016-09-12 22:47:22','2016-09-12 22:47:22',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1427,9,78,'Linear homogeneous equations','linear-homogeneous-2nd-order',NULL,'To make your life interesting, we\'ll now use the word \"homogeneous\" in a way that is not connected to the way we used the term when talking about first-order equations.  As you\'ll see, second order linear homogeneous equations can be solved with a little bit of algebra (and a lot of love).','/math/differential-equations/second-order-differential-equations/linear-homogeneous-2nd-order/v/2nd-order-linear-homogeneous-differential-equations-1','2016-09-12 22:47:28','2016-09-12 22:47:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1428,9,78,'Complex and repeated roots of characteristic equation','complex-roots-characteristic-equation',NULL,'Thinking about what happens when you have complex or repeated roots for your characteristic equation.','/math/differential-equations/second-order-differential-equations/complex-roots-characteristic-equation/v/complex-roots-of-the-characteristic-equations-1','2016-09-12 22:47:28','2016-09-12 22:47:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1429,9,78,'Method of undetermined coefficients','undetermined-coefficients',NULL,'Now we can apply some of our second order linear differential equations skills to nonhomogeneous equations.  Yay!','/math/differential-equations/second-order-differential-equations/undetermined-coefficients/v/undetermined-coefficients-1','2016-09-12 22:47:28','2016-09-12 22:47:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1430,9,79,'Laplace transform','laplace-transform-tutorial',NULL,'We now use one of the coolest techniques in mathematics to transform differential equations into algebraic ones. You\'ll also learn about transforms in general!','/math/differential-equations/laplace-transform/laplace-transform-tutorial/v/laplace-transform-1','2016-09-12 22:47:33','2016-09-12 22:47:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1431,9,79,'Properties of the Laplace transform','properties-of-laplace-transform',NULL,'You know how to use the definition of the Laplace transform.  In this tutorial, we\'ll explore some of the properties of the transform that will start to make it clear why they are so useful for differential equations.\n\nThis tutorial is paired well with the tutorial on using the \"Laplace transform to solve differential equations\".  In fact you  might come back to this tutorial over and over as you solve more and more problems.','/math/differential-equations/laplace-transform/properties-of-laplace-transform/v/laplace-transform-5','2016-09-12 22:47:33','2016-09-12 22:47:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1432,9,79,'Laplace transform to solve a differential equation','laplace-transform-to-solve-differential-equation',NULL,'You know a good bit about taking Laplace transform and useful properties of the transform.  You are dying to actually apply these skills to an actual differential equation.  Wait no longer!','/math/differential-equations/laplace-transform/laplace-transform-to-solve-differential-equation/v/laplace-transform-to-solve-an-equation','2016-09-12 22:47:34','2016-09-12 22:47:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1433,9,79,'The convolution integral','convolution-integral',NULL,'This tutorial won\'t be as convoluted as you might suspect.  We\'ll see what multiplying transforms in the s-domain give us in the time domain.','/math/differential-equations/laplace-transform/convolution-integral/v/introduction-to-the-convolution','2016-09-12 22:47:34','2016-09-12 22:47:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1434,10,80,'The geometry of vectors and matrices','eola',NULL,'For anyone looking to review/preview linear algebra, this sequence of videos by one of our content creators Grant Sanderson illustrates many of the geometric intuitions underlying the core concepts surrounding matrices and vectors.  (New videos posted every week).','/math/linear-algebra/eola-topic/eola/v/eola-preview','2016-09-12 22:47:40','2016-09-12 22:47:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1435,10,81,'Vectors','vectors',NULL,'We will begin our journey through linear algebra by defining and conceptualizing what a vector is (rather than starting with matrices and matrix operations like in a more basic algebra course) and defining some basic operations (like addition, subtraction and scalar multiplication).','/math/linear-algebra/vectors-and-spaces/vectors/v/vector-introduction-linear-algebra','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1436,10,81,'Linear combinations and spans','linear-combinations',NULL,'Given a set of vectors, what other vectors can you create by adding and/or subtracting scalar multiples of those vectors.  The set of vectors that you can create through these linear combinations of the original set is called the \"span\" of the set.','/math/linear-algebra/vectors-and-spaces/linear-combinations/v/linear-combinations-and-span','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1437,10,81,'Linear dependence and independence','linear-independence',NULL,'If no vector in a set can be created from a linear combination of the other vectors in the set, then we say that the set is linearly independent.  Linearly independent sets are great because there aren\'t any extra, unnecessary vectors lying around in the set. :)','/math/linear-algebra/vectors-and-spaces/linear-independence/v/linear-algebra-introduction-to-linear-independence','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1438,10,81,'Subspaces and the basis for a subspace','subspace-basis',NULL,'In this tutorial, we\'ll define what a \"subspace\" is --essentially a subset of vectors that has some special properties.  We\'ll then think of a set of vectors that can most efficiently be use to construct a subspace which we will call a \"basis\".','/math/linear-algebra/vectors-and-spaces/subspace-basis/v/linear-subspaces','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1439,10,81,'Vector dot and cross products','dot-cross-products',NULL,'In this tutorial, we define two ways to \"multiply\" vectors-- the dot product and the cross product.  As we progress, we\'ll get an intuitive feel for their meaning, how they can used and how the two vector products relate to each other.','/math/linear-algebra/vectors-and-spaces/dot-cross-products/v/vector-dot-product-and-vector-length','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1440,10,81,'Matrices for solving systems by elimination','matrices-elimination',NULL,'This tutorial is a bit of an excursion back to you Algebra II days when you first solved systems of equations (and possibly used matrices to do so).  In this tutorial, we did a bit deeper than you may have then, with emphasis on valid row operations and getting a matrix into reduced row echelon form.','/math/linear-algebra/vectors-and-spaces/matrices-elimination/v/matrices-reduced-row-echelon-form-1','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1441,10,81,'Null space and column space','null-column-space',NULL,'We will define matrix-vector multiplication and think about the set of vectors that satisfy Ax=0 for a given matrix A (this is the null space of A).  We then proceed to think about the linear combinations of the columns of a matrix (column space).  Both of these ideas help us think the possible solutions to the Matrix-vector equation Ax=b.','/math/linear-algebra/vectors-and-spaces/null-column-space/v/matrix-vector-products','2016-09-12 22:47:46','2016-09-12 22:47:46',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1442,10,82,'Functions and linear transformations','linear-transformations',NULL,'People have been telling you forever that linear algebra and matrices are useful for modeling, simulations and computer graphics, but it has been a little non-obvious.  This tutorial will start to draw the lines by re-introducing you functions (a bit more rigor than you may remember from high school) and linear functions/transformations in particular.','/math/linear-algebra/matrix-transformations/linear-transformations/v/a-more-formal-understanding-of-functions','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1443,10,82,'Linear transformation examples','lin-trans-examples',NULL,'In this tutorial, we do several examples of actually constructing transformation matrices.  Very useful if you\'ve got some actual transforming to do (especially scaling, rotating and projecting) ;)','/math/linear-algebra/matrix-transformations/lin-trans-examples/v/linear-transformation-examples-scaling-and-reflections','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1444,10,82,'Transformations and matrix multiplication','composition-of-transformations',NULL,'You probably remember how to multiply matrices from high school, but didn\'t know why or what it represented.  This tutorial will address this.  You\'ll see that multiplying two matrices can be view as the composition of linear transformations.','/math/linear-algebra/matrix-transformations/composition-of-transformations/v/compositions-of-linear-transformations-1','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1445,10,82,'Inverse functions and transformations','inverse-transformations',NULL,'You can use a transformation/function to map from one set to another, but can you invert it?  In other words, is there a function/transformation that given the output of the original mapping, can output the original input (this is much clearer with diagrams).  \n\nThis tutorial addresses this question in a linear algebra context.  Since matrices can represent linear transformations, we\'re going to spend a lot of time thinking about matrices that represent the inverse transformation.','/math/linear-algebra/matrix-transformations/inverse-transformations/v/linear-algebra-introduction-to-the-inverse-of-a-function','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1446,10,82,'Finding inverses and determinants','inverse-of-matrices',NULL,'We\'ve talked a lot about inverse transformations abstractly in the last tutorial.  Now, we\'re ready to actually compute inverses.  We start from \"documenting\" the row operations to get a matrix into reduced row echelon form and use this to come up with the formula for the inverse of a 2x2 matrix.  After this we define a determinant for 2x2, 3x3 and nxn matrices.','/math/linear-algebra/matrix-transformations/inverse-of-matrices/v/linear-algebra-deriving-a-method-for-determining-inverses','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1447,10,82,'More determinant depth','determinant-depth',NULL,'In the last tutorial on matrix inverses, we first defined what a determinant is and gave several examples of computing them.  In this tutorial we go deeper.  We will explore what happens to the determinant under several circumstances and conceptualize it in several ways.','/math/linear-algebra/matrix-transformations/determinant-depth/v/linear-algebra-determinant-when-row-multiplied-by-scalar','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1448,10,82,'Transpose of a matrix','matrix-transpose',NULL,'We now explore what happens when you switch the rows and columns of a matrix!','/math/linear-algebra/matrix-transformations/matrix-transpose/v/linear-algebra-transpose-of-a-matrix','2016-09-12 22:47:53','2016-09-12 22:47:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1449,10,83,'Orthogonal complements','othogonal-complements',NULL,'We will now explore the set of vectors that is orthogonal to every vector in a second set (this is the second set\'s orthogonal complement).','/math/linear-algebra/alternate-bases/othogonal-complements/v/linear-algebra-orthogonal-complements','2016-09-12 22:48:00','2016-09-12 22:48:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1450,10,83,'Orthogonal projections','orthogonal-projections',NULL,'This is one of those tutorials that bring many ideas we\'ve been building together into something applicable.  Orthogonal projections (which can sometimes be conceptualized as a \"vector\'s shadow\" on a subspace if the light source is above it) can be used in fields varying from computer graphics and statistics!\n\nIf you\'re familiar with orthogonal complements, then you\'re ready for this tutorial!','/math/linear-algebra/alternate-bases/orthogonal-projections/v/linear-algebra-projections-onto-subspaces','2016-09-12 22:48:00','2016-09-12 22:48:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1451,10,83,'Change of basis','change-of-basis',NULL,'Finding a coordinate system boring.  Even worse, does it make certain transformations difficult (especially transformations that you have to do over and over and over again)?  Well, we have the tool for you:  change your coordinate system to one that you like more.  Sound strange?  Watch this tutorial and it will be less so.  Have fun!','/math/linear-algebra/alternate-bases/change-of-basis/v/linear-algebra-coordinates-with-respect-to-a-basis','2016-09-12 22:48:00','2016-09-12 22:48:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1452,10,83,'Orthonormal bases and the Gram-Schmidt process','orthonormal-basis',NULL,'As we\'ll see in this tutorial, it is hard not to love a basis where all the vectors are orthogonal to each other and each have length 1 (hey, this sounds pretty much like some coordinate systems you\'ve known for a long time!).  We explore these orthonormal bases in some depth and also give you a great tool for creating them: the Gram-Schmidt Process (which would also be a great name for a band).','/math/linear-algebra/alternate-bases/orthonormal-basis/v/linear-algebra-introduction-to-orthonormal-bases','2016-09-12 22:48:00','2016-09-12 22:48:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1453,10,83,'Eigen-everything','eigen-everything',NULL,'Eigenvectors, eigenvalues, eigenspaces!  We will not stop with the \"eigens\"!  Seriously though, eigen-everythings have many applications including finding \"good\" bases for a transformation (yes, \"good\" is a technical term in this context).','/math/linear-algebra/alternate-bases/eigen-everything/v/linear-algebra-introduction-to-eigenvalues-and-eigenvectors','2016-09-12 22:48:00','2016-09-12 22:48:00',1);
