insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1490,14,102,'Using brackets','new-topic-2015-11-17T22:50:42.295Z',NULL,'The use of brackets (or parentheses) while multiplying or adding numbers helps us do long calculations in a very simple way! Let\'s see how.','/math/in-sixth-grade-math/knowing-numbers/new-topic-2015-11-17T22:50:42.295Z/v/evaluating-an-expression-with-and-without-parentheses','2016-09-12 22:49:53','2016-09-12 22:49:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1491,14,103,'The number line','number-line',NULL,'Here we will learn how to draw a number line that starts with zero, and we will understand ways to multiply and add two numbers on this line. We will also look into some examples concerning money and distance.','/math/in-sixth-grade-math/whole-numbers/number-line/v/number-line-1','2016-09-12 22:50:00','2016-09-12 22:50:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1492,14,103,'Properties of whole numbers','properties-whole-numbers',NULL,'Addition and multiplication of whole numbers is commutative and associative. These are simple properties of whole numbers that can help us find solutions to long calculations in a very simple manner. Let\'s see how!','/math/in-sixth-grade-math/whole-numbers/properties-whole-numbers/v/commutative-law-of-addition','2016-09-12 22:50:00','2016-09-12 22:50:00',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1493,14,104,'Factors and multiples','in-factors-multiples',NULL,'In this topic we will see that a factor of a number is an exact divisor of that number, and a multiple is a result of the multiplication of that number with another whole number. Let\'s look at this concept closely!','/math/in-sixth-grade-math/playing-numbers/in-factors-multiples/v/finding-factors-of-a-number','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1494,14,104,'Prime and composite numbers','prime-composite-numbers',NULL,'In this topic we will see that composite numbers have more than two factors while\' Prime numbers\' have exactly two factors: 1 and the number itself. We will also look into prime factorization and a fundamental theorem of arithmetic.','/math/in-sixth-grade-math/playing-numbers/prime-composite-numbers/v/prime-numbers','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1495,14,104,'Test for divisibility of numbers','test-divisibility-numbers',NULL,'In this topic we will look into how the 2, 3, 4, 5, 6, 8, 9, 10 divisibility rules govern the divisibility of a large number. We will also do the 3 and 9 divisibility rules in detail. Let\'s see what we mean by that!','/math/in-sixth-grade-math/playing-numbers/test-divisibility-numbers/v/divisibility-tests-for-2-3-4-5-6-9-10','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1496,14,104,'More divisibility rules','more-divisibility-rules',NULL,'Here, we will lean few more divisibility rules, like if a number is divisible by another number then it is divisible by each of the factors of that number.','/math/in-sixth-grade-math/playing-numbers/more-divisibility-rules/v/common-divisibility-examples','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1497,14,104,'Prime factorization','prime-factorization',NULL,'When a number is expressed as a product of its factors, we say that the number has been factorized. Here, we will look deeper into the concept of prime factorization, the fundamental theorem of arithmetic, and do a divisibility exercise!','/math/in-sixth-grade-math/playing-numbers/prime-factorization/v/prime-factorization','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1498,14,104,'Highest common factor','highest-common-factor',NULL,'We will understand what the we mean by the term \"Greatest common divisor\" or \"Highest common factor\", and cover some basic exercises. Let\'s begin!','/math/in-sixth-grade-math/playing-numbers/highest-common-factor/v/greatest-common-divisor-factor-exercise','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1499,14,104,'Lowest common multiple','lowest-common-multiple',NULL,'Here, we will look into what we mean when we use the term \"Lowest common multiple\" and how we can calculate it.','/math/in-sixth-grade-math/playing-numbers/lowest-common-multiple/v/least-common-multiple-exercise','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1500,14,104,'Some problems on HCF and LCM','hcf-lcm',NULL,'Let\'s look into some applications of Highest common factor (HCF) and Lowest common multiple (LCM), and try to build a better understanding of these topics through some exercises.','/math/in-sixth-grade-math/playing-numbers/hcf-lcm/v/lcm-and-gcf-greatest-common-factor-word-problems','2016-09-12 22:50:08','2016-09-12 22:50:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1501,14,105,'Introduction','basic-geo-introduction',NULL,'Here we will discuss the meaning of geometry and the history related to it. We will also look into the life of Euclid, who is known as the father of Geometry.','/math/in-sixth-grade-math/basic-geometrical-ideas/basic-geo-introduction/v/euclid-as-the-father-of-geometry','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1502,14,105,'Points','geo-points',NULL,'We will understand the definition of a point and look into how we can make it.','/math/in-sixth-grade-math/basic-geometrical-ideas/geo-points/v/language-and-notation-of-basic-geometry','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1503,14,105,'Line segments, lines and rays','line-segments-rays',NULL,'In this topic we will see that a line segment is a part of a line that is bounded by two end points. A line segment that goes off on both directions indefinitely is called a line, whereas a line segment that only extends in one direction is called a ray. In this topic we will learn to draw, recognize and differentiate between line segments, lines, and rays.','/math/in-sixth-grade-math/basic-geometrical-ideas/line-segments-rays/v/lines-line-segments-and-rays','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1504,14,105,'Parallel lines','parallel-lines',NULL,'Two lines that cannot have a common point are called parallel lines. When two intersecting lines make an angle of 90 degrees, they are called perpendicular lines. When two lines intersect and the angle between them is a right angle, then the lines are said to be perpendicular. In this topic we will learn to clearly differentiate between parallel and perpendicular lines and to draw them. ','/math/in-sixth-grade-math/basic-geometrical-ideas/parallel-lines/v/parallel-and-perpendicular-lines-intro','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1505,14,105,'Angles','new-topic-2015-11-17T23:24:25.732Z',NULL,'Angles are made when corners are formed. In this topic we will start with angle basics and understand what we call different angles.','/math/in-sixth-grade-math/basic-geometrical-ideas/new-topic-2015-11-17T23:24:25.732Z/v/angle-basics','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1506,14,105,'Circles','basic-geo-circles',NULL,'A circle is a simple closed curve that is not a polygon. It is the locus of all points that are equidistant from it\'s centre. In this topic we will look into the language and notations that we use while dealing with circles.','/math/in-sixth-grade-math/basic-geometrical-ideas/basic-geo-circles/v/language-and-notation-of-the-circle','2016-09-12 22:50:14','2016-09-12 22:50:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1507,14,106,'Angles: acute, obtuse, and reflex','acute-obtuse-reflex',NULL,'An angle smaller than a right angle is called an acute angle, an angle greater than right angle but less then straight angle is obtuse angle while a reflex angle is larger then a straight angle. I this chapter we will also learn how to categorize triangles based on the angles that it has.','/math/in-sixth-grade-math/understanding-elementary-shapes/acute-obtuse-reflex/v/acute-right-and-obtuse-angles','2016-09-12 22:50:20','2016-09-12 22:50:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1508,14,106,'Measuring angles','measuring-angles',NULL,'One complete revolution is divided into 360 equal parts. Each part is a degree, and angles are measured in degrees. A right angle is 90 degrees and a straight angle is 180 degrees. In this topic we will learn how to measure angles in degrees. We will also learn how to use a protractor and to solve for unknown angles.','/math/in-sixth-grade-math/understanding-elementary-shapes/measuring-angles/v/measuring-angles-in-degrees','2016-09-12 22:50:20','2016-09-12 22:50:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1509,14,106,'Classification of triangles','classification-triangles',NULL,'Triangles are of various types, they can be scalene/isosceles/equilateral or acute angled/obtuse angled/right angled triangle in nature. In this topic we will learnt how to categorize and recognize these different types of triangles.','/math/in-sixth-grade-math/understanding-elementary-shapes/classification-triangles/v/scalene-isosceles-equilateral-acute-right-obtuse','2016-09-12 22:50:20','2016-09-12 22:50:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1510,14,106,'Quadrilaterals','quadrilaterals-in',NULL,'Rectangle, parallelogram, square, rhombus, trapezium all are different types of Quadrilaterals. In this topic we will look more closely at quadrilaterals and will understand how to differentiate between it\'s various forms.','/math/in-sixth-grade-math/understanding-elementary-shapes/quadrilaterals-in/v/quadrilateral-overview','2016-09-12 22:50:20','2016-09-12 22:50:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1511,14,107,'Integers','integers-in',NULL,'If we put the whole numbers and the negative numbers together, the new collection of numbers are called integers. In this topic we will learn how to depict negative numbers on a number line, ordering of negative numbers, and will introduce the concept of fractions on a number line.','/math/in-sixth-grade-math/integers-india/integers-in/v/negative-numbers-introduction','2016-09-12 22:50:26','2016-09-12 22:50:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1512,14,107,'Addition of integers','addition-integers',NULL,'When we add a negative integer to a positive integer, we move to the left on the number line. Similarly, when we add a positive number to a negative number, we move to the right on the number line. In this topic we will learn how to add/subtract negative numbers, integers with different signs, and will practice this concept on the number line.','/math/in-sixth-grade-math/integers-india/addition-integers/v/adding-negative-numbers','2016-09-12 22:50:26','2016-09-12 22:50:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1513,14,107,'Subtraction of integers with the help of the number line','subtraction-integers-number-line',NULL,'To subtract an integer from another integer, it is enough to add the additive inverse of the integer that is being subtracted, to the other integer. We will also see that subtracting a negative equals adding a positive, and we will practice this concept on several basic problems.','/math/in-sixth-grade-math/integers-india/subtraction-integers-number-line/v/why-subtracting-a-negative-equivalent-to-adding-a-positive','2016-09-12 22:50:26','2016-09-12 22:50:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1514,14,108,'Introduction','fractions-introduction',NULL,'A fraction is a number representing part of a whole. We use fractions in everyday life, from dividing a to even checking the time. In this chapter we will learn about the basics of fractions, what proper, improper, equivalent, and mixed fractions are. We will also learn how to write fractions in their simplest forms, to compare fractions, and to add or subtract two fractions.','/math/in-sixth-grade-math/fractions-1/fractions-introduction/v/fraction-basics','2016-09-12 22:50:33','2016-09-12 22:50:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1515,14,108,'A fraction','a-fraction',NULL,'5/12 is a fraction. We read it as “five-twelfths”.12 is the number of equal parts into which the whole has been divide, out of which we are talking about 5 only. In this topic we will learn ow to recognize a fraction, we will introduce the concept of numerators and denominators and we will learn how to compare fractions visually and using the number line.','/math/in-sixth-grade-math/fractions-1/a-fraction/v/identifying-fraction-parts','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1516,14,108,'Fraction on the number line','fraction-number-line',NULL,'In this topic we will learn how to plot proper and improper fractions on a number line. We will also learn how to add fractions with different signs.','/math/in-sixth-grade-math/fractions-1/fraction-number-line/v/plotting-basic-fractions-on-the-number-line','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1517,14,108,'Improper and mixed fractions','improper-mixed-fractions',NULL,'Fractions less than 1 are called proper fractions. In a proper fraction, the numerator is always less than the denominator. A mixed fraction is a combination of a whole and a part, while fractions greater than 1 are called improper fractions. In this topic we will learn about changing/converting mixed numbers into improper fractions and vice versa. We will also do exercises to convert decimals into fractions.','/math/in-sixth-grade-math/fractions-1/improper-mixed-fractions/v/mixed-numbers-and-improper-fractions','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1518,14,108,'Equivalent fractions','equivalent-fractions',NULL,'Fractions containing a similar part of the whole are allied equivalent fractions, e.g. 1/2 = 2/4. In this chapter we will practice word problems related to equivalent fractions, and we will learn how to compare them visually and on the number line.','/math/in-sixth-grade-math/fractions-1/equivalent-fractions/v/visualizing-equivalent-fractions','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1519,14,108,'Simplest form of a fraction','simplest-form-fraction',NULL,'A fraction is said to be in the simplest (or lowest) form if its numerator and denominator have no common factor other than 1.','/math/in-sixth-grade-math/fractions-1/simplest-form-fraction/v/fractions-in-lowest-terms','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1520,14,108,'Like fractions','like-fractions',NULL,'Fractions with same denominators are called like fractions. Here we will learn how to decompose a fraction visually and practice adding fractions through exercises.','/math/in-sixth-grade-math/fractions-1/like-fractions/v/decomposing-a-fraction-visually','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1521,14,108,'Comparing fractions','comparing-fractions-in',NULL,'In like fractions, the fraction having the greater numerator is greater. In fractions with same numerator, the fraction having the greater denominator is smaller. In this topic we will learn to compare fractions with different numerators and denominators. We will also learn about ordering fractions, multiplication as scaling, and compare probabilities.','/math/in-sixth-grade-math/fractions-1/comparing-fractions-in/v/comparing-fractions-with-greater-than-and-less-than-symbols','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1522,14,108,'Addition and subtraction of fractions','addition-subtraction-fractions',NULL,'In this topic we will learn to add and subtract fractions with like, unlike and common denominators. We will also learn how to add fractions with different signs, mixed fractions with like, unlike denominators, and addition of decimals, fractions, and percentages.','/math/in-sixth-grade-math/fractions-1/addition-subtraction-fractions/v/adding-fractions-with-like-denominators','2016-09-12 22:50:34','2016-09-12 22:50:34',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1523,14,109,'Introduction','decimals-introduction',NULL,'A dot represents a decimal point. In real life problems like measuring of length, weight, or time, the values are not exact whole numbers, but rather portions of whole numbers. In situations where we need to be exact, we use decimal numbers. In this chapter we will learn how to reduce number to 10ths, 100ths of a decimal place, to compare decimal numbers. We will also learn how to add and subtract decimal numbers, and understand how to use them in real life situations.','/math/in-sixth-grade-math/decimals-1/decimals-introduction/v/introduction-to-decimals','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1524,14,109,'Tenths and hundredths','tenths-hundredths',NULL,'One block divided into 10 equal parts means each part is 1/10 (one-tenth) of a unit, and similarly, each block divided into 100 equal parts means each part is 1/100 (one-hundredth) of a unit.','/math/in-sixth-grade-math/decimals-1/tenths-hundredths/v/decimal-intuition-with-grids','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1525,14,109,'Comparing decimals','comparing-decimals-in',NULL,'We first compare the whole part, and if it is the same, then the fractional part is compared to arrive at a conclusion. In this topic we will compare decimals by difference in largest place value, ordering from least to greatest, and ordering from smallest to biggest.','/math/in-sixth-grade-math/decimals-1/comparing-decimals-in/v/comparing-decimals-visually-example','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1526,14,109,'Using decimals','using-decimals',NULL,'Money (1 paisa = Rs.0.01), Length (1 cm=0.01 m), Weight (1 g = 0.001 kg). In this topic we will consider real life problems of measurement and see how the concept of decimals can be applied in converting centimeters to meters, gram to kilogram etc.','/math/in-sixth-grade-math/decimals-1/using-decimals/v/cm-to-meters','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1527,14,109,'Addition of decimals','addition-decimals',NULL,'To add decimals, we add them as though they are regular numbers, and then we place the decimal at its original position in the result. For example, 0.56 + 1.22 = 1.78. In this topic, we will practice a few problems of decimal addition to get a better understanding of this topic.','/math/in-sixth-grade-math/decimals-1/addition-decimals/v/adding-decimals-example-1','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1528,14,109,'Subtraction of decimals','subtraction-decimals',NULL,'To subtract decimals, we keep the decimal point in place, and simply subtract each digit like we would for regular subtraction problems. For example, 2.58 – 1.32 = 1.26. In this topic we will practice a few problems based on decimal subtraction to get a better understanding of the topic.','/math/in-sixth-grade-math/decimals-1/subtraction-decimals/v/subtracting-decimals-up-to-hundredths','2016-09-12 22:50:40','2016-09-12 22:50:40',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1529,14,110,'Interpretation of a pictograph','interpretation-pictograph',NULL,'In this topic we will learn to interpret pictorially depicted data.','/math/in-sixth-grade-math/data-handling/interpretation-pictograph/v/reading-pictographs','2016-09-12 22:50:47','2016-09-12 22:50:47',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1530,14,110,'A bar graph','bar-graph',NULL,'Bars of uniform width can be drawn horizontally or vertically with equal spacing between them to depict complex data, which can then be understood easily at a glance. In this topic, we will learn to read and create bar charts.','/math/in-sixth-grade-math/data-handling/bar-graph/v/reading-bar-graphs','2016-09-12 22:50:47','2016-09-12 22:50:47',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1531,14,111,'Perimeter','perimeter-in',NULL,'Perimeter is the distance covered along the boundary forming a closed figure when you go round the figure once.','/math/in-sixth-grade-math/mensuration/perimeter-in/v/perimeter-of-a-shape','2016-09-12 22:50:53','2016-09-12 22:50:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1532,14,111,'Area','area-in',NULL,'The amount of surface enclosed by a closed figure is called its area.','/math/in-sixth-grade-math/mensuration/area-in/v/rectangle-area-as-product-of-dimensions-same-as-counting-unit-squares','2016-09-12 22:50:53','2016-09-12 22:50:53',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1533,14,112,'Introduction','algebra-introduction',NULL,'Algebra is the language through which we describe patterns in mathematics. It involves usage of letters with numbers to depict problems. In this topic chapter we will learn about Matchstick patters, we will introduce the idea of variables, and we will understand where these variables can be used. A variable is depicted as a letter which can take any value. We will also learn to write linear expressions and ways to solve them.','/math/in-sixth-grade-math/algebra-in/algebra-introduction/v/origins-of-algebra','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1534,14,112,'Matchstick patterns','matchstick-patterns',NULL,'Understand how variables can be used to denote patterns and equations, thus making our lives simpler in complex scenarios.','/math/in-sixth-grade-math/algebra-in/matchstick-patterns/v/the-beauty-of-algebra','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1535,14,112,'The idea of a variable','idea-variable',NULL,'A variable is a number whose value is not fixed; e.g. n is a variable and it can take any value 1, 2, 3, 4, …','/math/in-sixth-grade-math/algebra-in/idea-variable/v/what-is-a-variable','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1536,14,112,'Expressions with variables','expressions-variables',NULL,'Expression like (4x + 5), which contains the variable x, cannot be evaluated. Only if x is given some value, an expression like (4x + 5) can be evaluated. In this topic we will learn how to evaluate exponential equations, we will write expressions variables and learn how to solve them when the value of the variable is specified.','/math/in-sixth-grade-math/algebra-in/expressions-variables/v/writing-expressions-1','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1537,14,112,'Using expressions practically','using-expressions-practically',NULL,'In this topic we will learn to evaluate and interpret linear expression using examples from real world problems.','/math/in-sixth-grade-math/algebra-in/using-expressions-practically/v/writing-basic-expressions-from-word-problems-examples','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1538,14,112,'Solution of an equation','solution-equation',NULL,'The value of the variable in an equation which satisfies the equation is called a solution to the equation. Here we will learn to solve an equation using substitution.','/math/in-sixth-grade-math/algebra-in/solution-equation/v/solving-equations-and-inequalities-through-substitution-example-2','2016-09-12 22:51:01','2016-09-12 22:51:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1539,14,113,'Introduction','introduction-ratios',NULL,'In certain situations, comparison by division makes better sense than comparison by taking the difference. The comparison by division is called the ratio of the two numbers. In this chapter we will introduce the concept of ratios and proportions.','/math/in-sixth-grade-math/ratio-and-proportion/introduction-ratios/v/introduction-to-ratios-new-hd-version','2016-09-12 22:51:08','2016-09-12 22:51:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1540,14,113,'Ratio','inda-ratio-proportion',NULL,'Comparing any two quantities in terms of ‘how many times’ is known as ratio, and it is denoted by a \':\' sign.Two quantities can be compared only if they are in the same unit. In this topic we will learn to simplify rations, and see how they are used through several basic examples.','/math/in-sixth-grade-math/ratio-and-proportion/inda-ratio-proportion/v/ratios-intro','2016-09-12 22:51:08','2016-09-12 22:51:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1541,14,113,'Proportion','proportion-ratio-proportion',NULL,'If two ratios are equal, we say that they are in proportion and use the symbol ‘::’ or ‘=’ to equate the two ratios. Here we will look into an example that should help us gain a better understanding of this topic.','/math/in-sixth-grade-math/ratio-and-proportion/proportion-ratio-proportion/v/understanding-proportions','2016-09-12 22:51:08','2016-09-12 22:51:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1542,14,113,'Unitary method','unitary-method',NULL,'The method in which first we find the value of one unit and then the value of required number of units is known as Unitary Method. In this topic we will learn to use the unitary method to solve problems involving ratios.','/math/in-sixth-grade-math/ratio-and-proportion/unitary-method/v/introduction-to-rates','2016-09-12 22:51:08','2016-09-12 22:51:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1543,14,114,'Introduction','symmetry-introductions',NULL,'Symmetry is when one shape becomes exactly like another if you flip, slide or turn it. In this chapter we will introduce the concept of axis of symmetry. The axis of symmetry is an imaginary line on a given shape around which if the shape is folded, both sides resemble each other.','/math/in-sixth-grade-math/symmetry-1/symmetry-introductions/v/axis-of-symmetry','2016-09-12 22:51:13','2016-09-12 22:51:13',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1544,14,115,'Perpendiculars','perpendiculars',NULL,'Two lines are said to be perpendicular if they intersect such that the angles formed between them are right angles.','/math/in-sixth-grade-math/practical-geometry/perpendiculars/v/constructing-a-perpendicular-line-using-a-compass-and-straightedge','2016-09-12 22:51:19','2016-09-12 22:51:19',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1545,14,115,'Angles','angles-practical-geometry',NULL,'A shape, formed by two lines or rays diverging from a common point (the vertex). In this topic we will learn how to construct basic angles using compass and straightedge using the bisector technique.','/math/in-sixth-grade-math/practical-geometry/angles-practical-geometry/v/angle-basics','2016-09-12 22:51:19','2016-09-12 22:51:19',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1546,15,116,'Properties of addition and subtraction of integers','properties-addition-subtraction-integers',NULL,'In this topic we will understand the properties of addition and subtraction of integers, like Commutative Law of Addition, which says that the two numbers can be added in any order--but this does not hold true for subtraction. Apart from this, we will study various other properties like Commutative Property, Associative Law, and that subtracting a negative is equivalent to adding a positive.','/math/in-seventh-grade-math/india-integers/properties-addition-subtraction-integers/v/associative-law-of-addition','2016-09-12 22:51:25','2016-09-12 22:51:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1547,15,116,'Multiplication of integers','multiplication-integers',NULL,'In this section we will see what happens when we multiply two integers, and how their signs affect the result. For example, a negative times a negative is a positive but positive times a negative is a negative.','/math/in-seventh-grade-math/india-integers/multiplication-integers/v/multiplying-positive-and-negative-numbers','2016-09-12 22:51:26','2016-09-12 22:51:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1548,15,116,'Properties of multiplication of integers','properties-multiplication-integers',NULL,'In this section, we will understand the fundamental laws of Multiplication like Commutative Law which says that integers can be multiplied in any order; Identity property of 1 which says that an integer multiplied with 1 gives the same integer. Apart from this we will study various other laws like Associative Law, Combining like terms, and the Distributive Property','/math/in-seventh-grade-math/india-integers/properties-multiplication-integers/v/commutative-law-of-multiplication','2016-09-12 22:51:26','2016-09-12 22:51:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1549,15,116,'Division of integers','division-integers',NULL,'In this topic we will see what happens when we divide two integers and how their signs affect the result. For example, a negative divided by a negative gives a positive but a negative divided by a positive gives a negative.','/math/in-seventh-grade-math/india-integers/division-integers/v/dividing-positive-and-negative-numbers','2016-09-12 22:51:26','2016-09-12 22:51:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1550,15,117,'Multiplication of fractions','multiplicaiton-fractions',NULL,'In this topic we will understand multiplication of fractions, like how to multiply a fraction with a whole number, how to multiply a fraction with a fraction, and how to multiply mixed numbers. We will use number line approach to understand this multiplication in a better way.','/math/in-seventh-grade-math/fractions-decimals/multiplicaiton-fractions/v/multiplying-fractions-and-whole-numbers','2016-09-12 22:51:32','2016-09-12 22:51:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1551,15,117,'Division of fractions','division-fractions',NULL,'In this section you will learn division of fractions like how to divide whole numbers by fractions. It\'s quite simple and easy to understand. You just need to multiply whole number by reciprocal of that fraction. Apart from this, there are videos for Dividing fractions, Dividing mixed numbers, Dividing mixed numbers and Fractions, Reciprocal of a Mixed Number','/math/in-seventh-grade-math/fractions-decimals/division-fractions/v/dividing-a-whole-number-by-a-fraction-word-problem','2016-09-12 22:51:32','2016-09-12 22:51:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1552,15,117,'Multiplication of decimal number','multiplication-decimal-numbers',NULL,'In this topic we will understand multiplication of decimal numbers like how to multiply a whole number with a decimal number, how to multiply a decimal number with a decimal number. Apart from this, we will also figure out an interesting pattern when a decimal number is multiplied by powers of 10.','/math/in-seventh-grade-math/fractions-decimals/multiplication-decimal-numbers/v/intro-to-multiplying-decimals','2016-09-12 22:51:32','2016-09-12 22:51:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1553,15,117,'Division of a decimal number','division-decimal-number',NULL,'In this topic we will learn division of a decimal number by a whole number, or another decimal number. Apart from this, we will also figure out an interesting pattern when a decimal number is divided by powers of 10.','/math/in-seventh-grade-math/fractions-decimals/division-decimal-number/v/dividing-completely-to-get-decimal-answer','2016-09-12 22:51:32','2016-09-12 22:51:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1554,15,118,'Collecting data','collecting-data',NULL,'Data collection is the process of gathering information. There are various methods of data collection, such as personal interviewing, telephone, mail and the internet. But before collecting data, we need to know what would we use it for.','/math/in-seventh-grade-math/seventh-data-handling/collecting-data/v/statistical-questions','2016-09-12 22:51:39','2016-09-12 22:51:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1555,15,118,'Arithmetic mean, mode, and median','arithmetic-mean-mode-median',NULL,'Mean, Mode and Median are basic statistical terminologies which you should be aware of. Arithmetic Mean is measure of the central tendency of the group of data. Mode of a set of observations is the observation in the data that occurs most often. Median refers to the value which lie in the middle of data, when arranged in increasing or decreasing order. Let\'s learn about them in detail.','/math/in-seventh-grade-math/seventh-data-handling/arithmetic-mean-mode-median/v/statistics-intro-mean-median-and-mode','2016-09-12 22:51:39','2016-09-12 22:51:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1556,15,118,'Chance and probability','chance-probability',NULL,'Probability of an event helps us quantify and understand the likeliness of that event to occur.','/math/in-seventh-grade-math/seventh-data-handling/chance-probability/v/basic-probability','2016-09-12 22:51:39','2016-09-12 22:51:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1557,15,119,'What an equation is','equation-definition',NULL,'The word \'equation\' is derived from the word \'equal\'. An Equation is a statement which defines the equality between variables and constants. The addition, subtraction, multiplication, and division of a number on both sides does not disturb this equality. Here we will learn various forms of equation like ax=b, a/x=b. We also learn methods to solve an equation.','/math/in-seventh-grade-math/simple-equations/equation-definition/v/why-we-do-the-same-thing-to-both-sides-simple-equations','2016-09-12 22:51:45','2016-09-12 22:51:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1558,15,119,'Application of Simple Equation to Practical Situations','new-topic-2015-11-13T01:43:29.211Z',NULL,'We can solve a variety of real world problems using simple equations. We start by forming an equation corresponding to the problem, the solution to this equation can then be found and applied in the real world!','/math/in-seventh-grade-math/simple-equations/new-topic-2015-11-13T01:43:29.211Z/v/constructing-basic-equations-examples','2016-09-12 22:51:45','2016-09-12 22:51:45',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1559,15,120,'Related angles','related-angles',NULL,'Here we will understand the meaning of various types of related angles like complementary, supplementary and vertical angles. We will use these concepts to find values of unknown angles.','/math/in-seventh-grade-math/lines-angles/related-angles/v/complementary-and-supplementary-angles','2016-09-12 22:51:50','2016-09-12 22:51:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1560,15,120,'Pairs of lines','pairs-lines',NULL,'Here we will understand the various relationships that a pair of lines can have with each other. We will understand the meaning of parallel lines, transversals, congruent angles, and how to figure out angles between transversal and parallel lines.','/math/in-seventh-grade-math/lines-angles/pairs-lines/v/angles-formed-by-parallel-lines-and-transversals','2016-09-12 22:51:50','2016-09-12 22:51:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1561,15,120,'Checking for parallel lines','checking-parallel-lines',NULL,'There is a simple way to prove that two lines are parallel. If we have a transversal which cuts two lines such that the interior angles are equal to each other, then the two lines are parallel to each other. Let\'s see how this works!','/math/in-seventh-grade-math/lines-angles/checking-parallel-lines/v/proof-corresponding-angle-equivalence-implies-parallel-lines','2016-09-12 22:51:50','2016-09-12 22:51:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1562,15,121,'Angle sum property of a triangle','angle-sum-property-triangle',NULL,'The sum of the three interior angles of a triangle is always 180 degree. This property helps us solve a wide variety of problems based on Triangles.','/math/in-seventh-grade-math/triangle-pror/angle-sum-property-triangle/v/proof-sum-of-measures-of-angles-in-a-triangle-are-180','2016-09-12 22:51:55','2016-09-12 22:51:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1563,15,121,'Two special triangles: equilateral and isosceles','equilateral-isosceles',NULL,'Here we will learn two special types of triangles: Equilateral and Isosceles. A triangle in which all the three sides are of equal lengths and the measure of each angle is 60 degree is called an \'equilateral\' triangle , whereas a triangle with two sides equal is called \'Isosceles\'. Also, the angles opposite to equal sides of an isosceles triangle are equal and on the basis of this property, we are going to find missing angles of triangles. ','/math/in-seventh-grade-math/triangle-pror/equilateral-isosceles/v/equilateral-and-isosceles-example-problems','2016-09-12 22:51:55','2016-09-12 22:51:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1564,15,121,'Sum of lengths of two sides of a triangle','sum-length-triangle',NULL,'The sum of the lengths of any two sides of a triangle is always greater than the third side. This is called Triangle Inequality Theorem.','/math/in-seventh-grade-math/triangle-pror/sum-length-triangle/v/triangle-inqequality-theorem','2016-09-12 22:51:55','2016-09-12 22:51:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1565,15,121,'Right angled triangles and Pythagoras property','right-angles-pythagoras',NULL,'A right-angled triangle is the one in which the measure of one of the angles is 90 degrees. In a right-angled triangle, the square of the hypotenuse equals the sum of the squares of the other two sides. This is called Pythagorean Theorem. If the Pythagoras property holds, the triangle must be right-angled.','/math/in-seventh-grade-math/triangle-pror/right-angles-pythagoras/v/pythagorean-theorem','2016-09-12 22:51:55','2016-09-12 22:51:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1566,15,122,'Congruence among line segments','congruence-line-segments',NULL,'If two line segments have the same length, they are congruent and vice versa.','/math/in-seventh-grade-math/congruence-triangles/congruence-line-segments/v/congruent-segments','2016-09-12 22:52:01','2016-09-12 22:52:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1567,15,122,'Criteria for congruence of triangles','new-topic-2015-11-17T01:08:32.289Z',NULL,'In this topic we will learn the criteria that are used to prove that two triangles are congruent.','/math/in-seventh-grade-math/congruence-triangles/new-topic-2015-11-17T01:08:32.289Z/v/congruent-triangles-and-sss','2016-09-12 22:52:01','2016-09-12 22:52:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1568,15,123,'Equivalent ratios','equivalent-ratios',NULL,'Ratios are a comparison between two quantities. If two ratios are equivalent, they are said to be in proportion. You have already studied about ratios and unitary method in rade 6th, let\'s see how much you remember!','/math/in-seventh-grade-math/comparing-quantities/equivalent-ratios/e/ratio_word_problems','2016-09-12 22:52:07','2016-09-12 22:52:07',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1569,15,123,'Percentage: another way of comparing quantities','percentage-comparing-quantities',NULL,'\'Percent\' comes from the latin \'Per Centum\'. The latin word Centum means 100. So a percentage is a measure of the quantity we have of a specific part in relation to 100 portions of the whole! Here we will understand how to convert decimals to percents, percents to decimals, and discuss some percent word problems as well!','/math/in-seventh-grade-math/comparing-quantities/percentage-comparing-quantities/v/describing-the-meaning-of-percent','2016-09-12 22:52:07','2016-09-12 22:52:07',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1570,15,123,'Use of percentages','use-percentages',NULL,'Percentages are used in every day life to state progress, ratio of quantities, probability etc. When we say we have done 50% of the work we mean that 1/2 of the work has been completed. Let\'s do a couple of examples to understand this better!','/math/in-seventh-grade-math/comparing-quantities/use-percentages/v/taking-a-percentage-example','2016-09-12 22:52:07','2016-09-12 22:52:07',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1571,15,124,'Rational numbers on a number line','rational-numbers-number-line',NULL,'Here we will learn how to represent decimals, fractions, rational numbers on number line. Representation of rational numbers on number line makes their comparison easier!','/math/in-seventh-grade-math/rational-numbers/rational-numbers-number-line/v/points-on-a-number-line','2016-09-12 22:52:14','2016-09-12 22:52:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1572,15,124,'Comparison of rational numbers','comparison-rational-numbers',NULL,'We know how to compare two integers or two fractions and tell which is smaller or which is greater among them. Let us now see how we can compare two rational numbers','/math/in-seventh-grade-math/rational-numbers/comparison-rational-numbers/v/comparing-rational-numbers','2016-09-12 22:52:14','2016-09-12 22:52:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1573,15,124,'Operations on rational numbers','operations-rational-numbers',NULL,'We know how to add, subtract, multiply and divide integers as well as fractions. Let us now study these basic operations on rational numbers','/math/in-seventh-grade-math/rational-numbers/operations-rational-numbers/v/sum-and-product-of-rational-numbers','2016-09-12 22:52:14','2016-09-12 22:52:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1574,15,125,'Construction of triangles when the lengths of its three sides are known (SSS Criterion)','new-topic-2015-11-17T01:34:20.426Z',NULL,'We can easily construct triangles when the lengths of all of its sides are known to us. In this chapter, we will get to understand how.','/math/in-seventh-grade-math/seventh-practical-geometry/new-topic-2015-11-17T01:34:20.426Z/v/constructing-triangles-with-constraints','2016-09-12 22:52:20','2016-09-12 22:52:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1575,15,126,'Squares and rectangles','squares-rectangles',NULL,'The perimeter of a square of side A is equal to four times one of its side (4XA), whereas, the perimeter of a rectangle with length L and breadth B is two times the sum of two adjacent sides(2 X [L+B] ). Area of a rectangle is the product of its length and breadth (LXB), whereas, Area of a square is the square of its side (A X A). All this you have already studied in previous classes, let\'s see how much you remember!','/math/in-seventh-grade-math/seventh-perimeter-area/squares-rectangles/e/area-and-perimeter-of-rectangles-word-problems','2016-09-12 22:52:25','2016-09-12 22:52:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1576,15,126,'Area of a parallelogram','area-parallelogram',NULL,'A parallelogram is a four-sided figure whose opposite sides are parallel. Here we will learn how to calculate area of parallelogram. The area of parallelogram is the product of its base and height (b * h).','/math/in-seventh-grade-math/seventh-perimeter-area/area-parallelogram/v/area-of-a-parallelogram','2016-09-12 22:52:25','2016-09-12 22:52:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1577,15,126,'Area of triangles','area-triangles',NULL,'The area of triangle is 1/2 * base * height. Here we will learn how to derive this formula and solve an exercise based on finding area of trianges.','/math/in-seventh-grade-math/seventh-perimeter-area/area-triangles/v/triangle-area-proofs','2016-09-12 22:52:25','2016-09-12 22:52:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1578,15,126,'Circles','new-topic-2015-11-17T01:38:41.827Z',NULL,'A circle is the locus of all points equidistant from a central point. Here we will learn basic concepts related to circle -- radius, diameter, circumference, area.','/math/in-seventh-grade-math/seventh-perimeter-area/new-topic-2015-11-17T01:38:41.827Z/v/circles-radius-diameter-and-circumference','2016-09-12 22:52:25','2016-09-12 22:52:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1579,15,127,'How are expressions formed?','expression-formation',NULL,'A variable is denoted by \'x,y,z…\' and it can hold many values whereas a constant has a fixed value. The combination of variables and constants makes up an algebraic expression. You have already lean how to form algebraic expressions in class 6th, let\'s check how much you remember.','/math/in-seventh-grade-math/algebraic-expressions/expression-formation/v/writing-expressions-1','2016-09-12 22:52:32','2016-09-12 22:52:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1580,15,127,'Terms of an expression','terms-expression',NULL,'In this topic, we will learn what is meant by terms, factors and coefficients.','/math/in-seventh-grade-math/algebraic-expressions/terms-expression/v/expression-terms-factors-and-coefficients','2016-09-12 22:52:32','2016-09-12 22:52:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1581,15,127,'Like and unlike terms: adding and subtracting','like-unlike-addition-subtraction',NULL,'When terms have the same algebraic factors, they are like terms. When terms have different algebraic factors, they are unlike terms. Here we will learn how to write expressions and how to add expressions. While adding and subtracting expressions, first look for like terms and add them; then handle unlike terms. Let\'s understand this better!','/math/in-seventh-grade-math/algebraic-expressions/like-unlike-addition-subtraction/v/combining-like-terms','2016-09-12 22:52:32','2016-09-12 22:52:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1582,15,127,'Finding the value of an expression','finding-value-expression',NULL,'The value of an algebraic expression depends on the values of the variables forming the expression. There are a number of situations in which we need to find the value of an expression, such as when we wish to check whether a particular value of a variable satisfies a given equation or not. Let\'s understand in detail what we mean by this!','/math/in-seventh-grade-math/algebraic-expressions/finding-value-expression/v/evaluating-exponential-expressions','2016-09-12 22:52:32','2016-09-12 22:52:32',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1583,15,128,'Exponents','in-exponents',NULL,'Here we will get to know what exponents are through various examples.','/math/in-seventh-grade-math/exponents-powers/in-exponents/v/introduction-to-exponents','2016-09-12 22:52:38','2016-09-12 22:52:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1584,15,128,'Laws of exponents with examples','laws-exponents-examples',NULL,'In this topic we will understand the laws which govern the way in which exponents behave under different combinations of multiplication and division of powers and bases and simplify problems.','/math/in-seventh-grade-math/exponents-powers/laws-exponents-examples/v/exponent-properties-involving-products','2016-09-12 22:52:38','2016-09-12 22:52:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1585,15,129,'Lines of symmetry for regular polygons','lines-symmetry-regular-polygones',NULL,'The concept of line symmetry is closely related to mirror reflection. A shape has line symmetry when one half of it is the mirror image of the other half. A mirror line, thus, helps to visualise a line of symmetry.','/math/in-seventh-grade-math/in-s/lines-symmetry-regular-polygones/v/axis-of-symmetry','2016-09-12 22:52:44','2016-09-12 22:52:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1586,15,129,'Rotational symmetry','new-topic-2015-11-17T21:33:13.427Z',NULL,'If any object rotates and there is no change in the shape and size, then it is said to have rotational symmetry. In this section we will learn more about rotational symmetry.','/math/in-seventh-grade-math/in-s/new-topic-2015-11-17T21:33:13.427Z/v/example-rotating-polygons','2016-09-12 22:52:44','2016-09-12 22:52:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1587,15,130,'Nets for building 3D shapes','nets-building-3d-shapes',NULL,'A net is a sort of skeleton-outline in 2-D, which, when folded, results in a 3-D shape.','/math/in-seventh-grade-math/visualizing-solid-shapes/nets-building-3d-shapes/e/nets-of-3d-figures','2016-09-12 22:52:50','2016-09-12 22:52:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1588,16,131,'Introduction','rational-numbers-introduction-1',NULL,'We have already seen basic operations on rational numbers. We now try to explore some properties of operations on the different types of numbers seen so far.','/math/in-eighth-grade-math/rational-numbers-1/rational-numbers-introduction-1/v/number-sets','2016-09-12 22:52:55','2016-09-12 22:52:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1589,16,131,'Properties of a rational number','properties-rational-number',NULL,'In this topic we will look at the inverse property of multiplication, distributive property of algebraic expressions, equivalent forms of expressions, etc.','/math/in-eighth-grade-math/rational-numbers-1/properties-rational-number/v/inverse-property-of-multiplication','2016-09-12 22:52:55','2016-09-12 22:52:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1590,16,132,'Solving equations with linear expressions on one side and numbers on the other','equations-linear-expressions-one-side-numbers-other',NULL,'We have already studied how to solve equations with variable on one side in class 7th, let\'s see how much you remember!','/math/in-eighth-grade-math/linear-equations-one-variable/equations-linear-expressions-one-side-numbers-other/e/linear_equations_1','2016-09-12 22:53:01','2016-09-12 22:53:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1591,16,132,'Some applications','some-applications-1',NULL,'We have already studied how to frame and solve equations with variable on one side in class 7th, let\'s see how much you remember!','/math/in-eighth-grade-math/linear-equations-one-variable/some-applications-1/e/equations-in-one-variable-1','2016-09-12 22:53:01','2016-09-12 22:53:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1592,16,132,'Solving equations with the variable on both sides','solving-equations-variable-both-sides',NULL,'In the above cases, we had one variable on only one side, but this might not always be the case. Both LHS and RHS can have variables. Note: Just like numbers, variables can, also, be transposed from one side of the equation to the other.','/math/in-eighth-grade-math/linear-equations-one-variable/solving-equations-variable-both-sides/v/why-we-do-the-same-thing-to-both-sides-multi-step-equations','2016-09-12 22:53:01','2016-09-12 22:53:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1593,16,132,'Reducing equations to simpler form','reducing-equations-simpler-form',NULL,'Here we will study how to reduce complex linear equations to simpler form.','/math/in-eighth-grade-math/linear-equations-one-variable/reducing-equations-simpler-form/v/solving-equations-with-the-distributive-property','2016-09-12 22:53:01','2016-09-12 22:53:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1594,16,132,'Some more applications','some-more-applications-1',NULL,'Here we are going to deal with the framing of equations that variables on both sides of the equality.','/math/in-eighth-grade-math/linear-equations-one-variable/some-more-applications-1/v/ex-2-multi-step-equation','2016-09-12 22:53:02','2016-09-12 22:53:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1595,16,133,'Polygons','polygons-1',NULL,'Polygons are many-sided figures, with sides that are line segments. Polygons are named according to the number of sides and angles they have. The most familiar polygons are the triangle, the rectangle, and the square. In this topic we will learn about the exterior angle property, interior angle property, and we will also practice with some exercises related to polygons.','/math/in-eighth-grade-math/understanding-quadrilaterals-1/polygons-1/v/sum-of-interior-angles-of-a-polygon','2016-09-12 22:53:08','2016-09-12 22:53:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1596,16,133,'Sum of the measures of the exterior angles of a polygon','sum-measures-exterior-angles-polygon',NULL,'In this section we will learn about how to calculate the sum of the measure of the exterior angles of a polygon.','/math/in-eighth-grade-math/understanding-quadrilaterals-1/sum-measures-exterior-angles-polygon/v/sum-of-the-exterior-angles-of-convex-polygon','2016-09-12 22:53:09','2016-09-12 22:53:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1597,16,133,'Kinds of quadrilaterals','kinds-quadrilaterals',NULL,'A quadrilateral is a four sides polygon with 4 angles. In this topic we will learn about the different kinds of quadrilaterals that can exist!','/math/in-eighth-grade-math/understanding-quadrilaterals-1/kinds-quadrilaterals/v/kites-as-a-mathematical-shape','2016-09-12 22:53:09','2016-09-12 22:53:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1598,16,133,'Some special parallelograms','some-special-parallelograms',NULL,'A parallelogram is a quadrilateral with two pairs of parallel sides. The opposite or facing sides of a parallelogram are of equal length and the opposite angles of a parallelogram are of equal measure. In this chapter we will learn about two different kinds of parallelograms, the rhombus and the rectangle.','/math/in-eighth-grade-math/understanding-quadrilaterals-1/some-special-parallelograms/v/quadrilateral-properties','2016-09-12 22:53:09','2016-09-12 22:53:09',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1599,16,134,'Organizing data','organizing-data',NULL,'Usually, data available to us is in an unorganised form called raw data. To draw meaningful inferences, we need to organise the data systematically. We will learn about a couple of ways to do so.','/math/in-eighth-grade-math/data-handling-1/organizing-data/e/intro-to-simple-data','2016-09-12 22:53:17','2016-09-12 22:53:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1600,16,134,'Grouping Data','grouping-data-1',NULL,'In this topic we will learn about depicting and interpreting data on bar graphs, and pictographs.','/math/in-eighth-grade-math/data-handling-1/grouping-data-1/v/histograms','2016-09-12 22:53:17','2016-09-12 22:53:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1601,16,134,'Circle Graphs or Pie Charts','new-topic-2015-11-12T18:23:20.795Z',NULL,'In this topic we will learn about reading and depicting data on pie charts (circle graphs).','/math/in-eighth-grade-math/data-handling-1/new-topic-2015-11-12T18:23:20.795Z/v/reading-pie-graphs-circle-graphs','2016-09-12 22:53:17','2016-09-12 22:53:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1602,16,134,'Chance and Probability','new-topic-2015-11-12T18:23:30.291Z',NULL,'Probability is the measure of the likeliness that an event will occur. In this topic we will learn about the basics of probability, frequency probability, independent events, compound probability and we will increase our understanding of this topic by looking at several examples and exercises.','/math/in-eighth-grade-math/data-handling-1/new-topic-2015-11-12T18:23:30.291Z/v/basic-probability','2016-09-12 22:53:17','2016-09-12 22:53:17',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1603,16,135,'Square roots','square-roots',NULL,'The square root of a given number is an integer which yields the given number when multiplied by itself.','/math/in-eighth-grade-math/squares-square-roots/square-roots/v/introduction-to-square-roots','2016-09-12 22:53:24','2016-09-12 22:53:24',1);
