insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1605,16,136,'Cube roots','cube-roots',NULL,'In this topic we will learn about ways to find the cube root of a number and we will solve some exercises to practice this technique.','/math/in-eighth-grade-math/cubes-cube-roots/cube-roots/v/introduction-to-cube-roots','2016-09-12 22:53:30','2016-09-12 22:53:30',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1606,16,137,'What are expressions?','expression-definition-1',NULL,'An algebraic expression is a statement built up from integer constants, variables, and the algebraic operations like addition, subtraction, multiplication, and division.','/math/in-eighth-grade-math/algebraic-expressions-identities/expression-definition-1/v/variables-expressions-and-equations','2016-09-12 22:53:36','2016-09-12 22:53:36',1);
