insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1610,16,137,'Multiplying a polynomial by a polynomial','polynomial-by-polynomial',NULL,'Here we will learn how to multiply a polynomial with a polynomial, a binomial with a binomial, and a binomial with a trinomial.','/math/in-eighth-grade-math/algebraic-expressions-identities/polynomial-by-polynomial/v/multiplying-binomials','2016-09-12 22:53:37','2016-09-12 22:53:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1611,16,137,'Standard identities','standard-identities',NULL,'An identity is an equality, which is true for all values of the variables in the equality. Here we will learn three standard identities- (a+b)^2=a^2+b^2+2ab, (a-b)^2=a^2+b^2-2ab, (a+b)(a-b)=a^2-b^2.','/math/in-eighth-grade-math/algebraic-expressions-identities/standard-identities/v/special-products-of-binomials','2016-09-12 22:53:37','2016-09-12 22:53:37',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1612,16,138,'Let us recall','lets-recall',NULL,'We have studied about area of squares, rectangles, triangles, circles in previous classes; let\'s check how much you remember!','/math/in-eighth-grade-math/eighth-mensuration/lets-recall/e/area-and-circumference-of-circles','2016-09-12 22:53:43','2016-09-12 22:53:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1613,16,138,'Area of a trapezium','area-trapezium',NULL,'A quadrilateral which has one pair of opposite sides parallel is called trapezium. Here we will learn how to calculate area of trapezium','/math/in-eighth-grade-math/eighth-mensuration/area-trapezium/v/area-of-a-trapezoid-1','2016-09-12 22:53:43','2016-09-12 22:53:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1614,16,138,'Area of general quadrilaterals','area-quadrilaterals',NULL,'Here we will learn how to calculate area of rhombus.','/math/in-eighth-grade-math/eighth-mensuration/area-quadrilaterals/v/proof-rhombus-area-half-product-of-diagonal-length','2016-09-12 22:53:43','2016-09-12 22:53:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1615,16,138,'Area of polygons','area-polygons',NULL,'A polygon can be decomposed into different parts and the area of individual parts can be added to find the area of polygon.','/math/in-eighth-grade-math/eighth-mensuration/area-polygons/v/area-breaking-up-shape','2016-09-12 22:53:43','2016-09-12 22:53:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1616,16,138,'Volume of cube, cuboid, and cylinder','volume-cube-cuboid-cylinder',NULL,'In this topic we will learn about three dimensional shapes like Cubes, Cuboids, and Cylinders and we will learn about to calculate their volumes.','/math/in-eighth-grade-math/eighth-mensuration/volume-cube-cuboid-cylinder/v/how-we-measure-volume','2016-09-12 22:53:43','2016-09-12 22:53:43',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1617,16,139,'Powers with negative exponents','powers-negative-exponents',NULL,'In this topic we will learn about negative exponents, exponent properties involving quotients, zero, non-negative, and fractional exponents. We will also solve some exercises to have a better understanding of this topic.','/math/in-eighth-grade-math/exponents-powers-1/powers-negative-exponents/v/negative-exponents','2016-09-12 22:53:49','2016-09-12 22:53:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1618,16,139,'Laws of exponents','laws-exponents',NULL,'We have already studied laws of exponents in grade 7 but there powers of exponents used to be positive. Here we will study about the laws of exponents with negative powers. The laws which are applicable to positive powers of exponents shall be applicable on negative powers of  exponents as well.','/math/in-eighth-grade-math/exponents-powers-1/laws-exponents/v/exponent-rules-part-1','2016-09-12 22:53:49','2016-09-12 22:53:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1619,16,139,'Use of Exponents to Express Small Numbers in Standard Form','exponents-express-small-numbers-standard-form',NULL,'Here we will learn how large and small numbrs can be expressed in standard form/scientfic notation and solve various word problems with that.','/math/in-eighth-grade-math/exponents-powers-1/exponents-express-small-numbers-standard-form/v/scientific-notation-old','2016-09-12 22:53:49','2016-09-12 22:53:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1620,16,140,'Direct and inverse proportions','direct-inverse-props',NULL,'Two values are said to be in direct proportion when increase in one results in an increase in the other. Similarly, they are said to be in indirect proportion when the increase in one results in a decrease in the other.','/math/in-eighth-grade-math/direct-inverse-proportions/direct-inverse-props/v/direct-and-inverse-variation','2016-09-12 22:53:55','2016-09-12 22:53:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1621,16,141,'What Is factorization?','what-is-factorization',NULL,'In this topic we will see how to expand an expression using the distributive property.','/math/in-eighth-grade-math/factorization/what-is-factorization/v/distributive-property-with-rational-terms','2016-09-12 22:54:01','2016-09-12 22:54:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1622,16,141,'Division of algebraic expressions','division-algebraic-expressions',NULL,'We have learnt how to add and subtract algebraic expressions. We also know how to\nmultiply two expressions. We have not however, looked at division of one algebraic\nexpression by another. In this section we will learn about that.','/math/in-eighth-grade-math/factorization/division-algebraic-expressions/v/polynomial-division','2016-09-12 22:54:01','2016-09-12 22:54:01',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1623,16,142,'Introduction','introduction-to-graphs',NULL,'Have you seen graphs in the newspapers, television, magazines, books etc.? The purpose of the graph is to show numerical facts in visual form so that they can be understood quickly, easily and clearly. Thus graphs are visual representations of data collected. Let\'s recall some of the graphs about which we have studied earlier. ','/math/in-eighth-grade-math/intro-graphs/introduction-to-graphs/v/reading-pie-graphs-circle-graphs','2016-09-12 22:54:06','2016-09-12 22:54:06',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1624,16,142,'Linear graphs','linear-graphs',NULL,'A series of points, discrete or continuous, forming a curve or surface, each of which represents a value of a variable make up a linear graph. It can also be called a network of lines connecting points. In this topic we will learn how to read a line graph.','/math/in-eighth-grade-math/intro-graphs/linear-graphs/v/u08-l1-t2-we2-reading-line-graphs','2016-09-12 22:54:06','2016-09-12 22:54:06',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1625,16,142,'Some applications','some-applications',NULL,'In this topic we will learn about visualization of sequence relationships, interpreting & graphing relationships, interpreting relationships betweens patterns on a graph, and we will do some exercises which will help us understand the use of graphs a little better!','/math/in-eighth-grade-math/intro-graphs/some-applications/v/interpreting-linear-graphs','2016-09-12 22:54:06','2016-09-12 22:54:06',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1626,16,143,'Numbers in general form','numbers-general-form',NULL,'Writing numbers in the expanded form, as \'10a + b\' or \'100a + 10b + c\'.','/math/in-eighth-grade-math/playing-numbers-1/numbers-general-form/v/place-value-3','2016-09-12 22:54:12','2016-09-12 22:54:12',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1627,16,143,'Tests of divisibility','tests-divisibility',NULL,'In grade 6, you learnt how to check divisibility by the following divisors: 10, 5, 2, 3, 6, 4, 8, 9, 11. You would have found the tests easy to do, but you may have wondered at the same time why they work. Now, in this chapter, we shall go into the “why” aspect of the above.','/math/in-eighth-grade-math/playing-numbers-1/tests-divisibility/v/divisibility-tests-for-2-3-4-5-6-9-10','2016-09-12 22:54:12','2016-09-12 22:54:12',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1628,18,144,'Displacement, velocity, and time','displacement-velocity-time',NULL,'This tutorial is the backbone of your understanding of kinematics (i.e., the motion of objects).  You might already know that distance = rate x time.  This tutorial essentially reviews that idea with a vector lens (we introduce you to vectors here as well).  So strap your belts (actually this might not be necessary since we don\'t plan on decelerating in this tutorial) and prepare for a gentle ride of foundational physics knowledge.','/science/physics/one-dimensional-motion/displacement-velocity-time/a/preparing-to-study-physics','2016-09-12 22:54:18','2016-09-12 22:54:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1629,18,144,'Acceleration','acceleration-tutorial',NULL,'In a world full of unbalanced forces (which you learn more about when you study Newton\'s laws), you will have acceleration (which is the rate in change of velocity).  Whether you\'re thinking about how fast a Porsche can get to 60mph or how long it takes for a passenger plane to get to the necessary speed for flight, this tutorial will help.','/science/physics/one-dimensional-motion/acceleration-tutorial/v/acceleration','2016-09-12 22:54:18','2016-09-12 22:54:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1630,18,144,'Kinematic formulas and projectile motion','kinematic-formulas',NULL,'We don\'t believe in memorizing formulas and neither should you (unless you want to live your life as a shadow of your true potential).  This tutorial builds on what we know about displacement, velocity and acceleration to solve problems in kinematics (including projectile motion problems).  Along the way, we derive (and re-derive) some of the classic formulas that you might see in your physics book.','/science/physics/one-dimensional-motion/kinematic-formulas/v/average-velocity-for-constant-acceleration','2016-09-12 22:54:18','2016-09-12 22:54:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1631,18,144,'Old videos on projectile motion','old-projectile-motion',NULL,'This tutorial has some of the old videos that Sal first did around 2007.  This content is covered elsewhere, but some folks like the raw (and masculine) simplicity of these first lessons (Sal added the bit about \"masculine\").','/science/physics/one-dimensional-motion/old-projectile-motion/v/projectile-motion-part-1','2016-09-12 22:54:18','2016-09-12 22:54:18',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1632,18,145,'Two-dimensional projectile motion','two-dimensional-projectile-mot',NULL,'Let\'s escape from the binds of one-dimension (where we were forced to launch things straight up) and start launching at angles.  With a little bit of trig (might want to review sin and cos) we\'ll be figuring out just how long and far something can travel.','/science/physics/two-dimensional-motion/two-dimensional-projectile-mot/v/visualizing-vectors-in-2-dimensions','2016-09-12 22:54:25','2016-09-12 22:54:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1633,18,145,'Optimal angle for a projectile','optimal-projectile-angle',NULL,'This tutorial tackles a fundamental question when trying to launch things as far as possible (key if you\'re looking to capture a fort with anything from water balloons to arrows).  With a bit of calculus, we\'ll get to a fairly intuitive answer.','/science/physics/two-dimensional-motion/optimal-projectile-angle/v/optimal-angle-for-a-projectile-part-1','2016-09-12 22:54:25','2016-09-12 22:54:25',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1634,18,146,'Newton\'s laws of motion','newtons-laws-of-motion',NULL,'This tutorial will expose you to the foundation of classical mechanics--Newton\'s laws.  On one level they are intuitive, on another lever they are completely counter-intuitive.  Challenge your take on reality and watch this tutorial.  The world will look very different after you\'re done.','/science/physics/forces-newtons-laws/newtons-laws-of-motion/v/newton-s-1st-law-of-motion','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1635,18,146,'Normal force and contact force','normal-contact-force',NULL,'A dog is balancing on one arm on my head.  Is my head applying a force to the dog\'s hand?  If it weren\'t, wouldn\'t there be nothing to offset the pull of gravity causing the acrobatic dog to fall?  What would we call this force?  Can we have a general term from the component of a contact force that acts perpendicular to the plane of contact?  These are absolutely normal questions to ask.','/science/physics/forces-newtons-laws/normal-contact-force/v/normal-force-and-contact-force','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1636,18,146,'Balanced and unbalanced forces','balanced-unbalanced-forces',NULL,'You will often hear physics professors be careful to say \"net force\" or \"unbalanced force\" rather than just \"force\".  Why?  This tutorial explains why and might give you more intuition about Newton\'s laws in the process.','/science/physics/forces-newtons-laws/balanced-unbalanced-forces/v/balanced-and-unbalanced-forces','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1637,18,146,'Slow sock on Lubricon VI','lubricon-vi',NULL,'This short tutorial will have you dealing with orbiting frozen socks in order to understand whether you understand Newton\'s Laws.  We also quiz you a bit during the videos just to make sure that you aren\'t daydreaming about what you would do with a frozen sock.','/science/physics/forces-newtons-laws/lubricon-vi/v/slow-sock-on-lubricon-vi','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1638,18,146,'Inclined planes and friction','inclined-planes-friction',NULL,'We\'ve all slid down slides/snow-or-mud-covered-hills/railings at some point in our life (if not, you haven\'t really lived) and noticed that the smoother the surface the more we would accelerate (try to slide down a non-snow-or-mud-covered hill).  This tutorial looks into this in some depth.  We\'ll look at masses on inclined planes and think about static and kinetic friction.','/science/physics/forces-newtons-laws/inclined-planes-friction/v/inclined-plane-force-components','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1639,18,146,'Tension','tension-tutorial',NULL,'Bad commute? Baby crying?  Bills to pay?  Looking to take a bath with some Calgon (do a search on YouTube for context) to ease your tension?  This tutorial has nothing (actually little, not nothing) to do with that.\n\nSo far, most of the forces we\'ve been dealing with are forces of \"pushing\"--contact forces at the macro level because of atoms not wanting to get to close at the micro level.  Now we\'ll deal with \"pulling\" force or tension (at a micro level this is the force of attraction between bonded atoms).','/science/physics/forces-newtons-laws/tension-tutorial/v/the-force-of-tension','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1640,18,146,'Treating systems','treating-systems',NULL,'When two or more objects must move with the same magnitude of acceleration (like masses on strings, or boxes pushed into each other), we can treat the entire system as a single object when finding the acceleration.','/science/physics/forces-newtons-laws/treating-systems/v/treating-systems-the-hard-way','2016-09-12 22:54:31','2016-09-12 22:54:31',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1641,18,147,'Circular motion and centripetal acceleration','centripetal-acceleration-tutoria',NULL,'Why do things move in circles? Seriously.  Why does *anything* ever move in a circle (straight lines seem much more natural)?  Is something moving in a circle at a constant speed accelerating?  If so, in what direction?  This tutorial will help you get your mind around this super-fun topic.','/science/physics/centripetal-force-and-gravitation/centripetal-acceleration-tutoria/v/race-cars-with-constant-speed-around-curve','2016-09-12 22:54:38','2016-09-12 22:54:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1642,18,147,'Centripetal forces','centripetal-forces',NULL,'Learn how to deal with centripetal forces and how to relate it to speed, tension, normal force, and radius.','/science/physics/centripetal-force-and-gravitation/centripetal-forces/v/centripetal-force-problem-solving','2016-09-12 22:54:38','2016-09-12 22:54:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1643,18,147,'Newton\'s law of gravitation','gravity-newtonian',NULL,'Why are you sticking to your chair (ignoring the spilled glue)?  Why does the earth orbit the sun (or does it)? How high could I throw my dog on the moon?\n\nGravitation defines our everyday life and the structure of the universe.  This tutorial will introduce it to you in the Newtonian sense.','/science/physics/centripetal-force-and-gravitation/gravity-newtonian/v/introduction-to-gravity','2016-09-12 22:54:38','2016-09-12 22:54:38',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1644,18,148,'Work and energy','work-and-energy-tutorial',NULL,'You\'re doing a lot more work than you realize (most of which goes unpaid).  This tutorial will have you seeing the world in terms of potentials and energy and work (which is more fun than you can possibly imagine).','/science/physics/work-and-energy/work-and-energy-tutorial/v/introduction-to-work-and-energy','2016-09-12 22:54:44','2016-09-12 22:54:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1645,18,148,'Springs and Hooke\'s law','hookes-law',NULL,'Weighing machines of all sorts employ springs that take a certain amount of force to keep compressed or stretched to a certain point.  Hooke\'s law will give us all the tools to weigh in on the subject ourselves and spring into action (yes, the puns are annoying us too)!','/science/physics/work-and-energy/hookes-law/v/intro-to-springs-and-hooke-s-law','2016-09-12 22:54:44','2016-09-12 22:54:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1646,18,148,'Mechanical advantage','mechanical-advantage',NULL,'If you have ever used a tool of any kind (including the bones in your body), you have employed mechanical advantage.  Whether you used an incline plane to drag something off of a pick-up truck, or the back of a hammer to remove a nail, the world of mechanical advantage surrounds us.','/science/physics/work-and-energy/mechanical-advantage/v/introduction-to-mechanical-advantage','2016-09-12 22:54:44','2016-09-12 22:54:44',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1647,18,149,'Momentum and Impulse','momentum-tutorial',NULL,'Depending on your view of things, this may be the most violent of our tutorials.  Things will crash and collide.  We\'ll learn about momentum and how it is transferred.  Whether you\'re playing pool (or \"billiards\") or deciding whether you want to get tackled by the 300lb. guy, this tutorial is of key importance.','/science/physics/linear-momentum/momentum-tutorial/v/introduction-to-momentum','2016-09-12 22:54:50','2016-09-12 22:54:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1648,18,149,'Elastic and inelastic collisions','elastic-and-inelastic-collisions',NULL,'Categorizing the different types of collisions can be useful. Here, you\'ll learn what it means for a collision to be elastic or inelastic and what that implies mathematically.','/science/physics/linear-momentum/elastic-and-inelastic-collisions/v/elastic-and-inelastic-collisions','2016-09-12 22:54:50','2016-09-12 22:54:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1649,18,149,'Center of mass','center-of-mass',NULL,'Learn what center of mass means, why it\'s useful, and how to calculate it.','/science/physics/linear-momentum/center-of-mass/v/center-of-mass','2016-09-12 22:54:50','2016-09-12 22:54:50',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1650,18,150,'Rotational kinematics','rotational-kinematics',NULL,'Learn what angular displacement, angular velocity, and angular acceleration mean, as well as the relationship between them.','/science/physics/torque-angular-momentum/rotational-kinematics/v/angular-motion-variables','2016-09-12 22:54:55','2016-09-12 22:54:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1651,18,150,'Torque, moments, and angular momentum','torque-tutorial',NULL,'Until this tutorial, we have been completely ignoring that things rotate.  In this tutorial, we\'ll explore why they rotate and how they do it.  It will leave your head spinning (no, it won\'t, but seemed like a fun thing to say given the subject matter).','/science/physics/torque-angular-momentum/torque-tutorial/v/introduction-to-torque','2016-09-12 22:54:55','2016-09-12 22:54:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1652,18,151,'Simple harmonic motion','harmonic-motion',NULL,'Ever watch a slinky gyrate back and forth.  This is harmonic motion (a special class of oscillatory motion).  In this tutorial we\'ll see how we can model and deal with this type of phenomena.','/science/physics/mechanical-waves-and-sound/harmonic-motion/v/intuition-about-simple-harmonic-oscillators','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1653,18,151,'Simple harmonic motion (with calculus)','simple-harmonic-motion-with-calculus',NULL,'In these videos Sal explores how to deal with simple harmonic motion when using calculus.','/science/physics/mechanical-waves-and-sound/simple-harmonic-motion-with-calculus/v/introduction-to-harmonic-motion','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1654,18,151,'Introduction to mechanical waves','mechanical-waves',NULL,'In these videos and articles you\'ll learn the definition of a wave and the difference between transverse and longitudinal waves. You\'ll also learn the definition of the basic terms used to characterize waves; amplitude, period, frequency, and wavelength. ','/science/physics/mechanical-waves-and-sound/mechanical-waves/v/introduction-to-waves','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1655,18,151,'Sound','sound-topic',NULL,'In these videos and articles you\'ll see visualizations of the propagation of sound waves, along with a visual definition of a sound wave\'s amplitude, period, frequency, and wavelength. You\'ll also learn how the speed of sound depends on the different characteristics of the medium. It will be shown how Mach numbers characterize faster than sound travel and their relationship to sonic booms. Also, you\'ll learn about the decibel scale, why sounds get softer, and how ultrasound medical imaging works.','/science/physics/mechanical-waves-and-sound/sound-topic/v/production-of-sound','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1656,18,151,'The Doppler effect','doppler-effect',NULL,'In these videos and articles you\'ll learn how the Doppler effect causes a change in the measured frequency of a moving source of sound or moving sound detector. You\'ll also learn how to calculate the observed frequency using the formula for the Doppler effect when dealing with either a moving source of sound or a moving sound detector. ','/science/physics/mechanical-waves-and-sound/doppler-effect/v/introduction-to-the-doppler-effect','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1657,18,151,'Wave interference','standing-waves',NULL,'In these videos and articles you\'ll learn how standing waves are produced and how to determine the wavelength and frequency of the different harmonics of standing waves produced in an open open tube or an open closed tube.','/science/physics/mechanical-waves-and-sound/standing-waves/v/wave-interference-pulses','2016-09-12 22:55:02','2016-09-12 22:55:02',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1658,18,152,'Density and Pressure','density-and-pressure',NULL,'In these videos and articles you\'ll learn the definitions of density and specific gravity. You\'ll also learn the definition of pressure, how it relates to force and area, and how to determine the pressure within a fluid. The difference between absolute and gauge pressure will also be discussed.','/science/physics/fluids/density-and-pressure/v/specific-gravity','2016-09-12 22:55:08','2016-09-12 22:55:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1659,18,152,'Buoyant Force and Archimedes\' Principle','buoyant-force-and-archimedes-principle',NULL,'In these videos and articles you\'ll learn how pressure variations cause a buoyant force on a submerged object and how to calculate the value of the buoyant force. You\'ll also learn the meaning of Archimedes\' principle.','/science/physics/fluids/buoyant-force-and-archimedes-principle/v/fluids-part-5','2016-09-12 22:55:08','2016-09-12 22:55:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1660,18,152,'Fluid Dynamics','fluid-dynamics',NULL,'Flowing fluids behave different from static fluids. In these videos and articles you\'ll learn about volume flow rate and the equation of continuity. You\'ll also learn how Bernoulli\'s principle and Bernoulli\'s equation relate the pressure and speed of a flowing fluid. The effects of turbulence and viscosity will also be discussed.','/science/physics/fluids/fluid-dynamics/v/fluids-part-7','2016-09-12 22:55:08','2016-09-12 22:55:08',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1661,18,153,'Temperature, kinetic theory, and the ideal gas law','temp-kinetic-theory-ideal-gas-law',NULL,'In these videos and articles you\'ll learn about the Celsius and Kelvin temperature scales. The definition of a mole of a substance will be given. You\'ll also learn how the ideal gas law relates the pressure, volume, and temperature of a gas. Lastly, you\'ll learn how the Maxwell-Boltzmann distribution gives the probability of finding a gas molecule moving at a specific speed.','/science/physics/thermodynamics/temp-kinetic-theory-ideal-gas-law/v/thermodynamics-part-1','2016-09-12 22:55:14','2016-09-12 22:55:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1662,18,153,'Specific heat and heat transfer','specific-heat-and-heat-transfer',NULL,'In these videos and articles you\'ll learn the definition of specific heat and latent heat and how to use them in problem solving. You\'ll also learn the 3 modes of heat transfer; conduction, convection, and radiation. Lastly, we will look in more depth at how the rate of thermal conduction through a material depends on the material\'s thickness, conductivity constant, area, and difference in temperature.','/science/physics/thermodynamics/specific-heat-and-heat-transfer/v/specific-heat-and-latent-leat-of-fusion-and-vaporization-2','2016-09-12 22:55:14','2016-09-12 22:55:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1663,18,153,'Laws of thermodynamics','laws-of-thermodynamics',NULL,'In these videos and articles you\'ll learn how the first law of thermodynamics relates the change in internal energy of a gas, heat that enters the gas, and work done on the gas. PV diagrams will be discussed, as well as the four common thermal process; isobaric, isochoric/isovolumetric, isothermal, and isovolumetric processes.\n\nYou\'ll also learn about how the second law of thermodynamics relates the entropy change to the multiplicity of microstates and the heat that enters a macroscopic system. The efficiency of a heat engine will also be explained.','/science/physics/thermodynamics/laws-of-thermodynamics/v/macrostates-and-microstates','2016-09-12 22:55:14','2016-09-12 22:55:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1664,18,154,'Charge and electric force (Coulomb\'s law)','charge-electric-force',NULL,'In these videos and articles the definition of electric charge will be given as well as a discussion of the law of conservation of charge. You\'ll learn how Coulomb\'s law relates the electric force to charge and separation distance. The difference between an electrically conducting vs. electrically insulating material will also be discussed. ','/science/physics/electric-charge-electric-force-and-voltage/charge-electric-force/v/triboelectric-effect-and-charge','2016-09-12 22:55:20','2016-09-12 22:55:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1665,18,154,'Electric field','electric-field',NULL,'In these videos and articles you\'ll learn what an electric field is and how to calculate the value of the electric field for a point charge and an infinite sheet.','/science/physics/electric-charge-electric-force-and-voltage/electric-field/v/electric-field-definition','2016-09-12 22:55:20','2016-09-12 22:55:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1666,18,154,'Electric potential energy, electric potential, and voltage','electric-potential-voltage',NULL,'In these videos and articles you\'ll learn the difference between electric potential, electric potential energy, and voltage. You\'ll also learn how to calculate the electric potential at a point in space, electric potential energy between two charges, and voltage between two points in space.','/science/physics/electric-charge-electric-force-and-voltage/electric-potential-voltage/v/electric-potential-energy','2016-09-12 22:55:20','2016-09-12 22:55:20',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1667,18,155,'Ohm\'s law and circuits with resistors','circuits-resistance',NULL,'In these videos and articles you\'ll learn how Ohm\'s law relates the voltage across a resistor, the current through a resistor, and the resistance of the resistor. You\'ll also learn how resistors in series and parallel combine to form an equivalent resistance. The resistivity of materials, power used by a resistor, and how to use voltmeters and ammeters will also be discussed.','/science/physics/circuits-topic/circuits-resistance/v/circuits-part-1','2016-09-12 22:55:26','2016-09-12 22:55:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1668,18,155,'Circuits with capacitors','circuits-with-capacitors',NULL,'In these videos and articles you\'ll learn how capacitors store charge and energy. You\'ll also learn the relationships between capacitance, charge stored, energy stored, and voltage across a capacitor. Techniques for finding the equivalent capacitance for capacitors in series and parallel will be discussed. Lastly, you\'ll learn how a dielectric can change the amount of charge, voltage, and capacitance of a capacitor.','/science/physics/circuits-topic/circuits-with-capacitors/v/capacitors-and-capacitance','2016-09-12 22:55:26','2016-09-12 22:55:26',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1669,18,156,'Magnets and Magnetic Force','magnets-magnetic',NULL,'In these videos and articles you\'ll learn how the magnitude of the magnetic force depends on the charge, velocity, and magnetic field. You\'ll also learn how to use the right hand rule to determine the direction of the magnetic force. Lastly, it will be explained how to determine the magnetic force on a current carrying wire.','/science/physics/magnetic-forces-and-magnetic-fields/magnets-magnetic/v/introduction-to-magnetism','2016-09-12 22:55:33','2016-09-12 22:55:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1670,18,156,'Magnetic field created by a current','magnetic-field-current-carrying-wire',NULL,'In these videos and articles you\'ll learn how the magnetic field created by a long straight wire depends on the current and distance from the wire. You\'ll also learn how to use the right hand rule to determine the direction of the magnetic field. The magnetic force between two current carrying wires will be discussed as well as the induced current in a wire moving through a magnetic field.','/science/physics/magnetic-forces-and-magnetic-fields/magnetic-field-current-carrying-wire/v/magnetism-6-magnetic-field-due-to-current','2016-09-12 22:55:33','2016-09-12 22:55:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1671,18,156,'Electric motors','electric-motors',NULL,'In these videos and articles you\'ll learn how electric motors rely on a magnetic force that can cause a torque which rotates a loop of wire. You\'ll also learn how commutators allow the direction the the current to switch, which causes the motor to continue turning. ','/science/physics/magnetic-forces-and-magnetic-fields/electric-motors/v/magnetism-9-electric-motors','2016-09-12 22:55:33','2016-09-12 22:55:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1672,18,156,'Magnetic flux and Faraday\'s law','magnetic-flux-faradays-law',NULL,'In these videos and articles you\'ll learn how magnetic flux depends on magnetic field strength, area, and angle. You\'ll also learn how Faraday\'s law states that a changing magnetic flux can cause an induced current. It will be shown how Lenz\'s law can allow you to find the direction of the induced current. Example problems will also be solved to show how to apply Faraday\'s law and Lenz\'s law.','/science/physics/magnetic-forces-and-magnetic-fields/magnetic-flux-faradays-law/v/flux-and-magnetic-flux','2016-09-12 22:55:33','2016-09-12 22:55:33',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1673,18,157,'Introduction to electromagnetic waves','introduction-to-light-waves',NULL,'In these videos and articles you\'ll learn about the different categories of the electromagnetic spectrum; radio waves, microwaves, infrared, visible, ultraviolet, x-ray, and gamma ray. You\'ll also learn what it means for light to be polarized, and how polarized light can allow for the creation of 3D movies.','/science/physics/light-waves/introduction-to-light-waves/a/light-and-the-electromagnetic-spectrum','2016-09-12 22:55:39','2016-09-12 22:55:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1674,18,157,'Interference of electromagnetic waves','interference-of-light-waves',NULL,'In these videos and articles you\'ll learn how overlapping light waves can cause constructive and destructive interference. Some examples of light interference that will be discussed include Young\'s double slit interference, single slit interference, and thin film interference. ','/science/physics/light-waves/interference-of-light-waves/v/constructive-and-destructive-interference','2016-09-12 22:55:39','2016-09-12 22:55:39',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1675,18,158,'Reflection and refraction','reflection-refraction',NULL,'In these videos and articles you\'ll learn how to determine the angle of reflected light and the definition of specular and diffuse reflection. You\'ll also learn how to use Snell\'s law to determine the angle of a refracted light ray. Example problems dealing with the refraction of light rays will be solved. The topics of total internal reflection, critical angle, and dispersion will also be discussed.','/science/physics/geometric-optics/reflection-refraction/v/specular-and-diffuse-reflection','2016-09-12 22:55:48','2016-09-12 22:55:48',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1676,18,158,'Mirrors','mirrors',NULL,'In these videos and articles you\'ll learn how flat and curved/parabolic mirrors create images. The definitions of real and virtual images will be discussed. You\'ll also learn how to use the mirror equation to determine the image distance, magnification, image height, and object height when dealing with curved mirrors. The sign convention for positive and negative image distance, object distance, and focal length will be discussed. ','/science/physics/geometric-optics/mirrors/v/virtual-image','2016-09-12 22:55:48','2016-09-12 22:55:48',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1677,18,158,'Lenses','lenses',NULL,'Curves lenses help people see better. In this section you\'ll learn how to use ray tracings and the thin lens equation to find the location and size of images created by a curved lenses.','/science/physics/geometric-optics/lenses/v/convex-lenses','2016-09-12 22:55:48','2016-09-12 22:55:48',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1678,18,159,'Michelson and Morley\'s luminiferous ether experiment','michelson-and-morleys-luminiferous-ether-experiment',NULL,'The Michelson and Morley experiment was a momentous event in the lead up to special relativity. In this section, we\'ll explain Michelson and Morley\'s search for experimental evidence of a luminiferous ether. (Spoiler...they didn\'t find it. And that blew people\'s minds.)','/science/physics/special-relativity/michelson-and-morleys-luminiferous-ether-experiment/v/light-and-the-luminiferous-ether','2016-09-12 22:55:55','2016-09-12 22:55:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1679,18,159,'Minkowski spacetime','minkowski-spacetime-2016-01-18T22:56:14.718Z',NULL,'In a Newtonian world we assume that the passage of time or lengths in space are absolute--that regardless of one\'s inertial frame of reference, everyone would agree that the same time passed in every frame of reference and that something of a certain length in one frame would be the same length in other frames.  This leads to the speed of light needing to be relative depending on the frame of reference which contradicts observation.  We reconcile observations by loosening our assumptions about absolute space and time.  We explore this through Minkowski spacetime diagrams.','/science/physics/special-relativity/minkowski-spacetime-2016-01-18T22:56:14.718Z/v/starting-to-set-up-a-newtonian-space-time-diagram','2016-09-12 22:55:55','2016-09-12 22:55:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1680,18,159,'Lorentz transformation','lorentz-transformation',NULL,'We\'ll now put some algebraic meat on our special relativity bone by introducing ourselves to the Lorentz transformation.  Interestingly, Lorentz came up with them before Einstein introduced Special Relativity as a nice way to reconcile some observations, but special relativity game them real conceptual meat.  The Lorentz transformation is going to allow us to calculate the spacetime coordinates in different frames of reference!','/science/physics/special-relativity/lorentz-transformation/v/introduction-to-the-lorentz-transformation','2016-09-12 22:55:55','2016-09-12 22:55:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1681,18,159,'Einstein velocity addition','einstein-velocity-addition',NULL,'You\'re traveling toward me at half the speed of light.  My friend is traveling away from me at half the speed of light.  From either of your points-of-view, how quickly are you approaching each other?  Things are going to get even more fun now.  We\'re going to think about relative velocities in different frames of reference.','/science/physics/special-relativity/einstein-velocity-addition/v/lorentz-transformation-for-change-in-coordinates','2016-09-12 22:55:55','2016-09-12 22:55:55',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1682,18,160,'Photons','photons',NULL,'Light can have wave-like or particle-like properties depending on the experiment being done. These \"particles\" of light are called photons. In this tutorial you\'ll learn about photon energy and momentum.','/science/physics/quantum-physics/photons/v/photon-energy','2016-09-12 22:56:03','2016-09-12 22:56:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1683,18,160,'Atoms and electrons','atoms-and-electrons',NULL,'Sometimes people draw atoms like little solar systems, but they\'re way stranger than that. In this section you\'ll learn how electrons can have wavelengths, jump orbitals, and emit photons while in an atom. ','/science/physics/quantum-physics/atoms-and-electrons/v/de-broglie-wavelength','2016-09-12 22:56:03','2016-09-12 22:56:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1684,18,160,'Quantum numbers and orbitals','quantum-numbers-and-orbitals',NULL,'In this tutorial, we will see how the quantum numbers predict the orbitals available in different energy levels.  ','/science/physics/quantum-physics/quantum-numbers-and-orbitals/a/the-quantum-mechanical-model-of-the-atom','2016-09-12 22:56:03','2016-09-12 22:56:03',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1685,18,160,'Nuclear physics','nucleus-physics',NULL,'In this tutorial, we will learn about radioactive decay, nuclear stability, and what is happening on a subatomic level during a nuclear reaction. ','/science/physics/quantum-physics/nucleus-physics/v/mass-defect-and-binding-energy','2016-09-12 22:56:03','2016-09-12 22:56:03',1);
