insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1687,18,161,'Discovery of magnetic fields','magnetic-fields',NULL,'Let\'s find out more about this invisible force which guides the compass. How strong is it? What shape is it?','/science/physics/discoveries/magnetic-fields/v/magnet-near-compass','2016-09-12 22:56:10','2016-09-12 22:56:10',1);
