insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1690,18,161,'Discovery of electromagnetism','electromagnet',NULL,'Discoveries leading to the Right Hand rule','/science/physics/discoveries/electromagnet/v/discovery-of-electromagnetism','2016-09-12 22:56:10','2016-09-12 22:56:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1691,18,161,'Discovery of resistors','resistance',NULL,'Exploring materials which cause a decrease in deflection when added in series with our meter.','/science/physics/discoveries/resistance/v/current-change-due-to-electromagnet-load','2016-09-12 22:56:10','2016-09-12 22:56:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1692,18,161,'Electric motor','electric-motor',NULL,'How can we turn electric current into rotary motion?','/science/physics/discoveries/electric-motor/v/motor-hd-youtube','2016-09-12 22:56:10','2016-09-12 22:56:10',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1693,18,161,'Electrostatics','electrostatics',NULL,'Discovering static electricity & electrostatic force. What is it? How can it be created, detected, and measured?','/science/physics/discoveries/electrostatics/v/discovery-of-electrostatics','2016-09-12 22:56:10','2016-09-12 22:56:10',1);
