insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1727,19,174,'Enthalpy','enthalpy-chemistry-sal',NULL,'An introduction to enthalpy and Hess\'s law','/science/chemistry/thermodynamics-chemistry/enthalpy-chemistry-sal/v/chem19-calorimetry','2016-09-12 22:57:42','2016-09-12 22:57:42',1);
