insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1730,19,175,'Oxidation-reduction reactions','redox-oxidation-reduction',NULL,'Oxidation and reduction are powerful ideas for thinking about how charge is transferred within a reaction.  As we\'ll see, it is something of a hypothetical, but it is, nonetheless, very useful.','/science/chemistry/oxidation-reduction/redox-oxidation-reduction/v/introduction-to-oxidation-and-reduction','2016-09-12 22:57:49','2016-09-12 22:57:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1731,19,175,'Galvanic cells','batter-galvanic-voltaic-cell',NULL,'Batteries power much of our lives (literally).  In this tutorial, we\'ll use our knowledge of oxidation and reduction to understand how Galvanic/Voltaic cells actually work.','/science/chemistry/oxidation-reduction/batter-galvanic-voltaic-cell/v/redox-reaction-from-dissolving-zinc-in-copper-sulfate','2016-09-12 22:57:49','2016-09-12 22:57:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1732,19,175,'Standard cell potentials','cell-potential',NULL,'An introduction to calculating cell potentials at standard state. ','/science/chemistry/oxidation-reduction/cell-potential/v/standard-reduction-potentials','2016-09-12 22:57:49','2016-09-12 22:57:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1733,19,175,'Electrochemistry, thermodynamics, and equilibrium','electrochemistry-thermodynamics-and-equilibrium',NULL,'Relating the cell potential to the equilibrium constant K and Gibbs free energy.','/science/chemistry/oxidation-reduction/electrochemistry-thermodynamics-and-equilibrium/v/free-energy-and-cell-potential','2016-09-12 22:57:49','2016-09-12 22:57:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1734,19,175,'Cell potentials under nonstandard conditions','cell-potentials-under-nonstandard-conditions',NULL,'Calculating the cell potential when operating at nonstandard conditions.','/science/chemistry/oxidation-reduction/cell-potentials-under-nonstandard-conditions/v/galvanic-cells-and-changes-in-free-energy','2016-09-12 22:57:49','2016-09-12 22:57:49',1);
