insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1918,21,220,'Endocrine system introduction ','endocrine-system-introduction',NULL,'Glands are special organs that secrete chemical messages called hormones, which seep into the blood - it’s like putting a tea bag in hot water. As the heart pumps, this blood carries these chemical messages throughout the body, allowing the hormones to interact with specific target cells and organs. Endocrine glands help us to maintain our appetites, grow up, metabolize molecules, concentrate urine,- and oh, so much more! We will examine how these variegated hormones play a role in homeostasis as the body responds to a changing environment.','/science/health-and-medicine/advanced-endocrine-system/endocrine-system-introduction/v/endocrine-gland-hormone-review','2016-09-12 23:03:14','2016-09-12 23:03:14',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1919,21,220,'Biological basis of behavior: Endocrine system','biological-basis-of-behavior-endocrine-system-2014-03-27T18:06:55.890Z',NULL,'Consider any behavior, sleeping for instance, and think about all of the organs that have to work together to have it go smoothly.  The heart and lungs need to slow down, the brain needs to stop taking in the cues from the environment, and the bladder needs to wait until morning to empty.  This coordinated effort is achieved by a number of unique hormones acting on different tissues.  Learn more about how this process works and why it’s so critical to our everyday lives!','/science/health-and-medicine/advanced-endocrine-system/biological-basis-of-behavior-endocrine-system-2014-03-27T18:06:55.890Z/v/terpenes-to-steroids1','2016-09-12 23:03:14','2016-09-12 23:03:14',1);
