insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1922,21,222,'Neural cells and neurotransmitters','neural-cells-and-neurotransmitters',NULL,'Get an overview of the structure and function of neurons, and learn about the many other important cells needed to help our nervous system function optimally!  By Matt Jensen.','/science/health-and-medicine/nervous-system-and-sensory-infor/neural-cells-and-neurotransmitters/v/neurotransmitter-release','2016-09-12 23:03:27','2016-09-12 23:03:27',1);
