insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1924,21,222,'Sensory perception','sensory-perception-2014-03-27T18:45:20.451Z',NULL,'Learn about how we perceive our various senses, including the theories, laws, and organizational principles that underly our ability to make sense of the world around us.','/science/health-and-medicine/nervous-system-and-sensory-infor/sensory-perception-2014-03-27T18:45:20.451Z/v/perceptual-organization','2016-09-12 23:03:27','2016-09-12 23:03:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1925,21,222,'Sight (vision)','sight-2014-03-27T18:45:34.237Z',NULL,'Photons are hitting your eye as you read this!  Learn about our sense of sight, including the cells responsible for converting light into a neural impulse, the structure of the eye, and how we break down images to make sense of them.','/science/health-and-medicine/nervous-system-and-sensory-infor/sight-2014-03-27T18:45:34.237Z/v/vision-structure-of-the-eye','2016-09-12 23:03:27','2016-09-12 23:03:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1926,21,222,'Sound (Audition)','sound-audition-2014-03-27T18:45:45.806Z',NULL,'Learn about how we hear, including the structure of the outer, middle, and inner ear, as well as the basics of auditory processing & cochlear implants.','/science/health-and-medicine/nervous-system-and-sensory-infor/sound-audition-2014-03-27T18:45:45.806Z/v/auditory-structure-part-1','2016-09-12 23:03:27','2016-09-12 23:03:27',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1927,21,222,'Somatosensation','somatosensation-2014-03-27T18:45:57.777Z',NULL,'We perceive the environment through our bodily senses, including our sensation of pain, temperature, pressure, balance, and movement.  Discover how our body gathers this information and processes it so that we can make sense of the world.','/science/health-and-medicine/nervous-system-and-sensory-infor/somatosensation-2014-03-27T18:45:57.777Z/v/somatosensation-1','2016-09-12 23:03:27','2016-09-12 23:03:27',1);
