insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1961,22,235,'Getting started','intro-to-ee',NULL,'Read a summary of the math and science preparation that will help you have the best experience studying electrical engineering on Khan Academy. Gain an intuitive understanding of the two most important electrical quantities: current and voltage. ','/science/electrical-engineering/introduction-to-ee/intro-to-ee/v/ee-current','2016-09-12 23:06:36','2016-09-12 23:06:36',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1962,22,236,'Circuit elements','circuit-elements',NULL,'The basic elements of circuit design are components (resistors, capacitors, and inductors), and sources (voltage and current). We describe these elements as both ideal elements and real-world objects. We also develop precise terminology for how to talk about circuits and label voltages and currents.','/science/electrical-engineering/ee-circuit-analysis-topic/circuit-elements/v/ideal-circuit-elements','2016-09-12 23:06:43','2016-09-12 23:06:43',1);
