insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (1966,22,236,'AC circuit analysis','ee-ac-analysis',NULL,'If we limit ourselves to input signals shaped like sines or cosines, we can develop a whole AC analysis method to figure out what happens in circuits with changing signals. The methods are far simpler than solving a differential equation.','/science/electrical-engineering/ee-circuit-analysis-topic/ee-ac-analysis/v/ee-ac-analysis-intro1','2016-09-12 23:06:43','2016-09-12 23:06:43',1);
