insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2099,30,284,'The Comma','the-comma',NULL,'Commas are the superheroes of punctuation! They can perform many different functions. Learn about how to use them here!','/humanities/grammar/punctuation/the-comma/v/three-ways-to-end-a-sentence-punctuation-khan-academy','2016-09-12 23:12:28','2016-09-12 23:12:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2100,30,284,'The Apostrophe','the-apostrophe',NULL,'Apostrophes are most often used to stand in for missing letters or to show possession. In the rarest of cases, they can be used to form plurals. Let\'s learn more!','/humanities/grammar/punctuation/the-apostrophe/v/introduction-to-the-apostrophe-the-apostrophe-punctuation-khan-academy','2016-09-12 23:12:28','2016-09-12 23:12:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2101,30,284,'The Colon and Semicolon ','the-colon-and-semicolon',NULL,'Colons and semicolons are two valuable pieces of punctuation that separate sentence elements from each other. Learn more about how to use them here!','/humanities/grammar/punctuation/the-colon-and-semicolon/v/linking-function-of-the-colon-the-colon-and-semicolon-punctuation-khan-academy','2016-09-12 23:12:28','2016-09-12 23:12:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2102,30,284,'Miscellaneous punctuation','miscellaneous-punctuation',NULL,'Parentheses, dashes, hyphens, ellipses . . . here\'s where all the other punctuation lives!','/humanities/grammar/punctuation/miscellaneous-punctuation/v/italics-and-underlining','2016-09-12 23:12:28','2016-09-12 23:12:28',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2103,30,285,'Syntax','syntax',NULL,'Syntax involves arranging words to create logical phrases, clauses, and sentences. This is a big topic, so we\'ll be covering a lot, including: dependent and independent clauses; simple, complex, compound, and compound-complex sentences; subject-verb agreement; dangling modifiers; and much more!','/humanities/grammar/syntax/syntax/v/what-is-a-sentence-syntax-khan-academy','2016-09-12 23:12:35','2016-09-12 23:12:35',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2104,31,286,'Notes and rhythm','notes-rhythm',NULL,'How are time, duration and rhythm expressed in musical notation? Find out here with easy-to-follow lessons presented by music director Gerard Schwarz, with video examples provided by the All-Star Orchestra','/humanities/music/music-basics2/notes-rhythm/v/lesson-1-note-values-duration-and-time-signatures','2016-09-12 23:12:42','2016-09-12 23:12:42',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2105,31,286,'Reading music','reading-music2',NULL,'Learn how to read music as music director Gerard Schwarz explains how notes, clefs, scales, flats and sharps work together in the context of musical notation, with performance examples provided by the All-Star Orchestra.','/humanities/music/music-basics2/reading-music2/v/lesson-1-staff-names-of-notes-treble-clef','2016-09-12 23:12:42','2016-09-12 23:12:42',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2106,31,287,'Ludwig van Beethoven: Symphony No. 5','ludwig-van-beethoven-music',NULL,'Possibly the most iconic of all symphonies, \"Beethoven\'s 5th\" is instantly recognizable by its dramatic opening motive. Learn how this rhythmic and melodic idea permeates the entire work, holding the listener in thrall through all four movements to the blazing finale. Music Director Gerard Schwarz and noted expert Leon Botstein explore the many facets of Beethoven\'s masterpiece, including a demonstration by Maestro Schwarz of how to conduct the first movement.','/humanities/music/music-masterpieces-old-new/ludwig-van-beethoven-music/v/ludwig-van-beethoven-part-1','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2107,31,287,'Pyotr Ilyich Tchaikovsky:  Symphony No.4','tchaikovsky-4th',NULL,'The 4th Symphony of Piotr Il’yich Tchaikovsky, composed in 1877-78 when he was 37 years old, is one of the most dramatic works for orchestra. The music reflects the powerful emotions that Tchaikovsky experienced during this most difficult period of his life: forbidden love, a failed marriage, a mysterious patron whom he was never to meet, and the struggle to understand and accept the inevitability of Fate.','/humanities/music/music-masterpieces-old-new/tchaikovsky-4th/v/pyotr-ilyich-tchaikovsky-symphony-no4-analysis-by-gerard-schwarz-part-1','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2108,31,287,'Robert Schumann: Symphony No. 3 \"Rhenish\"','schumann-thirdsymph-analysis',NULL,'The “ Rhenish\" Symphony was inspired by a boat trip that Robert and Clara Schumann made in 1850 on the Rhein River, as well as their visit to the great gothic cathedral of Cologne. It is a celebration of traditional life and folklore along the legendary river, and is filled with the spirit of German Romanticism. Music Director Gerard Schwarz offers a guide to this exciting musical journey.','/humanities/music/music-masterpieces-old-new/schumann-thirdsymph-analysis/v/robert-schumann-symphony-no-3-analysis-by-gerard-schwarz-part-1','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2109,31,287,'Johannes Brahms: Academic Festival Overture','brahms-overture',NULL,'Although Johannes Brahms never went to a university, he was offered a PhD from the University of Breslau, but he to receive this he had to compose a work for the school. This became the Academic Festival Overture, described by Brahms as a cheerful potpourri of student songs. The overture has the largest orchestration of any of Brahms’ works, and is among his most charming and optimistic pieces.','/humanities/music/music-masterpieces-old-new/brahms-overture/v/brahms-acad-fest-overture','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2110,31,287,'Antonín Dvo?ák: Symphony No. 9 \"From the New World\"','antonin-dvorak-music',NULL,'Inspired by American dreams and legends, Antonin Dvo?ák created some of his greatest works while living in the United States, above all the “New World” Symphony. Music Director Gerard Schwarz and Dvo?ák expert Joseph Horowitz delve into the music, and illuminate the multiple stories and influences – Native American, African-American and Czech –that Dvo?ák transformed in his most famous work.\n','/humanities/music/music-masterpieces-old-new/antonin-dvorak-music/v/dvorak-one','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2111,31,287,'Gustav Mahler: Symphony No. 2 – 1st Movement','mahler-sym-2',NULL,'Following the heroic birth of his first symphony, “The Titan,” in 1889, Gustav Mahler began his next symphony with the question “Is there life after death?” The opening movement of Symphony #2 represents mystical “funerary rites.” The music evokes awe of the unknown, and the hope of resurrection. Music Director Gerard Schwarz and Mahler expert Gilbert Kaplan offer two perspectives of this powerful work.','/humanities/music/music-masterpieces-old-new/mahler-sym-2/v/gustav-mahler-symphony-no-2-1st-movement-analysis-by-gerard-schwarz','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2112,31,287,'Igor Stravinsky: Suite from The Firebird','igor-stravinsky-music',NULL,'The magical world of Russian folklore comes to life as Music Director Gerard Schwarz tells the story of The Firebird ballet, and looks into the music of Igor Stravinsky\'s thrilling musical score that was commissioned by the legendary impresario Serge Diaghilev and his Ballets Russes.','/humanities/music/music-masterpieces-old-new/igor-stravinsky-music/v/discover-stravinsky-firebird','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2113,31,287,'Maurice Ravel: Suite No. 2 from Daphnis et Chloe','maurice-ravel-music',NULL,'Analysis by Gerard Schwarz. A love story from ancient Greek mythology inspired the creation of an epic ballet in 1912. French composer Maurice Ravel expanded the possibilities of orchestral color and texture in his remarkable score. Music Director Gerard Schwarz explores the history and musical structure of this landmark work of musical Impressionism.','/humanities/music/music-masterpieces-old-new/maurice-ravel-music/v/ravel-suite2','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2114,31,287,'Dmitri Shostakovich: Symphony No. 5','shostakovich5thagain',NULL,'Music has sometimes reflected, and at other times challenged repressive ideologies.  Dmitri Shostakovich abandoned the premiere of his daring 4th symphony in 1935 for fear of reprisals from the Soviet government. Then he composed his triumphant 5th Symphony in 1937, and Stalin was pleased.  To this day the 5th is Shostakovich’s most popular symphony. What is its message?  What does “political music” mean today? ','/humanities/music/music-masterpieces-old-new/shostakovich5thagain/v/dmitri-shostakovich-symphony-no-5-analysis-by-gerard-schwarz-part-1','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2115,31,287,'Introduction to Philip Glass\' \"Harmonium Mountain\"','philip-glass-intro',NULL,'Philip Glass is often called the most famous living composer today. His short work, Harmonium Mountain, was conceived in partnership with the visual artist Clifford Ross as the soundtrack for an animated film based on ever-changing images of a mountain in Colorado. This analysis explores connections between the visual and musical arts.','/humanities/music/music-masterpieces-old-new/philip-glass-intro/v/introp-glass-harmonium-mountain','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2116,31,287,'Bernard Rands: Adieu','bernard-rands-music',NULL,'Pulitzer-prize-winning composer and Harvard Professor of Music Bernard Rands discusses the art of composition. He and Music Director Gerard Schwarz look into the score of Rands\' recent work \"Adieu\"  (\"Goodbye\") as recorded by the All-Star Orchestra.','/humanities/music/music-masterpieces-old-new/bernard-rands-music/v/adieu-composer-and-his-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2117,31,287,'Augusta Read Thomas: Of Paradise and Light','augusta-read-thomas-music',NULL,'A leading composer of her generation, Augusta Read Thomas\' works have been performed by major orchestras around the world. Here she discusses the compositional process that led to Of Paradise and Light. Music Director Gerard Schwarz joins in the discussion of this transcendent new work.','/humanities/music/music-masterpieces-old-new/augusta-read-thomas-music/v/of-paradise-and-light-composer-and-her-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2118,31,287,'Bright Sheng: \"Black Swan\", the composer and his work','sheng',NULL,'A lovely Intermezzo for piano by Johannes Brahms (1833-1897) inspired contemporary composer Bright Sheng to orchestrate this short work in his own style. Sheng, who served as composer in residence for the New York City Ballet, has titled this musical transformation “Black Swan.\"','/humanities/music/music-masterpieces-old-new/sheng/v/bright-sheng-black-swan','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2119,31,287,'Richard Danielpour: Piano Concerto No. 4, Movement III','danielpour-concerto-analysis-music',NULL,'“A Hero’s Journey” is the title given by  Richard Danielpour to this dynamic movement from his Fourth Piano Concerto. In this analysis the composer describes the inspiration for his music, with comments by the soloist  Xiayin Wang and Music Director Gerard Schwarz.','/humanities/music/music-masterpieces-old-new/danielpour-concerto-analysis-music/v/richard-danielpour-concerto-4-movement-iii-the-composer-and-his-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2120,31,287,'David Stock: Blast!','stockblastyetagain',NULL,'The composer says: \"I knew what kind of piece this needed to be, and I knew I wanted it to be loud, and I knew I wanted it to be fun, so that\'s how it came to be “Blast!\", and I had a blast writing it!”','/humanities/music/music-masterpieces-old-new/stockblastyetagain/v/david-stock-blast-composer-and-his-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2121,31,287,'Ellen Taaffe Zwilich: Avanti!','avantionmusic',NULL,'The composer describes this work as an orchestral celebration: \"I think of the orchestra is like a giant chamber ensemble and I like the feeling that everybody on that stage down to the last chair player is an artist.\"','/humanities/music/music-masterpieces-old-new/avantionmusic/v/ellen-taaffe-zwilch-avanti-composer-and-her-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2122,31,287,'Joseph Schwantner: The Poet\'s Hour','schwantenerthepoetshouragain',NULL,'This piece was inspired by the transcendental writings of Thoreau, and the New England landscape. The composer says: \"There are mornings when the world seems to begin, it is a \'new beyond\' where memory need not go, it’s the poet’s hour. You get up early in the morning, and it becomes an extraordinary invigorating environment in which to lead an artist\'s life.”','/humanities/music/music-masterpieces-old-new/schwantenerthepoetshouragain/v/joseph-schwantner-the-poets-hour-composer-and-his-work','2016-09-12 23:12:49','2016-09-12 23:12:49',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2123,31,288,'Strings','strings-music',NULL,'The largest group within the orchestra is comprised of instruments that produce sound from vibrating strings. The vibrations – resulting from the strings being played by a bow, or being plucked with the fingers – resonate within the body of each instrument. The violin, viola, cello and contrabass are all primary played with a bow. The harp is  plucked and stroked with the hands. Principals from the All-Star Orchestra introduce the special qualities and histories of each instrument.','/humanities/music/music-instruments-orchestra/strings-music/v/harp-interview-demo','2016-09-12 23:13:15','2016-09-12 23:13:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2124,31,288,'Woodwinds','woodwinds-music',NULL,'This family of instruments is situated in the middle of the orchestra, and is comprised of three groups: the flutes (flutes, piccolo), single reeds (clarinets, bass-clarinet), and double reeds (oboe, English horn, bassoons, contra-bassoon) Watch and listen as principal players of the All-Star Orchestra demonstrate their instruments and tell about their musical lives. ','/humanities/music/music-instruments-orchestra/woodwinds-music/v/piccolo-interview-and-demonstration-with-nadine-asin','2016-09-12 23:13:15','2016-09-12 23:13:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2125,31,288,'Brass','brass-music',NULL,'Each of the instruments in this family are made to sound by the vibrations of the player\'s lips combined with a steady stream of breath. Learn from the All-Star Orchestra players themselves about the special attributes of the trumpet, French horn, trombone, bass trombone and tuba.','/humanities/music/music-instruments-orchestra/brass-music/v/french-horn-interview-demo','2016-09-12 23:13:15','2016-09-12 23:13:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2126,31,288,'Percussion','percussion-music',NULL,'This large family, including timpani, drums, cymbals, xylophones, gongs, bells, and rattles, is comprised of  musical instruments played by striking with the hand or with a stick or beater, or by shaking or rubbing.  The All-Star Orchestra percussion section demonstrates the remarkable variety of sounds that can be produced. Also included in this group are orchestral keyboard instruments like the piano and celesta.','/humanities/music/music-instruments-orchestra/percussion-music/v/timpani-interview-and-demonstration-with-principal-jauvon-gilliam','2016-09-12 23:13:15','2016-09-12 23:13:15',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2127,32,289,'How to think like a historian','how-to-think-like-a-historian',NULL,'Historians approach the past with a mindset of hypothesis and discovery. Learn about reading primary documents and interpreting sources. ','/humanities/us-history/historians-toolkit/how-to-think-like-a-historian/v/thinking-like-a-historian','2016-09-12 23:13:21','2016-09-12 23:13:21',1);
insert  into `topics`(`id`,`subject_id`,`parent_id`,`title`,`slug`,`icon`,`description`,`ka_url`,`created_at`,`updated_at`,`is_active`) values (2128,32,289,'AP US history exam practice','apush-practice',NULL,'Learn how to tackle the AP US history exam, including examples of the multiple choice questions, short answer questions, document-based questions (DBQ), and free-response questions (FRQ). ','/humanities/us-history/historians-toolkit/apush-practice/a/ap-us-history-study-sheet-1','2016-09-12 23:13:21','2016-09-12 23:13:21',1);
